  1016→function addJobToList(jobId, options, isSceneAware = false) {
  1017→  const job = {
  1018→    jobId,
  1019→    status: 'pending',
  1020→    progress: 0,
  1021→    mood: options.mood,
  1022→    duration: options.duration,
  1023→    isSceneAware,
  1024→    createdAt: new Date().toISOString()
  1025→  };
  1026→  musicState.jobs.unshift(job);
  1027→  renderJobsList();
  1028→}
