  1030→function startPollingJob(jobId) {
  1031→  if (musicState.pollInterval) clearInterval(musicState.pollInterval);
  1032→
  1033→  musicState.pollInterval = setInterval(async () => {
  1034→    try {
  1035→      const status = await getJobStatus(jobId);
  1036→      const jobIndex = musicState.jobs.findIndex(j => j.jobId === jobId);
  1037→      if (jobIndex !== -1) {
  1038→        musicState.jobs[jobIndex] = { ...musicState.jobs[jobIndex], ...status };
  1039→        renderJobsList();
  1040→      }
  1041→
  1042→      if (status.status === 'completed' || status.status === 'failed') {
  1043→        clearInterval(musicState.pollInterval);
  1044→        musicState.pollInterval = null;
  1045→        if (status.status === 'completed') {
  1046→          setMusicStatus('Music generation completed!', 'success');
  1047→          loadMusicLibrary();
  1048→        } else {
  1049→          setMusicStatus(`Generation failed: ${status.failedReason || 'Unknown error'}`, 'error');
  1050→        }
  1051→      }
  1052→    } catch (error) {
  1053→      console.error('Polling error:', error);
  1054→    }
  1055→  }, MUSIC_POLL_INTERVAL);
  1056→}
