  1069→      <div class="music-job-status ${job.status}"></div>
  1070→      <div class="music-job-info">
  1071→        <span class="music-job-mood">${job.mood || 'Music'}${job.isSceneAware ? ' Scene' : ''}</span>
  1072→        <span class="music-job-duration">${job.duration}s</span>
  1073→      </div>
  1074→      <div class="music-job-progress">
  1075→        ${job.status === 'completed' ? 'Done' : job.status === 'failed' ? 'Failed' : `${job.progress}%`}
