  1543→function formatDuration(seconds) {
  1544→  if (!seconds) return '0:00';
  1545→  const mins = Math.floor(seconds / 60);
  1546→  const secs = Math.round(seconds % 60);
  1547→  return `${mins}:${secs.toString().padStart(2, '0')}`;
  1548→}
