 * This file handles user interactions for the multitrack editing features,
 * including time navigation, manual overrides, scrolling, audio thresholds,
 * video track settings, and decision modifiers.
 */

/**
 * Sets listeners for jumping to specific decision points in the timeline.
 * Triggered when clicking on the time section of a multitrack video button.
 */
function set_multitrack_go_to_time_listeners() {
    $('.button-multitrack-video-time-section').on('click', function (event) {
        // Parse the decision number from the button's attribute
        let decisionNumber = parseInt($(this).parent().parent().attr('multitrack-video-decision-number'));

        // Jump to the specific decision in the timeline
        select_multitrack_decision_number(decisionNumber);

        // Remove focus from the button
        $(this).blur();

        // Track the event
        tracker.event('GO TO MULTITRACK DECISION: Decision number: ' + decisionNumber);
    });
}

/**
 * Sets listeners for manually changing video tracks (decisions).
 * Handles clicking on individual video buttons in the decision list.
 */
function set_multitrack_manual_listeners() {
    // Unbind existing listeners to prevent duplicates
    $('.multitrack-manual-video-track-button').off('click');

    // Listener for manual video track buttons
    $('.multitrack-manual-video-track-button').on('click', function (event) {
        // Find the parent row element
        let rowElement = $(this).closest('.multitrack-video-table-row');
        console.log('Row element: ', rowElement);

        // Calculate the decision index (0-based) from the 1-based decision number
        // Formula from obfuscated code: -0x699*0x3+0xc1*-0x1c+0x28e8 = -5067 - 5404 + 10472 = 1
        // So it's parseInt(attr) - 1
        let decisionIndex = parseInt(rowElement.attr('multitrack-video-decision-number')) - 1;
        console.log('Decision index: ', decisionIndex);

        // Show the list of available video angles for this decision
        multitrack_decisions_show_video_switch_list(decisionIndex);

        // Remove focus
        $(this).blur();
    });

    // Listener for resetting all overrides
    $('#multitrack-video-reset-overrides').off('click');
    $('#multitrack-video-reset-overrides').on('click', function (event) {
        // Clear all manual overrides
        g_multitrack_decision_overrides = [];

        // Re-run the decision logic
        if (multitrack_wide_shots_available()) {
            test_101_finetune_multitrack_wide_shot_tolerance();
        } else {
            test_51_decide_multicam_cuts();
        }

        // Update the UI
        writeHTML_multitrack_decisions_table();
        update_multitrack_screentime_chart();
    });
}

