     1→<!DOCTYPE html>
     2→<html lang="en">
     3→
     4→<head>
     5→    <meta charset="UTF-8">
     6→    <meta name="viewport" content="width=device-width, initial-scale=1.0">
     7→    <title>SPLICE CEP - Simulation Mode</title>
     8→    <link rel="stylesheet" href="panel/css/style.css">
     9→    <style>
    10→        /* Simulation Mode Indicator */
    11→        .simulation-banner {
    12→            position: fixed;
    13→            top: 0;
    14→            left: 0;
    15→            right: 0;
    16→            background: linear-gradient(90deg, #ff6b6b, #feca57, #48dbfb, #ff9ff3);
    17→            color: #1a1a2e;
    18→            text-align: center;
    19→            padding: 4px 10px;
    20→            font-size: 11px;
    21→            font-weight: 600;
    22→            z-index: 9999;
    23→            display: flex;
    24→            justify-content: center;
    25→            align-items: center;
    26→            gap: 15px;
    27→        }
    28→
    29→        .simulation-banner button {
    30→            background: #1a1a2e;
    31→            color: #fff;
    32→            border: none;
    33→            padding: 3px 10px;
    34→            border-radius: 4px;
    35→            cursor: pointer;
    36→            font-size: 10px;
    37→            font-weight: 600;
    38→        }
    39→
    40→        .simulation-banner button:hover {
    41→            background: #333;
    42→        }
    43→
    44→        /* Offset body for banner */
    45→        body {
    46→            margin-top: 28px !important;
    47→        }
    48→
    49→        /* Test Panel Overlay */
    50→        #test-panel {
    51→            position: fixed;
    52→            top: 28px;
    53→            right: 0;
    54→            width: 320px;
    55→            height: calc(100vh - 28px);
    56→            background: #1a1a2e;
    57→            border-left: 2px solid #00d1b2;
    58→            overflow-y: auto;
    59→            z-index: 9998;
    60→            display: none;
    61→            padding: 15px;
    62→            box-sizing: border-box;
    63→        }
    64→
    65→        #test-panel.visible {
    66→            display: block;
    67→        }
    68→
    69→        #test-panel h2 {
    70→            color: #00d1b2;
    71→            font-size: 14px;
    72→            margin: 0 0 15px 0;
    73→            padding-bottom: 10px;
    74→            border-bottom: 1px solid #333;
    75→        }
    76→
    77→        #test-panel .test-controls {
    78→            display: flex;
    79→            flex-wrap: wrap;
    80→            gap: 8px;
    81→            margin-bottom: 15px;
    82→        }
    83→
    84→        #test-panel .test-controls button {
    85→            flex: 1 1 45%;
    86→            padding: 8px;
    87→            font-size: 11px;
    88→            background: #2d2d44;
    89→            border: 1px solid #00d1b2;
    90→            color: #00d1b2;
    91→            border-radius: 4px;
    92→            cursor: pointer;
    93→        }
    94→
    95→        #test-panel .test-controls button:hover {
    96→            background: #00d1b2;
    97→            color: #1a1a2e;
    98→        }
    99→
   100→        #test-panel .test-controls button.run-all {
   101→            flex: 1 1 100%;
   102→            font-weight: bold;
   103→            font-size: 12px;
   104→        }
   105→
   106→        #test-log {
   107→            background: #0d0d15;
   108→            border: 1px solid #333;
   109→            border-radius: 4px;
   110→            padding: 10px;
   111→            font-family: 'Monaco', 'Menlo', monospace;
   112→            font-size: 10px;
   113→            max-height: 400px;
   114→            overflow-y: auto;
   115→            white-space: pre-wrap;
   116→            color: #aaa;
   117→        }
   118→
   119→        #test-log .pass {
   120→            color: #00ff00;
   121→        }
   122→
   123→        #test-log .fail {
   124→            color: #ff0000;
   125→        }
   126→
   127→        #test-log .skip {
   128→            color: #ffaa00;
   129→        }
   130→
   131→        #test-log .suite {
   132→            color: #00d1b2;
   133→            font-weight: bold;
   134→        }
   135→
   136→        /* Shrink main content when test panel is visible */
   137→        body.test-panel-open {
   138→            margin-right: 320px;
   139→        }
   140→
   141→        /* Console output styling */
   142→        #console-output {
   143→            background: #0d0d15;
   144→            border: 1px solid #333;
   145→            border-radius: 4px;
   146→            padding: 10px;
   147→            font-family: 'Monaco', 'Menlo', monospace;
   148→            font-size: 10px;
   149→            max-height: 200px;
   150→            overflow-y: auto;
   151→            margin-top: 15px;
   152→            color: #888;
   153→        }
   154→
   155→        #console-output .log {
   156→            color: #aaa;
   157→        }
   158→
   159→        #console-output .warn {
   160→            color: #ffaa00;
   161→        }
   162→
   163→        #console-output .error {
   164→            color: #ff6b6b;
   165→        }
   166→
   167→        /* Stats display */
   168→        .test-stats {
   169→            display: flex;
   170→            justify-content: space-around;
   171→            padding: 10px;
   172→            background: #2d2d44;
   173→            border-radius: 4px;
   174→            margin-bottom: 15px;
   175→        }
   176→
   177→        .test-stats .stat {
   178→            text-align: center;
   179→        }
   180→
   181→        .test-stats .stat-value {
   182→            font-size: 24px;
   183→            font-weight: bold;
   184→        }
   185→
   186→        .test-stats .stat-label {
   187→            font-size: 10px;
   188→            color: #888;
   189→        }
   190→
   191→        .test-stats .passed .stat-value {
   192→            color: #00ff00;
   193→        }
   194→
   195→        .test-stats .failed .stat-value {
   196→            color: #ff0000;
   197→        }
   198→
   199→        .test-stats .skipped .stat-value {
   200→            color: #ffaa00;
   201→        }
   202→    </style>
   203→</head>
   204→
   205→<body>
   206→    <!-- Simulation Banner -->
   207→    <div class="simulation-banner">
   208→        <span>SIMULATION MODE - Not connected to Premiere Pro</span>
   209→        <button id="toggle-test-panel">Show Tests</button>
   210→        <button id="run-quick-test">Quick Test</button>
   211→        <button id="run-all-tests">Run All Tests</button>
   212→    </div>
   213→
   214→    <!-- Test Panel -->
   215→    <div id="test-panel">
   216→        <h2>Test Runner</h2>
   217→
   218→        <div class="test-stats">
   219→            <div class="stat passed">
   220→                <div class="stat-value" id="stat-passed">0</div>
   221→                <div class="stat-label">Passed</div>
   222→            </div>
   223→            <div class="stat failed">
   224→                <div class="stat-value" id="stat-failed">0</div>
   225→                <div class="stat-label">Failed</div>
   226→            </div>
   227→            <div class="stat skipped">
   228→                <div class="stat-value" id="stat-skipped">0</div>
   229→                <div class="stat-label">Skipped</div>
   230→            </div>
   231→        </div>
   232→
   233→        <div class="test-controls">
   234→            <button class="run-all" onclick="runAllTestsWithUI()">Run All Tests</button>
   235→            <button onclick="runSuite('quickEdit')">Quick Edit</button>
   236→            <button onclick="runSuite('multitrack')">Multitrack</button>
   237→            <button onclick="runSuite('captions')">Captions</button>
   238→            <button onclick="runSuite('textEditor')">Text Editor</button>
   239→            <button onclick="runSuite('socialReframe')">Social Reframe</button>
   240→            <button onclick="runSuite('music')">Music</button>
   241→            <button onclick="runSuite('authentication')">Auth</button>
   242→            <button onclick="runSuite('edgeCases')">Edge Cases</button>
   243→            <button onclick="runSuite('uiState')">UI State</button>
   244→            <button onclick="runSuite('jsxBridge')">JSX Bridge</button>
   245→            <button onclick="runSuite('globalState')">Global State</button>
   246→        </div>
   247→
   248→        <h3 style="color: #888; font-size: 11px; margin: 10px 0 5px;">Test Output</h3>
   249→        <div id="test-log"></div>
   250→
   251→        <h3 style="color: #888; font-size: 11px; margin: 15px 0 5px;">Console Log</h3>
   252→        <div id="console-output"></div>
   253→    </div>
   254→
   255→    <!-- Loading Overlay -->
   256→    <div id="loading-overlay">
   257→        <div class="loader-logo"></div>
   258→        <div class="loader-text">Initializing Antigravity...</div>
   259→    </div>
   260→
   261→    <!-- Header -->
   262→    <div class="header">
   263→        <h1>SPLICE</h1>
   264→        <div class="header-actions">
   265→            <div id="creditBadge" class="credit-badge login">Login</div>
   266→            <button id="helpBtn" class="icon-btn" title="Help">?</button>
   267→            <button id="settingsBtn" class="icon-btn" title="Settings">⚙</button>
   268→        </div>
   269→    </div>
   270→
   271→    <!-- Status Bar -->
   272→    <div id="status" role="status" aria-live="polite">Ready</div>
   273→
   274→    <!-- Main Action Card -->
   275→    <div class="action-card">
   276→        <h2>Quick Edit</h2>
   277→
   278→        <!-- GO Button -->
   279→        <button id="goBtn" class="go-btn" aria-label="Start detection">GO</button>
   280→
   281→        <!-- Options Toggle -->
   282→        <button id="optionsToggle" class="options-toggle" aria-expanded="false">Options</button>
   283→
   284→        <!-- Options Panel -->
   285→        <div id="optionsPanel" class="options-panel collapsed">
   286→            <!-- Preset Selector -->
   287→            <div class="form-group">
   288→                <label for="presetSelector">Preset</label>
   289→                <select id="presetSelector">
   290→                    <option value="podcast">Podcast</option>
   291→                    <option value="interview">Interview</option>
   292→                    <option value="reaction">Reaction</option>
   293→                    <option value="tutorial">Tutorial</option>
   294→                    <option value="vlog">Vlog</option>
   295→                    <option value="custom">Custom</option>
   296→                </select>
   297→            </div>
   298→
   299→            <!-- Sensitivity Slider -->
   300→            <div class="slider-container">
   301→                <label for="sensitivitySlider">Sensitivity: <span id="sensitivityValue">50</span></label>
   302→                <input type="range" id="sensitivitySlider" min="0" max="100" value="50">
   303→                <div class="slider-labels">
   304→                    <span>Less cuts</span>
   305→                    <span>More cuts</span>
   306→                </div>
   307→            </div>
   308→
   309→            <!-- Audio Source -->
   310→            <div class="audio-source-group">
   311→                <label>Audio Source</label>
   312→                <label class="checkbox-label">
   313→                    <input type="checkbox" id="sourceOriginal" checked>
   314→                    Original Audio
   315→                </label>
   316→                <label class="checkbox-label">
   317→                    <input type="checkbox" id="sourceIsolated">
   318→                    Isolated Vocals <span class="tier-badge">PRO</span>
   319→                </label>
   320→            </div>
   321→
   322→            <!-- Feature Toggles -->
   323→            <div class="feature-toggles">
   324→                <label class="checkbox-label">
   325→                    <input type="checkbox" id="enableTakesDetection" checked>
   326→                    Detect Takes
   327→                </label>
   328→
   329→                <label class="checkbox-label">
   330→                    <input type="checkbox" id="enableJCut">
   331→                    J-Cut / L-Cut
   332→                </label>
   333→                <div id="jcutSettings" class="sub-options collapsed">
   334→                    <div class="slider-container small">
   335→                        <label>Lead In: <span id="jcutLeadInValue">0.30s</span></label>
   336→                        <input type="range" id="jcutLeadIn" min="0" max="100" value="30">
   337→                    </div>
   338→                    <div class="slider-container small">
   339→                        <label>Lead Out: <span id="jcutLeadOutValue">0.20s</span></label>
   340→                        <input type="range" id="jcutLeadOut" min="0" max="100" value="20">
   341→                    </div>
   342→                </div>
   343→
   344→                <label class="checkbox-label">
   345→                    <input type="checkbox" id="enableZoom">
   346→                    Auto Zoom
   347→                </label>
   348→                <div id="zoomSettings" class="sub-options collapsed">
   349→                    <div class="form-group small">
   350→                        <label>Frequency</label>
   351→                        <select id="zoomFrequency">
   352→                            <option value="low">Low (~1/min)</option>
   353→                            <option value="medium" selected>Medium (~2/min)</option>
   354→                            <option value="high">High (~4/min)</option>
   355→                        </select>
   356→                    </div>
   357→                    <div class="form-group small">
   358→                        <label>Intensity</label>
   359→                        <select id="zoomPreset">
   360→                            <option value="subtle">Subtle (110%)</option>
   361→                            <option value="medium" selected>Medium (120%)</option>
   362→                            <option value="dramatic">Dramatic (140%)</option>
   363→                        </select>
   364→                    </div>
   365→                    <div class="form-group small">
   366→                        <label>Placement</label>
   367→                        <select id="zoomPlacement">
   368→                            <option value="sentence_start" selected>Sentence Starts</option>
   369→                            <option value="keywords">Keywords</option>
   370→                            <option value="random">Random</option>
   371→                        </select>
   372→                    </div>
   373→                </div>
   374→
   375→                <label class="checkbox-label">
   376→                    <input type="checkbox" id="enableChapters">
   377→                    Detect Chapters
   378→                </label>
   379→                <div id="chapterSettings" class="sub-options collapsed">
   380→                    <div class="form-group small">
   381→                        <label>Max Chapters</label>
   382→                        <input type="number" id="maxChapters" min="1" max="50" value="10">
   383→                    </div>
   384→                    <div class="form-group small">
   385→                        <label>Min Length (s)</label>
   386→                        <input type="number" id="minChapterLength" min="30" max="600" value="60">
   387→                    </div>
   388→                </div>
   389→
   390→                <!-- Profanity Bleeping -->
   391→                <label class="checkbox-label">
   392→                    <input type="checkbox" id="enableProfanity">
   393→                    Profanity Detection
   394→                </label>
   395→                <div id="profanitySettings" class="sub-options collapsed">
   396→                    <div class="form-group small">
   397→                        <label>Bleep Type</label>
   398→                        <select id="bleepType">
   399→                            <option value="standard">Standard (1kHz)</option>
   400→                            <option value="tv">TV Bleep</option>
   401→                            <option value="radio">Radio Bleep</option>
   402→                            <option value="duck">Duck Audio</option>
   403→                            <option value="mute">Mute</option>
   404→                        </select>
   405→                    </div>
   406→                    <div class="form-group small">
   407→                        <label>Volume</label>
   408→                        <input type="range" id="bleepVolume" min="0" max="100" value="50">
   409→                    </div>
   410→                    <label class="checkbox-label">
   411→                        <input type="checkbox" id="enableBleepInsertion">
   412→                        Auto-insert bleeps
   413→                    </label>
   414→                </div>
   415→
   416→                <!-- Filler Word Detection -->
   417→                <label class="checkbox-label">
   418→                    <input type="checkbox" id="enableFillerDetection">
   419→                    Filler Word Detection
   420→                </label>
   421→                <div id="fillerSettings" class="sub-options collapsed">
   422→                    <div class="form-group small">
   423→                        <label>Custom Fillers</label>
   424→                        <input type="text" id="customFillers" placeholder="basically, literally">
   425→                    </div>
   426→                    <button id="detectFillersBtn" class="btn btn-small">Detect Fillers</button>
   427→                    <button id="removeFillersBtn" class="btn btn-small btn-secondary" disabled>Remove Fillers</button>
   428→                </div>
   429→            </div>
   430→        </div>
   431→    </div>
   432→
   433→    <!-- Multitrack Section -->
   434→    <div id="multitrackSection" class="multitrack-section">
   435→        <div id="multitrackToggle" class="section-toggle">
   436→            <span class="toggle-icon">+</span>
   437→            <span>Multitrack / Multi-Speaker</span>
   438→        </div>
   439→        <div id="multitrackPanel" class="multitrack-panel collapsed">
   440→            <!-- Speaker Configuration -->
   441→            <div class="speaker-config">
   442→                <div class="speaker-config-header">
   443→                    <span>Speakers</span>
   444→                    <button id="addSpeakerBtn" class="btn btn-small">+ Add</button>
   445→                </div>
   446→                <div id="speakerList" class="speaker-list">
   447→                    <div class="speaker-item speaker-0" data-speaker-index="0">
   448→                        <input type="text" class="speaker-name" value="Speaker 1" placeholder="Name">
   449→                        <select class="speaker-track">
   450→                            <option value="0" selected>V1</option>
   451→                            <option value="1">V2</option>
   452→                            <option value="2">V3</option>
   453→                            <option value="3">V4</option>
   454→                        </select>
   455→                        <button class="speaker-remove-btn" title="Remove speaker" style="display: none;">x</button>
   456→                    </div>
   457→                    <div class="speaker-item speaker-1" data-speaker-index="1">
   458→                        <input type="text" class="speaker-name" value="Speaker 2" placeholder="Name">
   459→                        <select class="speaker-track">
   460→                            <option value="0">V1</option>
   461→                            <option value="1" selected>V2</option>
   462→                            <option value="2">V3</option>
   463→                            <option value="3">V4</option>
   464→                        </select>
   465→                        <button class="speaker-remove-btn" title="Remove speaker" style="display: none;">x</button>
   466→                    </div>
   467→                </div>
   468→            </div>
   469→
   470→            <!-- Parameters -->
   471→            <div class="multitrack-params">
   472→                <div class="slider-container small">
   473→                    <label>Min Shot Duration: <span id="minShotDurationValue">2.0s</span></label>
   474→                    <input type="range" id="minShotDuration" min="0.5" max="10" step="0.5" value="2">
   475→                </div>
   476→                <div class="slider-container small">
   477→                    <label>Wide Shot %: <span id="wideShotPercentValue">20%</span></label>
   478→                    <input type="range" id="wideShotPercent" min="0" max="50" step="5" value="20">
   479→                </div>
   480→                <div class="slider-container small">
   481→                    <label>Switching Frequency: <span id="switchingFrequencyValue">50</span></label>
   482→                    <input type="range" id="switchingFrequency" min="10" max="100" step="10" value="50">
   483→                </div>
   484→                <label class="checkbox-label">
   485→                    <input type="checkbox" id="enableWideShotDetection" checked>
   486→                    Enable Wide Shot Detection
   487→                </label>
   488→                <div class="form-group small">
   489→                    <label>Wide Shot Track</label>
   490→                    <select id="wideShotTrack">
   491→                        <option value="-1">None</option>
   492→                        <option value="0">V1</option>
   493→                        <option value="1">V2</option>
   494→                        <option value="2">V3</option>
   495→                        <option value="3">V4</option>
   496→                    </select>
   497→                </div>
   498→            </div>
   499→
   500→            <!-- Action Buttons -->
   501→            <div class="multitrack-actions">
   502→                <button id="analyzeMultitrackBtn" class="btn btn-primary">Analyze</button>
   503→                <button id="autoBalanceBtn" class="btn btn-secondary">Auto-Balance</button>
   504→            </div>
   505→
   506→            <!-- Results Preview -->
   507→            <div id="multitrackPreview" class="multitrack-preview hidden">
   508→                <div class="distribution-chart-container">
   509→                    <label>Speaker Distribution</label>
   510→                    <div id="distributionChart" class="distribution-chart"></div>
   511→                </div>
   512→                <div id="speakerStats" class="speaker-stats"></div>
   513→                <div class="decision-list-header">
   514→                    <span>Switching Decisions (<span id="decisionCount">0</span>)</span>
   515→                </div>
   516→                <div id="decisionList" class="decision-list"></div>
   517→                <div class="multitrack-preview-actions">
   518→                    <button id="applyMultitrackBtn" class="btn btn-primary" disabled>Apply</button>
   519→                </div>
   520→            </div>
   521→        </div>
   522→    </div>
   523→
   524→    <!-- Animated Captions Section -->
   525→    <div id="captionsSection" class="captions-section">
   526→        <div id="captionsToggle" class="section-toggle">
   527→            <span class="toggle-icon">+</span>
   528→            <span>Animated Captions</span>
   529→        </div>
   530→        <div id="captionsPanel" class="captions-panel collapsed">
   531→            <!-- Template Gallery -->
   532→            <div class="caption-templates">
   533→                <label>Template Style</label>
   534→                <div id="captionTemplateGallery" class="caption-template-gallery"></div>
   535→            </div>
   536→
   537→            <!-- Settings -->
   538→            <div class="caption-settings">
   539→                <div class="form-group small">
   540→                    <label>Words per Line</label>
   541→                    <select id="captionWordsPerLine">
   542→                        <option value="2">2 words</option>
   543→                        <option value="3">3 words</option>
   544→                        <option value="4" selected>4 words</option>
   545→                        <option value="5">5 words</option>
   546→                    </select>
   547→                </div>
   548→                <label class="checkbox-label">
   549→                    <input type="checkbox" id="captionHighlightKeywords" checked>
   550→                    Highlight Keywords
   551→                </label>
   552→                <label class="checkbox-label">
   553→                    <input type="checkbox" id="captionInsertEmojis">
   554→                    Insert Emojis
   555→                </label>
   556→                <div class="form-group small" id="emojiFrequencyGroup" style="display: none;">
   557→                    <label>Emoji Frequency</label>
   558→                    <select id="captionEmojiFrequency">
   559→                        <option value="low">Low</option>
   560→                        <option value="medium" selected>Medium</option>
   561→                        <option value="high">High</option>
   562→                    </select>
   563→                </div>
   564→            </div>
   565→
   566→            <!-- Action Buttons -->
   567→            <div class="caption-actions">
   568→                <button id="generateCaptionsBtn" class="btn btn-primary">Generate Captions</button>
   569→            </div>
   570→
   571→            <!-- Stats -->
   572→            <div id="captionStats" class="caption-stats"></div>
   573→
   574→            <!-- Status -->
   575→            <div id="captionStatus" class="caption-status"></div>
   576→
   577→            <!-- Preview -->
   578→            <div id="captionPreviewContainer" class="caption-preview-container"></div>
   579→
   580→            <!-- Export Actions -->
