    67→
    68→// Generate ETag from content
    69→function generateETag(content) {
    70→  return `"${crypto.createHash('md5').update(JSON.stringify(content)).digest('hex')}"`;
    71→}
    72→
    73→// Static response cache - populated at startup
    74→const STATIC_RESPONSE_CACHE = {
    75→  profanityLanguages: null,
    76→  profanityBleeps: null,
    77→  exportFormats: null
    78→};
    79→
    80→// Initialize cache at startup (called after server starts)
    81→function initializeStaticCache() {
    82→  // Cache profanity languages
    83→  const languages = getSupportedLanguages();
    84→  STATIC_RESPONSE_CACHE.profanityLanguages = {
    85→    body: { success: true, languages },
    86→    etag: generateETag({ languages })
    87→  };
    88→
    89→  // Cache profanity bleeps
    90→  const sounds = getAvailableBleepSounds();
    91→  STATIC_RESPONSE_CACHE.profanityBleeps = {
    92→    body: { success: true, sounds },
    93→    etag: generateETag({ sounds })
    94→  };
    95→
    96→  // Cache export formats
    97→  const formats = getSupportedFormats();
    98→  STATIC_RESPONSE_CACHE.exportFormats = {
    99→    body: { success: true, formats },
   100→    etag: generateETag({ formats })
   101→  };
   102→
   103→  console.log('[SPLICE] Static response cache initialized');
   104→}
   105→
   106→// Helper to send cached response with ETag
   107→function sendCachedResponse(req, res, cacheKey) {
   108→  const cached = STATIC_RESPONSE_CACHE[cacheKey];
   109→  if (!cached) {
   110→    return res.status(500).json({ error: 'Cache not initialized' });
   111→  }
   112→
   113→  // Check If-None-Match header for conditional GET
   114→  const clientETag = req.headers['if-none-match'];
   115→  if (clientETag === cached.etag) {
   116→    return res.status(304).end(); // Not Modified
   117→  }
   118→
   119→  // Send cached response with ETag and cache headers
   120→  res.set('ETag', cached.etag);
   121→  res.set('Cache-Control', 'public, max-age=86400'); // Cache for 24 hours
   122→  res.json(cached.body);
   123→}
