   149→// CORS whitelist - restrict origins in production
   150→const CORS_WHITELIST = [
   151→  'http://localhost:3000',
   152→  'http://localhost:3847',
   153→  'https://localhost:3847',
   154→  'http://127.0.0.1:3000',
   155→  'http://127.0.0.1:3847',
   156→  'https://127.0.0.1:3847',
   157→  'https://splice.app',
   158→  'https://www.splice.app',
   159→  'https://splice-api-production.up.railway.app',
   160→  // Adobe CEP/UXP panels run from file:// or bolt://
   161→  'file://',
   162→  'bolt://'
   163→];
   164→
   165→const corsOptions = {
   166→  origin: function (origin, callback) {
   167→    // Allow requests with no origin (like mobile apps, Postman, or Adobe plugins)
   168→    if (!origin) {
   169→      return callback(null, true);
   170→    }
   171→    // Check if origin is in whitelist
   172→    if (CORS_WHITELIST.some(allowed => origin.startsWith(allowed) || origin === allowed)) {
   173→      return callback(null, true);
   174→    }
   175→    // In development, allow all origins with warning
   176→    if (!isProduction) {
   177→      console.warn(`[CORS] Non-whitelisted origin in dev: ${origin}`);
   178→      return callback(null, true);
   179→    }
   180→    // In production, reject non-whitelisted origins
   181→    console.error(`[CORS] Blocked request from: ${origin}`);
   182→    return callback(new Error('Not allowed by CORS'));
   183→  },
   184→  credentials: true,
   185→  methods: ['GET', 'POST', 'PUT', 'DELETE', 'OPTIONS'],
   186→  allowedHeaders: ['Content-Type', 'Authorization', 'x-stripe-customer-id', 'stripe-signature']
   187→};
   188→
