   217→// Helper to determine tier from price ID with logging
   218→function getTierFromPriceId(priceId) {
   219→  if (priceId === process.env.STRIPE_PRICE_STARTER) return 'starter';
   220→  if (priceId === process.env.STRIPE_PRICE_PRO) return 'pro';
   221→  if (priceId === process.env.STRIPE_PRICE_TEAM) return 'team';
   222→
   223→  // Log unknown price ID for debugging
   224→  console.warn(`[SPLICE] Unknown price ID: ${priceId} - defaulting to starter tier`);
   225→  return 'starter';
   226→}
