   228→// =============================================================================
   229→// Stripe Webhook (must be before express.json())
   230→// =============================================================================
   231→
   232→app.post('/webhooks/stripe', express.raw({ type: 'application/json' }), async (req, res) => {
   233→  const sig = req.headers['stripe-signature'];
   234→  const webhookSecret = process.env.STRIPE_WEBHOOK_SECRET;
   235→
   236→  let event;
   237→
   238→  try {
   239→    if (webhookSecret) {
   240→      event = stripe.webhooks.constructEvent(req.body, sig, webhookSecret);
   241→    } else if (isProduction) {
   242→      // SECURITY: Reject unsigned webhooks in production
   243→      console.error('[SPLICE] CRITICAL: STRIPE_WEBHOOK_SECRET not set in production');
   244→      return res.status(500).json({ error: 'Webhook configuration error: secret not configured' });
   245→    } else {
   246→      // For local development testing only
   247→      // req.body is a Buffer from express.raw(), convert to string for JSON.parse
   248→      const bodyString = typeof req.body === 'string' ? req.body : req.body.toString('utf8');
   249→      event = JSON.parse(bodyString);
   250→      console.warn('[SPLICE] Warning: Processing webhook without signature verification (dev only)');
   251→    }
   252→  } catch (err) {
   253→    console.error('[SPLICE] Webhook signature verification failed:', err.message);
   254→    return res.status(400).json({ error: 'Webhook signature verification failed' });
   255→  }
   256→
   257→  console.log(`[SPLICE] Webhook received: ${event.type} (${event.id})`);
   258→
   259→  // Idempotency check - skip if already processed
   260→  if (await usageTracking.isEventProcessed(event.id)) {
   261→    console.log(`[SPLICE] Event ${event.id} already processed, skipping`);
   262→    return res.json({ received: true, skipped: true });
   263→  }
   264→
   265→  try {
   266→    switch (event.type) {
   267→      case 'customer.subscription.created':
   268→      case 'customer.subscription.updated': {
   269→        const subscription = event.data.object;
   270→        const customerId = subscription.customer;
   271→
   272→        // Validate customerId
   273→        if (!customerId) {
   274→          console.error('[SPLICE] Missing customer ID in subscription event');
   275→          return res.status(400).json({ error: 'Missing customer ID' });
   276→        }
   277→
   278→        // Get tier from price ID
   279→        const priceId = subscription.items?.data?.[0]?.price?.id;
   280→        const tier = getTierFromPriceId(priceId);
   281→
   282→        // Update user tier and reset hours
   283→        await usageTracking.updateTier(customerId, tier);
   284→        console.log(`[SPLICE] Updated customer ${customerId} to tier: ${tier}`);
   285→
   286→        // Generate license key for new subscriptions with retry and delivery
   287→        if (event.type === 'customer.subscription.created') {
   288→          let licenseResult = null;
   289→          let retryCount = 0;
   290→          const maxRetries = 3;
   291→
   292→          // Retry mechanism for license key generation
   293→          while (retryCount < maxRetries) {
   294→            licenseResult = await licenseService.generateLicenseKey(customerId);
   295→            if (licenseResult.success) {
   296→              break;
   297→            }
   298→            retryCount++;
   299→            console.warn(`[SPLICE] License key generation attempt ${retryCount}/${maxRetries} failed: ${licenseResult.error}`);
   300→            // Wait before retry (exponential backoff)
   301→            if (retryCount < maxRetries) {
   302→              await new Promise(resolve => setTimeout(resolve, 1000 * retryCount));
   303→            }
   304→          }
   305→
   306→          if (licenseResult && licenseResult.success) {
   307→            // SECURITY: Mask license key in logs
   308→            console.log(`[SPLICE] Generated license key for ${maskSensitiveData(customerId)}: ${maskSensitiveData(licenseResult.key)}`);
   309→
   310→            // Store license key in Stripe subscription metadata as backup
   311→            try {
   312→              await stripe.subscriptions.update(subscription.id, {
   313→                metadata: {
   314→                  license_key: licenseResult.key,
   315→                  license_generated_at: new Date().toISOString()
   316→                }
   317→              });
   318→              console.log(`[SPLICE] Stored license key in Stripe metadata for subscription ${subscription.id}`);
   319→            } catch (metaErr) {
   320→              console.error(`[SPLICE] Failed to store license key in Stripe metadata:`, metaErr.message);
   321→            }
   322→
   323→            // Get customer email and send license key
   324→            try {
   325→              const customer = await stripe.customers.retrieve(customerId);
   326→              if (customer.email) {
   327→                // SECURITY: Mask sensitive data in logs
   328→                console.log(`[SPLICE] License key ready for delivery to ${maskSensitiveData(customer.email)}: ${maskSensitiveData(licenseResult.key)}`);
   329→                // TODO: Integrate with email service (SendGrid, SES, etc.)
