   343→            console.error(`[SPLICE] ${errorMsg}`);
   344→            return res.status(500).json({ error: errorMsg });
   345→          }
   346→        }
   347→        break;
   348→      }
   349→
   350→      case 'customer.subscription.deleted': {
   351→        const subscription = event.data.object;
   352→        const customerId = subscription.customer;
   353→
   354→        // Validate customerId
   355→        if (!customerId) {
   356→          console.error('[SPLICE] Missing customer ID in subscription.deleted event');
   357→          return res.status(400).json({ error: 'Missing customer ID' });
   358→        }
   359→
   360→        // Downgrade to cancelled (0 hours)
   361→        await usageTracking.updateTier(customerId, 'cancelled');
   362→        console.log(`[SPLICE] Subscription cancelled for customer ${customerId}`);
   363→        break;
   364→      }
   365→
   366→      case 'invoice.payment_succeeded': {
   367→        const invoice = event.data.object;
   368→        const customerId = invoice.customer;
   369→        const subscriptionId = invoice.subscription;
   370→
   371→        // Validate customerId
   372→        if (!customerId) {
   373→          console.error('[SPLICE] Missing customer ID in invoice event');
   374→          return res.status(400).json({ error: 'Missing customer ID' });
   375→        }
   376→
   377→        // Reset hours on successful payment (new billing period)
   378→        if (subscriptionId) {
   379→          const subscription = await stripe.subscriptions.retrieve(subscriptionId);
   380→          const priceId = subscription.items?.data?.[0]?.price?.id;
   381→          const tier = getTierFromPriceId(priceId);
   382→
   383→          await usageTracking.resetHours(customerId, tier);
   384→          console.log(`[SPLICE] Reset hours for customer ${customerId} (tier: ${tier})`);
   385→        }
   386→        break;
   387→      }
   388→
   389→      default:
   390→        console.log(`[SPLICE] Unhandled event type: ${event.type}`);
   391→    }
   392→
   393→    // Record event as processed (idempotency)
   394→    await usageTracking.recordWebhookEvent(event.id, event.type);
   395→
   396→    res.json({ received: true });
   397→  } catch (err) {
   398→    console.error('[SPLICE] Webhook handler error:', err);
   399→    res.status(500).json({ error: err.message });
   400→  }
