    98→    await client.query(`
    99→      CREATE INDEX IF NOT EXISTS idx_usage_log_user_id ON usage_log(user_id)
   100→    `);
   101→
   102→    // PERFORMANCE: Add composite index for efficient customer usage history queries
   103→    await client.query(`
   104→      CREATE INDEX IF NOT EXISTS idx_usage_log_customer_created
   105→      ON usage_log(stripe_customer_id, created_at DESC)
   106→    `);
   107→
   108→    // Webhook events table for idempotency
