   276→/**
   277→ * Confirm a reservation (finalize usage) or adjust for actual duration
   278→ * Optimized: uses RETURNING to avoid extra SELECT after UPDATE
   279→ *
   280→ * @param {string} stripeCustomerId - Customer ID
   281→ * @param {number} actualSeconds - Actual processing time in seconds
   282→ * @param {number} reservedSeconds - Originally reserved seconds
   283→ * @param {string} endpoint - Endpoint name
   284→ * @returns {Promise<object>} Updated balance
   285→ */
   286→async function confirmReservation(stripeCustomerId, actualSeconds, reservedSeconds, endpoint = 'unknown') {
   287→  const actualHours = actualSeconds / 3600;
   288→  const reservedHours = reservedSeconds / 3600;
   289→  const difference = reservedHours - actualHours;
   290→
   291→  const client = await pool.connect();
   292→  try {
   293→    await client.query('BEGIN');
   294→
   295→    // Lock the row and get current state
   296→    const result = await client.query(
   297→      `SELECT id, hours_remaining, hours_total, isolation_hours_remaining, isolation_hours_total, tier
   298→       FROM users
   299→       WHERE stripe_customer_id = $1
   300→       FOR UPDATE`,
   301→      [stripeCustomerId]
   302→    );
   303→
   304→    if (result.rows.length === 0) {
   305→      await client.query('ROLLBACK');
   306→      throw new Error('User not found');
   307→    }
   308→
   309→    let user = result.rows[0];
   310→
   311→    // If actual usage was less than reserved, refund the difference
   312→    if (difference > 0) {
   313→      const updateResult = await client.query(
   314→        `UPDATE users
   315→         SET hours_remaining = hours_remaining + $1,
   316→             updated_at = CURRENT_TIMESTAMP
   317→         WHERE stripe_customer_id = $2
   318→         RETURNING hours_remaining, hours_total, isolation_hours_remaining, isolation_hours_total, tier`,
   319→        [difference, stripeCustomerId]
   320→      );
   321→      user = { ...user, ...updateResult.rows[0] };
   322→
   323→      // Log the adjustment
   324→      await client.query(
   325→        `INSERT INTO usage_log (user_id, stripe_customer_id, audio_duration_seconds, hours_used, endpoint)
   326→         VALUES ($1, $2, $3, $4, $5)`,
   327→        [user.id, stripeCustomerId, -(reservedSeconds - actualSeconds), -difference, `${endpoint}:adjusted`]
   328→      );
   329→    } else if (difference < 0) {
   330→      // Actual usage was more than reserved - deduct the extra
   331→      const updateResult = await client.query(
   332→        `UPDATE users
   333→         SET hours_remaining = GREATEST(0, hours_remaining - $1),
   334→             updated_at = CURRENT_TIMESTAMP
   335→         WHERE stripe_customer_id = $2
   336→         RETURNING hours_remaining, hours_total, isolation_hours_remaining, isolation_hours_total, tier`,
   337→        [-difference, stripeCustomerId]
   338→      );
   339→      user = { ...user, ...updateResult.rows[0] };
   340→
   341→      // Log the additional usage
   342→      await client.query(
   343→        `INSERT INTO usage_log (user_id, stripe_customer_id, audio_duration_seconds, hours_used, endpoint)
   344→         VALUES ($1, $2, $3, $4, $5)`,
   345→        [user.id, stripeCustomerId, actualSeconds - reservedSeconds, -difference, `${endpoint}:additional`]
   346→      );
   347→    }
   348→
   349→    await client.query('COMMIT');
   350→
   351→    console.log(`[UsageTracking] Confirmed ${actualHours.toFixed(4)}hrs (reserved: ${reservedHours.toFixed(4)}) for ${stripeCustomerId}`);
   352→
   353→    // Build balance from data we already have (no extra SELECT needed)
   354→    const hoursTotal = parseFloat(user.hours_total);
   355→    const hoursRemaining = parseFloat(user.hours_remaining);
   356→    const isolationHoursTotal = parseFloat(user.isolation_hours_total || 0);
   357→    const isolationHoursRemaining = parseFloat(user.isolation_hours_remaining || 0);
   358→
   359→    const tierNames = {
   360→      starter: 'Starter',
   361→      pro: 'Pro',
   362→      team: 'Team',
   363→      cancelled: 'Cancelled'
   364→    };
   365→
   366→    return {
   367→      hoursRemaining,
   368→      hoursTotal,
   369→      isolationHoursRemaining,
   370→      isolationHoursTotal,
   371→      tier: user.tier,
   372→      tierName: tierNames[user.tier] || user.tier,
   373→      percentUsed: hoursTotal > 0 ? ((hoursTotal - hoursRemaining) / hoursTotal * 100).toFixed(1) : '0.0',
   374→      isolationPercentUsed: isolationHoursTotal > 0 ? ((isolationHoursTotal - isolationHoursRemaining) / isolationHoursTotal * 100).toFixed(1) : '0.0',
   375→      hasIsolationAccess: TIER_ISOLATION_HOURS[user.tier] > 0,
   376→      isolationOverageRate: ISOLATION_OVERAGE_RATE
   377→    };
   378→  } catch (err) {
   379→    await client.query('ROLLBACK');
   380→    throw err;
   381→  } finally {
   382→    client.release();
   383→  }
   384→}
