   474→/**
   475→ * Check if user can use vocal isolation
   476→ * Returns: { allowed, hasIncludedHours, overageRequired, overageCost }
   477→ */
   478→async function checkIsolationAccess(stripeCustomerId, estimatedMinutes = 0) {
   479→  const balance = await getBalance(stripeCustomerId);
   480→
   481→  // Starter tier has no isolation access
   482→  if (!balance.hasIsolationAccess) {
   483→    return {
   484→      allowed: false,
   485→      reason: 'upgrade_required',
   486→      message: 'Vocal isolation requires Pro or Team tier',
   487→      hasIncludedHours: false,
   488→      overageRequired: false,
   489→      overageCost: 0
   490→    };
   491→  }
   492→
   493→  const estimatedHours = estimatedMinutes / 60;
   494→  const remainingIncluded = balance.isolationHoursRemaining;
   495→
   496→  // Check if within included hours
   497→  if (remainingIncluded >= estimatedHours) {
   498→    return {
   499→      allowed: true,
   500→      reason: 'included',
   501→      message: `Using ${estimatedMinutes.toFixed(1)} min of included isolation time`,
   502→      hasIncludedHours: true,
   503→      overageRequired: false,
   504→      overageCost: 0,
   505→      isolationHoursRemaining: remainingIncluded - estimatedHours
   506→    };
   507→  }
   508→
   509→  // Calculate overage
   510→  const overageMinutes = estimatedMinutes - (remainingIncluded * 60);
   511→  const overageCost = overageMinutes * ISOLATION_OVERAGE_RATE;
   512→
   513→  return {
   514→    allowed: true,
   515→    reason: 'overage',
   516→    message: `${remainingIncluded > 0 ? `Using ${(remainingIncluded * 60).toFixed(1)} included min + ` : ''}${overageMinutes.toFixed(1)} min overage ($${overageCost.toFixed(2)})`,
   517→    hasIncludedHours: remainingIncluded > 0,
   518→    overageRequired: true,
   519→    overageMinutes,
   520→    overageCost,
   521→    isolationHoursRemaining: 0
   522→  };
   523→}
