   525→/**
   526→ * Deduct isolation usage from user's balance
   527→ * Returns overage cost if beyond included hours
   528→ * Uses SELECT...FOR UPDATE to prevent race conditions (like confirmReservation)
   529→ */
   530→async function deductIsolationUsage(stripeCustomerId, audioDurationSeconds, endpoint = 'isolate-vocals') {
   531→  const durationMinutes = audioDurationSeconds / 60;
   532→  const durationHours = audioDurationSeconds / 3600;
   533→
   534→  const client = await pool.connect();
   535→  try {
   536→    await client.query('BEGIN');
   537→
   538→    // Lock the row for this customer - prevents concurrent modifications
   539→    const userResult = await client.query(
   540→      `SELECT id, isolation_hours_remaining, isolation_hours_total, hours_remaining, hours_total, tier
   541→       FROM users WHERE stripe_customer_id = $1
   542→       FOR UPDATE`,
   543→      [stripeCustomerId]
   544→    );
   545→
   546→    if (userResult.rows.length === 0) {
   547→      await client.query('ROLLBACK');
   548→      throw new Error('User not found');
   549→    }
   550→
   551→    const user = userResult.rows[0];
   552→    const remainingIncluded = parseFloat(user.isolation_hours_remaining || 0);
   553→
   554→    let overageCost = 0;
   555→    let hoursFromIncluded = 0;
   556→    let overageMinutes = 0;
   557→
   558→    if (remainingIncluded >= durationHours) {
   559→      // Fully covered by included hours
   560→      hoursFromIncluded = durationHours;
   561→    } else {
   562→      // Partial or full overage
   563→      hoursFromIncluded = remainingIncluded;
   564→      overageMinutes = durationMinutes - (remainingIncluded * 60);
   565→      overageCost = overageMinutes * ISOLATION_OVERAGE_RATE;
   566→    }
   567→
   568→    // Update isolation balance with RETURNING to get new values
   569→    const updateResult = await client.query(
   570→      `UPDATE users
   571→       SET isolation_hours_remaining = GREATEST(0, isolation_hours_remaining - $1),
   572→           updated_at = CURRENT_TIMESTAMP
   573→       WHERE stripe_customer_id = $2
   574→       RETURNING isolation_hours_remaining, isolation_hours_total, hours_remaining, hours_total, tier`,
   575→      [durationHours, stripeCustomerId]
   576→    );
   577→
   578→    const updatedUser = updateResult.rows[0];
   579→
   580→    // Log usage (mark as isolation)
   581→    await client.query(
   582→      `INSERT INTO usage_log (user_id, stripe_customer_id, audio_duration_seconds, hours_used, endpoint)
   583→       VALUES ($1, $2, $3, $4, $5)`,
   584→      [user.id, stripeCustomerId, audioDurationSeconds, durationHours, endpoint]
   585→    );
   586→
   587→    await client.query('COMMIT');
   588→
   589→    // Build balance from RETURNING data (no extra SELECT needed)
   590→    const hoursTotal = parseFloat(updatedUser.hours_total);
   591→    const hoursRemaining = parseFloat(updatedUser.hours_remaining);
   592→    const isolationHoursTotal = parseFloat(updatedUser.isolation_hours_total || 0);
   593→    const isolationHoursRemaining = parseFloat(updatedUser.isolation_hours_remaining || 0);
   594→
   595→    const tierNames = {
   596→      starter: 'Starter',
   597→      pro: 'Pro',
   598→      team: 'Team',
   599→      cancelled: 'Cancelled'
   600→    };
   601→
   602→    return {
   603→      hoursRemaining,
   604→      hoursTotal,
   605→      isolationHoursRemaining,
   606→      isolationHoursTotal,
   607→      tier: updatedUser.tier,
   608→      tierName: tierNames[updatedUser.tier] || updatedUser.tier,
   609→      percentUsed: hoursTotal > 0 ? ((hoursTotal - hoursRemaining) / hoursTotal * 100).toFixed(1) : '0.0',
   610→      isolationPercentUsed: isolationHoursTotal > 0 ? ((isolationHoursTotal - isolationHoursRemaining) / isolationHoursTotal * 100).toFixed(1) : '0.0',
   611→      hasIsolationAccess: TIER_ISOLATION_HOURS[updatedUser.tier] > 0,
   612→      isolationOverageRate: ISOLATION_OVERAGE_RATE,
   613→      isolationUsed: {
   614→        durationMinutes,
   615→        hoursFromIncluded,
   616→        overageMinutes,
   617→        overageCost
   618→      }
   619→    };
   620→  } catch (err) {
   621→    await client.query('ROLLBACK');
   622→    throw err;
   623→  } finally {
   624→    client.release();
   625→  }
   626→}
