useEffect(() => {
    let mounted = true;
    const timeouts: NodeJS.Timeout[] = [];

    const delay = (ms: number) => new Promise<void>(resolve => {
        const id = setTimeout(() => {
            if (mounted) resolve();
        }, ms);
        timeouts.push(id);
    });

    const sequence = async () => {
        // 1. Intro (Scanning raw footage)
        await delay(1000);
        if (mounted) setStep("scanning");

        // 2. Cursor moves to "Remove Silence" and clicks (handled by animations)
        // Wait for scan to finish
        await delay(2000);
        if (mounted) setStep("cutting");

        // 3. Cut animation happens
        await delay(1500);
        if (mounted) setStep("music");

        // 4. Music generation
        await delay(2000);
        if (mounted) setStep("done");
    };

    sequence();

    return () => {
        mounted = false;
        timeouts.forEach(id => clearTimeout(id));
    };
}, []);

