"use client";

import { useState, useEffect } from "react";
import { motion, AnimatePresence } from "framer-motion";
import {
    Play,
    Scissors,
    Music,
    Wand2,
    MousePointer2,
    Zap,
    CheckCircle2,
    Clock
} from "lucide-react";

// Waveforms for the "Raw" state (gappy)
const RAW_WAVEFORMS = Array.from({ length: 40 }).map((_, i) => ({
    height: Math.max(20, Math.random() * 100),
    isSilence: i % 5 === 0 || i % 6 === 0 || i % 13 === 0, // Random gaps
}));

// Waveforms for the "Cut" state (condensed, no gaps)
const CUT_WAVEFORMS = RAW_WAVEFORMS.filter(w => !w.isSilence).map(w => ({
    ...w,
    height: w.height
}));

export function ProductDemo() {
    const [step, setStep] = useState<"intro" | "scanning" | "cutting" | "music" | "done">("intro");

    useEffect(() => {
        let timeout: NodeJS.Timeout;

        const sequence = async () => {
            // 1. Intro (Scanning raw footage)
            await new Promise(r => setTimeout(r, 1000));
            setStep("scanning");

            // 2. Cursor moves to "Remove Silence" and clicks (handled by animations)
            // Wait for scan to finish
            await new Promise(r => setTimeout(r, 2000));
            setStep("cutting");

            // 3. Cut animation happens
            await new Promise(r => setTimeout(r, 1500));
            setStep("music");

            // 4. Music generation
            await new Promise(r => setTimeout(r, 2000));
            setStep("done");
        };

        sequence();

        return () => clearTimeout(timeout);
    }, []);

    return (
        <div className="relative w-full h-full bg-[#0d1420] overflow-hidden flex flex-col font-sans select-none">
            {/* --- UI Header --- */}
            <div className="h-12 border-b border-gray-800 bg-[#060b14] flex items-center justify-between px-4 z-20">
                <div className="flex items-center gap-2">
                    <div className="w-3 h-3 rounded-full bg-red-500/20 border border-red-500/50" />
                    <div className="w-3 h-3 rounded-full bg-yellow-500/20 border border-yellow-500/50" />
                    <div className="w-3 h-3 rounded-full bg-green-500/20 border border-green-500/50" />
                    <div className="ml-4 text-xs font-mono text-gray-500">SPLICE.PRPROJ</div>
                </div>
                <div className="flex items-center gap-3">
                    <div className="px-3 py-1 rounded bg-[#00D4FF]/10 text-[#00D4FF] text-[10px] font-bold border border-[#00D4FF]/20">
                        PRO
                    </div>
                </div>
            </div>

            {/* --- Main Workspace --- */}
            <div className="flex-1 flex relative">
                {/* Sidebar Controls */}
                <div className="w-60 border-r border-gray-800 bg-[#0a0f1a] p-4 flex flex-col gap-4 z-10">
                    <div className="text-xs font-bold text-gray-500 uppercase tracking-widest mb-2">Tools</div>

                    {/* Silence Remover Button */}
                    <motion.button
                        className={`group relative w-full h-12 rounded-lg border flex items-center px-3 gap-3 transition-all duration-300 ${step === "scanning" || step === "cutting" || step === "music" || step === "done"
                                ? "bg-[#00D4FF] border-[#00D4FF] shadow-[0_0_20px_rgba(0,212,255,0.3)]"
                                : "bg-gray-800/50 border-gray-700 hover:bg-gray-800"
                            }`}
                        whileTap={{ scale: 0.98 }}
                    >
                        <Scissors className={`w-5 h-5 ${step !== "intro" ? "text-black" : "text-gray-400 group-hover:text-white"
                            }`} />
                        <span className={`text-sm font-medium ${step !== "intro" ? "text-black" : "text-gray-300"
                            }`}>
                            Remove Silence
                        </span>

                        {/* Success Checkmark */}
                        {(step === "cutting" || step === "music" || step === "done") && (
                            <motion.div
                                initial={{ scale: 0 }}
                                animate={{ scale: 1 }}
                                className="absolute right-3 bg-white text-[#00D4FF] rounded-full p-0.5"
                            >
                                <CheckCircle2 className="w-3 h-3" />
                            </motion.div>
                        )}
                    </motion.button>

                    {/* AI Music Button */}
                    <motion.button
                        className={`group relative w-full h-12 rounded-lg border flex items-center px-3 gap-3 transition-all duration-300 ${step === "music" || step === "done"
                                ? "bg-purple-500 border-purple-500 shadow-[0_0_20px_rgba(168,85,247,0.3)]"
                                : "bg-gray-800/50 border-gray-700 hover:bg-gray-800"
                            }`}
                        whileTap={{ scale: 0.98 }}
                    >
                        <Music className={`w-5 h-5 ${step === "music" || step === "done" ? "text-white" : "text-gray-400 group-hover:text-white"
                            }`} />
                        <span className={`text-sm font-medium ${step === "music" || step === "done" ? "text-white" : "text-gray-300"
                            }`}>
                            Generate Music
                        </span>
                    </motion.button>

                    {/* Stats Panel (Appears later) */}
                    <AnimatePresence>
                        {(step === "cutting" || step === "music" || step === "done") && (
                            <motion.div
                                initial={{ opacity: 0, y: 10 }}
                                animate={{ opacity: 1, y: 0 }}
                                className="mt-auto p-4 rounded-xl bg-gradient-to-br from-gray-800 to-gray-900 border border-gray-700"
                            >
                                <div className="flex items-center gap-2 mb-2 text-gray-400 text-xs">
                                    <Clock className="w-3 h-3" />
                                    Time Saved
                                </div>
                                <div className="text-2xl font-bold text-white">2h 15m</div>
                                <div className="text-[10px] text-[#00D4FF] mt-1">+142 cuts made</div>
                            </motion.div>
                        )}
                    </AnimatePresence>
                </div>

                {/* Timeline Area */}
                <div className="flex-1 bg-[#060b14] p-6 flex flex-col overflow-hidden relative">
                    {/* Time markers */}
                    <div className="h-6 w-full border-b border-gray-800 mb-2 flex justify-between text-[10px] text-gray-600 font-mono">
                        <span>00:00</span><span>00:15</span><span>00:30</span><span>00:45</span><span>01:00</span>
                    </div>

                    {/* Video Track */}
                    <div className="h-32 w-full bg-[#1a2332] rounded-lg border border-gray-800 relative overflow-hidden mb-4 group">
                        <div className="absolute top-2 left-2 text-[10px] font-bold text-gray-500 uppercase tracking-wider">V1 • Raw Footage</div>

                        <div className="absolute inset-0 flex items-center px-4 gap-1">
                            {/* Waveform Animation */}
                            <AnimatePresence mode="wait">
                                {step === "intro" || step === "scanning" ? (
                                    <motion.div
                                        key="raw"
                                        exit={{ opacity: 0, scale: 0.95 }}
                                        className="flex items-end gap-[2px] h-16 w-full"
                                    >
                                        {RAW_WAVEFORMS.map((bar, i) => (
                                            <motion.div
                                                key={i}
                                                initial={{ height: 0 }}
                                                animate={{ height: `${bar.height}%` }}
                                                transition={{ delay: i * 0.02 }}
                                                className={`flex-1 rounded-sm ${bar.isSilence ? 'bg-red-500/20 h-[2px]!' : 'bg-[#00D4FF]'}`}
                                            />
                                        ))}
                                    </motion.div>
                                ) : (
                                    <motion.div
                                        key="cut"
                                        initial={{ opacity: 0, x: 20 }}
                                        animate={{ opacity: 1, x: 0 }}
                                        className="flex items-end gap-[2px] h-16 w-full"
                                    >
                                        {CUT_WAVEFORMS.map((bar, i) => (
                                            <motion.div
                                                key={i}
                                                className="flex-1 rounded-sm bg-[#00D4FF]"
                                                style={{ height: `${bar.height}%` }}
                                            />
                                        ))}
                                    </motion.div>
                                )}
                            </AnimatePresence>
                        </div>

                        {/* Scanning Line */}
                        {step === "scanning" && (
                            <motion.div
                                initial={{ left: "0%" }}
                                animate={{ left: "100%" }}
                                transition={{ duration: 1.5, ease: "linear" }}
                                className="absolute top-0 bottom-0 w-[2px] bg-red-500 shadow-[0_0_15px_rgba(239,68,68,0.5)] z-10"
                            >
                                <div className="absolute top-0 -translate-x-1/2 bg-red-500 text-white text-[8px] font-bold px-1 rounded-b">SCANNING</div>
                            </motion.div>
                        )}
                    </div>

                    {/* Audio Track */}
                    <div className="h-24 w-full bg-[#1a2332] rounded-lg border border-gray-800 relative overflow-hidden flex items-center justify-center">
                        <div className="absolute top-2 left-2 text-[10px] font-bold text-gray-500 uppercase tracking-wider">A1 • Soundtrack</div>

                        <AnimatePresence>
                            {(step === "music" || step === "done") ? (
                                <motion.div
                                    initial={{ width: 0, opacity: 0 }}
                                    animate={{ width: "95%", opacity: 1 }}
                                    transition={{ duration: 0.8, type: "spring" }}
                                    className="h-12 bg-purple-500/20 border border-purple-500/30 rounded-md flex items-center px-4 gap-3 w-[95%]"
                                >
                                    <Music className="w-5 h-5 text-purple-400 shrink-0" />
                                    <div className="flex-1 flex gap-1 h-8 items-center">
                                        {Array.from({ length: 30 }).map((_, i) => (
                                            <div key={i} className="flex-1 bg-purple-500/40 rounded-full" style={{ height: `${Math.random() * 100}%` }} />
                                        ))}
                                    </div>
                                    <div className="text-[10px] font-bold text-purple-300">AI GEN • LO-FI BEAT</div>
                                </motion.div>
                            ) : (
                                <div className="text-xs text-gray-600 font-medium">No Audio Track Selected</div>
                            )}
                        </AnimatePresence>
                    </div>
                </div>

                {/* --- Captions Overlay --- */}
                <div className="absolute bottom-8 left-1/2 -translate-x-1/2 z-30 w-full max-w-md text-center">
                    <AnimatePresence mode="wait">
                        {step === "intro" && (
                            <motion.div
                                key="intro"
                                initial={{ opacity: 0, y: 10 }}
                                animate={{ opacity: 1, y: 0 }}
                                exit={{ opacity: 0, y: -10 }}
                                className="bg-black/80 backdrop-blur-md text-white px-6 py-3 rounded-full border border-gray-700 shadow-2xl"
                            >
                                <span className="text-lg font-medium">Starting with <span className="text-red-400">raw footage</span>?</span>
                            </motion.div>
                        )}
                        {step === "scanning" && (
                            <motion.div
                                key="scan"
                                initial={{ opacity: 0, y: 10 }}
                                animate={{ opacity: 1, y: 0 }}
                                exit={{ opacity: 0, y: -10 }}
                                className="bg-black/80 backdrop-blur-md text-white px-6 py-3 rounded-full border border-gray-700 shadow-2xl"
                            >
                                <span className="text-lg font-medium">SPLICE automatically <span className="text-[#00D4FF]">detects silence</span>...</span>
                            </motion.div>
                        )}
                        {step === "cutting" && (
                            <motion.div
                                key="cut"
                                initial={{ opacity: 0, y: 10 }}
                                animate={{ opacity: 1, y: 0 }}
                                exit={{ opacity: 0, y: -10 }}
                                className="bg-black/80 backdrop-blur-md text-white px-6 py-3 rounded-full border border-gray-700 shadow-2xl"
                            >
                                <span className="text-lg font-medium">And cuts it <span className="text-[#00D4FF]">instantly!</span> ⚡️</span>
                            </motion.div>
                        )}
                        {step === "music" && (
                            <motion.div
                                key="music"
                                initial={{ opacity: 0, y: 10 }}
                                animate={{ opacity: 1, y: 0 }}
                                exit={{ opacity: 0, y: -10 }}
                                className="bg-black/80 backdrop-blur-md text-white px-6 py-3 rounded-full border border-gray-700 shadow-2xl"
                            >
                                <span className="text-lg font-medium">Need vibe? Generate a <span className="text-purple-400">unique track</span>.</span>
                            </motion.div>
                        )}
                    </AnimatePresence>
                </div>

                {/* --- Simulated Cursor --- */}
                <motion.div
                    className="absolute z-50 pointer-events-none drop-shadow-2xl"
                    initial={{ x: "50%", y: "50%" }}
                    animate={
                        step === "intro" ? { x: "50%", y: "50%" } :
                            step === "scanning" ? { x: "12%", y: "15%" } : // Sidebar position (approx)
                                step === "cutting" ? { x: "12%", y: "15%" } :
                                    step === "music" ? { x: "12%", y: "30%" } : // Music button position
                                        { x: "90%", y: "90%" } // Move away
                    }
                    transition={{ type: "spring", stiffness: 100, damping: 20 }}
                >
                    <MousePointer2 className="w-8 h-8 text-white fill-black" />

                    {/* Click Ripple */}
                    {(step === "scanning" || step === "music") && (
                        <motion.div
                            initial={{ scale: 0, opacity: 1 }}
                            animate={{ scale: 2, opacity: 0 }}
                            className="absolute -top-2 -left-2 w-12 h-12 bg-white/50 rounded-full"
                        />
                    )}
                </motion.div>

            </div>

            {/* --- Final CTA Overlay (Step = Done) --- */}
            <AnimatePresence>
                {step === "done" && (
                    <motion.div
                        initial={{ opacity: 0 }}
                        animate={{ opacity: 1 }}
                        className="absolute inset-0 z-40 bg-black/60 backdrop-blur-sm flex items-center justify-center"
                    >
                        <div className="text-center p-8">
                            <motion.div
                                initial={{ scale: 0.8, opacity: 0 }}
                                animate={{ scale: 1, opacity: 1 }}
                                transition={{ delay: 0.2 }}
                                className="mb-6 inline-block p-4 rounded-full bg-[#00D4FF]/20 text-[#00D4FF] border border-[#00D4FF]/50"
                            >
                                <Zap className="w-12 h-12" />
                            </motion.div>
                            <h3 className="text-4xl font-bold text-white mb-4">Ready to speed up?</h3>
                            <p className="text-gray-300 text-lg mb-8">This entire edit took 4 seconds.</p>

                            <a
                                href="/checkout?plan=pro"
                                className="inline-flex items-center justify-center h-14 px-8 rounded-full bg-[#00D4FF] hover:bg-[#00B8E6] text-black font-bold text-lg transition-all hover:scale-105 shadow-[0_0_30px_rgba(0,212,255,0.4)]"
                            >
                                Start Free Trial
                            </a>

                            <button
                                onClick={() => setStep("intro")}
                                className="block mx-auto mt-6 text-sm text-gray-500 hover:text-white transition-colors"
                            >
                                Replay Demo
                            </button>
                        </div>
                    </motion.div>
                )}
            </AnimatePresence>
        </div>
    );
}

