"use client";

import { motion } from "framer-motion";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import {
  ArrowLeft,
  ArrowRight,
  Check,
  Download,
  Zap,
  Play,
  Settings,
  Music,
} from "lucide-react";
import Link from "next/link";
import { Header } from "@/components/landing/Header";
import { Footer } from "@/components/landing/Footer";

const steps = [
  {
    number: 1,
    title: "Download SPLICE",
    description: "Download the UXP or CEP version based on your Premiere Pro version.",
    details: [
      "UXP version: Premiere Pro 25.6+ (Modern)",
      "CEP version: Premiere Pro 14.0-25.9 (Recommended)",
      "Both versions have identical features",
    ],
    action: { label: "Download Now", href: "/download" },
  },
  {
    number: 2,
    title: "Install the Plugin",
    description: "Follow the installation instructions for your operating system.",
    details: [
      "UXP: Use Adobe's UXP Developer Tool to load the plugin",
      "CEP: Copy to Extensions folder and enable debug mode",
      "Restart Premiere Pro after installation",
    ],
    action: { label: "Installation Guide", href: "/docs/installation" },
  },
  {
    number: 3,
    title: "Activate Your License",
    description: "Enter your license key to unlock all features.",
    details: [
      "Open SPLICE panel in Window > Extensions",
      "Click the settings icon and enter your license key",
      "Your license activates instantly",
    ],
    action: { label: "Get a License", href: "/pricing" },
  },
  {
    number: 4,
    title: "Start Editing!",
    description: "Open a sequence and click GO to analyze your footage.",
    details: [
      "SPLICE automatically detects silences and takes",
      "Review the analysis in the preview panel",
      "Click Build Sequence to create your edited timeline",
    ],
    action: null,
  },
];

const quickTips = [
  {
    icon: Zap,
    title: "Use Presets",
    description: "Choose from Podcast, Interview, Vlog, or Tutorial presets for optimized settings.",
  },
  {
    icon: Music,
    title: "AI Music",
    description: "Generate custom background music that matches your video's mood and energy.",
  },
  {
    icon: Settings,
    title: "Fine-tune Settings",
    description: "Adjust sensitivity and minimum silence duration for perfect results.",
  },
];

export default function QuickStartPage() {
  return (
    <div className="min-h-screen bg-[#060b14] dark relative overflow-hidden">
      {/* Background decoration */}
      <div className="absolute inset-0 grid-pattern opacity-30 pointer-events-none" />

      <Header />
      <main className="pt-24 pb-16 relative z-10">
        <div className="container mx-auto px-4">
