#!/usr/bin/env node
/**
 * SPLICE Backend Stress Test Suite
 *
 * Comprehensive stress testing for production readiness:
 * - All 43+ API endpoints
 * - Concurrent request simulation (10-50 users)
 * - Rate limiting verification (100 req/min per IP)
 * - Error handling robustness
 * - Authentication flows
 * - Webhook signature verification
 * - Response time measurements (p50, p95, p99)
 */

const https = require('https');
const http = require('http');
const crypto = require('crypto');

// =============================================================================
// Configuration
// =============================================================================

const CONFIG = {
  // Target can be production or local
  baseUrl: process.env.STRESS_TEST_URL || 'https://splice-api-production.up.railway.app',

  // Concurrency settings
  concurrentUsers: parseInt(process.env.CONCURRENT_USERS) || 10,
  maxConcurrentUsers: 50,

  // Rate limit testing
  rateLimitTestRequests: 120, // Test 120 requests to exceed 100/min limit

  // Timeouts
  requestTimeout: 30000, // 30 seconds

  // Test data
  testCustomerId: 'cus_test_stress_12345678',
  testLicenseKey: 'SPLICE-TEST-0000-0000',

  // Webhook secret for signature testing
  webhookSecret: 'whsec_test_secret',
};

// Determine if targeting local or production
const isLocalTest = CONFIG.baseUrl.includes('127.0.0.1') || CONFIG.baseUrl.includes('localhost');

// =============================================================================
// HTTP Client with Metrics
// =============================================================================

class MetricsCollector {
  constructor() {
    this.responseTimes = {};
    this.statusCodes = {};
    this.errors = {};
    this.totalRequests = 0;
    this.startTime = Date.now();
  }

  record(endpoint, statusCode, responseTimeMs, error = null) {
    this.totalRequests++;

    // Response times per endpoint
    if (!this.responseTimes[endpoint]) {
      this.responseTimes[endpoint] = [];
    }
    this.responseTimes[endpoint].push(responseTimeMs);

    // Status codes per endpoint
    const key = `${endpoint}:${statusCode}`;
    this.statusCodes[key] = (this.statusCodes[key] || 0) + 1;

    // Errors
    if (error) {
      if (!this.errors[endpoint]) {
        this.errors[endpoint] = [];
      }
      this.errors[endpoint].push({ statusCode, error, time: Date.now() });
    }
  }

  getPercentile(arr, p) {
    if (arr.length === 0) return 0;
    const sorted = [...arr].sort((a, b) => a - b);
    const idx = Math.ceil((p / 100) * sorted.length) - 1;
    return sorted[Math.max(0, idx)];
  }

  getStats(endpoint) {
    const times = this.responseTimes[endpoint] || [];
    if (times.length === 0) {
      return { p50: 0, p95: 0, p99: 0, min: 0, max: 0, avg: 0, count: 0 };
    }

    return {
      p50: this.getPercentile(times, 50),
      p95: this.getPercentile(times, 95),
      p99: this.getPercentile(times, 99),
      min: Math.min(...times),
      max: Math.max(...times),
      avg: Math.round(times.reduce((a, b) => a + b, 0) / times.length),
      count: times.length
    };
  }

  getOverallStats() {
    const allTimes = Object.values(this.responseTimes).flat();
    return {
      totalRequests: this.totalRequests,
      duration: Date.now() - this.startTime,
      rps: this.totalRequests / ((Date.now() - this.startTime) / 1000),
      p50: this.getPercentile(allTimes, 50),
      p95: this.getPercentile(allTimes, 95),
      p99: this.getPercentile(allTimes, 99),
    };
  }

  getErrorCount() {
    return Object.values(this.errors).reduce((sum, arr) => sum + arr.length, 0);
  }
}

const metrics = new MetricsCollector();

// =============================================================================
// HTTP Request Helper
// =============================================================================

function makeRequest(method, path, body = null, headers = {}) {
  return new Promise((resolve) => {
    const startTime = Date.now();
    const url = new URL(path, CONFIG.baseUrl);
    const isHttps = url.protocol === 'https:';
    const transport = isHttps ? https : http;

    const options = {
      hostname: url.hostname,
      port: url.port || (isHttps ? 443 : 80),
      path: url.pathname + url.search,
      method: method,
      headers: {
        'Content-Type': 'application/json',
        'User-Agent': 'SPLICE-StressTest/1.0',
        ...headers
      },
      timeout: CONFIG.requestTimeout,
      rejectUnauthorized: false, // Allow self-signed certs for local testing
    };

    const req = transport.request(options, (res) => {
      let data = '';
      res.on('data', chunk => data += chunk);
      res.on('end', () => {
        const responseTime = Date.now() - startTime;
        let parsedBody = null;
        try {
          parsedBody = JSON.parse(data);
        } catch {
          parsedBody = data;
        }

        metrics.record(
          path,
          res.statusCode,
          responseTime,
          res.statusCode >= 400 ? parsedBody : null
        );

        resolve({
          statusCode: res.statusCode,
          headers: res.headers,
          body: parsedBody,
          responseTime
        });
      });
    });

    req.on('error', (err) => {
      const responseTime = Date.now() - startTime;
      metrics.record(path, 0, responseTime, err.message);
      resolve({
        statusCode: 0,
        error: err.message,
        responseTime
      });
    });

    req.on('timeout', () => {
      req.destroy();
      const responseTime = Date.now() - startTime;
      metrics.record(path, 408, responseTime, 'Request timeout');
      resolve({
        statusCode: 408,
        error: 'Request timeout',
        responseTime
      });
    });

    if (body) {
      req.write(JSON.stringify(body));
    }

    req.end();
  });
}

