async function runEndpointTests() {
  console.log('\n========================================');
  console.log('ENDPOINT TESTS');
  console.log('========================================\n');

  const results = { passed: 0, failed: 0, tests: [] };

  for (const [category, tests] of Object.entries(ENDPOINT_TESTS)) {
    console.log(`\n--- ${category.toUpperCase()} ---`);

    for (const test of tests) {
      const res = await makeRequest(
        test.method,
        test.path,
        test.body || null,
        test.headers || {}
      );

      const expectedStatuses = Array.isArray(test.expectedStatus)
        ? test.expectedStatus
        : [test.expectedStatus];

      const passed = expectedStatuses.includes(res.statusCode);

      if (passed) {
        results.passed++;
        console.log(`  [PASS] ${test.name} (${res.statusCode}) - ${res.responseTime}ms`);
      } else {
        results.failed++;
        console.log(`  [FAIL] ${test.name}: expected ${test.expectedStatus}, got ${res.statusCode}`);
        if (res.body?.error) {
          console.log(`         Error: ${res.body.error}`);
        }
      }

      results.tests.push({
        name: test.name,
        passed,
        statusCode: res.statusCode,
        expectedStatus: test.expectedStatus,
        responseTime: res.responseTime
      });
    }
  }

  return results;
}

async function runConcurrencyTest() {
  console.log('\n========================================');
  console.log(`CONCURRENCY TEST (${CONFIG.concurrentUsers} users)`);
  console.log('========================================\n');

  const results = { passed: 0, failed: 0, responseTimeSum: 0 };
  const endpoint = '/health';

  // Create concurrent requests
  const requests = [];
  for (let i = 0; i < CONFIG.concurrentUsers; i++) {
    requests.push(makeRequest('GET', endpoint));
  }

  console.log(`Sending ${CONFIG.concurrentUsers} concurrent requests to ${endpoint}...`);
  const startTime = Date.now();

  const responses = await Promise.all(requests);

  const totalTime = Date.now() - startTime;
  console.log(`Completed in ${totalTime}ms\n`);

  for (const res of responses) {
    if (res.statusCode === 200 || res.statusCode === 503) {
      results.passed++;
    } else {
      results.failed++;
    }
    results.responseTimeSum += res.responseTime;
  }

  const avgResponseTime = Math.round(results.responseTimeSum / responses.length);

  console.log(`Results:`);
  console.log(`  Passed: ${results.passed}/${CONFIG.concurrentUsers}`);
  console.log(`  Failed: ${results.failed}/${CONFIG.concurrentUsers}`);
  console.log(`  Avg Response Time: ${avgResponseTime}ms`);
  console.log(`  Throughput: ${(CONFIG.concurrentUsers / (totalTime / 1000)).toFixed(2)} req/s`);

  return {
    ...results,
    avgResponseTime,
    throughput: CONFIG.concurrentUsers / (totalTime / 1000)
  };
}

async function runRateLimitTest() {
  console.log('\n========================================');
  console.log('RATE LIMIT TEST (100 req/min per IP)');
  console.log('========================================\n');

  const results = {
    totalRequests: 0,
    successfulRequests: 0,
    rateLimited: 0,
    errors: 0
  };

  const endpoint = '/health';
  const targetRequests = CONFIG.rateLimitTestRequests;

  console.log(`Sending ${targetRequests} rapid requests to ${endpoint}...`);
  console.log('(Rate limit should trigger after ~100 requests)\n');

  const startTime = Date.now();

  // Send requests as fast as possible
  for (let i = 0; i < targetRequests; i++) {
    const res = await makeRequest('GET', endpoint);
    results.totalRequests++;

    if (res.statusCode === 429) {
      results.rateLimited++;
      if (results.rateLimited === 1) {
        console.log(`  Rate limit triggered at request #${i + 1}`);
      }
    } else if (res.statusCode === 200 || res.statusCode === 503) {
      results.successfulRequests++;
    } else {
      results.errors++;
    }

    // Progress indicator every 20 requests
    if ((i + 1) % 20 === 0) {
      console.log(`  Progress: ${i + 1}/${targetRequests} requests`);
    }
  }

  const totalTime = Date.now() - startTime;

  console.log(`\nResults:`);
  console.log(`  Total Time: ${totalTime}ms`);
  console.log(`  Successful: ${results.successfulRequests}`);
  console.log(`  Rate Limited (429): ${results.rateLimited}`);
  console.log(`  Errors: ${results.errors}`);
  console.log(`  Rate Limit Working: ${results.rateLimited > 0 ? 'YES' : 'NO'}`);

  return results;
}

