    console.log(`  [FAIL] Expected 400, got ${res4.statusCode}`);
  }

  // Test 5: Refresh with invalid token
  console.log('Test 5: Refresh with invalid token');
  const res5 = await makeRequest('POST', '/auth/refresh', { refreshToken: 'invalid.token.here' });
  if (res5.statusCode === 401) {
    results.passed++;
    console.log('  [PASS] Rejected invalid refresh token');
  } else {
    results.failed++;
    console.log(`  [FAIL] Expected 401, got ${res5.statusCode}`);
  }

  // Test 6: Protected endpoint without auth
  console.log('Test 6: Protected endpoint without auth');
  const res6 = await makeRequest('GET', '/credits');
  if (res6.statusCode === 401) {
    results.passed++;
    console.log('  [PASS] Rejected unauthenticated request');
  } else {
    results.failed++;
    console.log(`  [FAIL] Expected 401, got ${res6.statusCode}`);
  }

  // Test 7: Protected endpoint with invalid bearer token
  console.log('Test 7: Protected endpoint with invalid bearer token');
  const res7 = await makeRequest('GET', '/credits', null, {
    'Authorization': 'Bearer invalid.jwt.token'
  });
  if (res7.statusCode === 401) {
    results.passed++;
    console.log('  [PASS] Rejected invalid bearer token');
  } else {
    results.failed++;
    console.log(`  [FAIL] Expected 401, got ${res7.statusCode}`);
  }

  // Test 8: Legacy header in production (should be blocked)
  console.log('Test 8: Legacy x-stripe-customer-id header');
  const res8 = await makeRequest('GET', '/credits', null, {
    'x-stripe-customer-id': 'cus_test12345678901234'
  });
  // In production, legacy auth is blocked (401). In dev, it validates customer existence
  if (res8.statusCode === 401) {
    results.passed++;
    console.log('  [PASS] Legacy auth blocked or rejected (production behavior)');
  } else {
    results.failed++;
    console.log(`  [WARN] Legacy auth returned ${res8.statusCode} (may be allowed in dev)`);
    results.passed++; // Don't fail for dev environments
  }

  console.log(`\nResults: ${results.passed}/${results.passed + results.failed} passed`);
  return results;
}

async function runHighLoadTest() {
  console.log('\n========================================');
  console.log(`HIGH LOAD TEST (${CONFIG.maxConcurrentUsers} concurrent users)`);
  console.log('========================================\n');

  const results = {
    waves: [],
    totalRequests: 0,
    successfulRequests: 0,
    failedRequests: 0
  };

  const endpoints = ['/health', '/', '/music/moods', '/profanity/languages'];
  const requestsPerWave = CONFIG.maxConcurrentUsers;
  const numberOfWaves = 3;

  for (let wave = 0; wave < numberOfWaves; wave++) {
    console.log(`\nWave ${wave + 1}/${numberOfWaves}:`);

    const requests = [];
    for (let i = 0; i < requestsPerWave; i++) {
      const endpoint = endpoints[i % endpoints.length];
      requests.push(makeRequest('GET', endpoint));
    }

    const startTime = Date.now();
    const responses = await Promise.all(requests);
    const waveTime = Date.now() - startTime;

    let waveSuccess = 0;
    let waveFailed = 0;

    for (const res of responses) {
      results.totalRequests++;
      if (res.statusCode >= 200 && res.statusCode < 500) {
        results.successfulRequests++;
        waveSuccess++;
      } else {
        results.failedRequests++;
        waveFailed++;
      }
    }

    console.log(`  Requests: ${requestsPerWave}`);
    console.log(`  Success: ${waveSuccess}, Failed: ${waveFailed}`);
    console.log(`  Time: ${waveTime}ms`);
    console.log(`  Throughput: ${(requestsPerWave / (waveTime / 1000)).toFixed(2)} req/s`);

    results.waves.push({
      wave: wave + 1,
      requests: requestsPerWave,
      success: waveSuccess,
      failed: waveFailed,
      time: waveTime,
      throughput: requestsPerWave / (waveTime / 1000)
    });

    // Brief pause between waves
    await new Promise(r => setTimeout(r, 1000));
  }

  console.log(`\nOverall Results:`);
  console.log(`  Total Requests: ${results.totalRequests}`);
  console.log(`  Successful: ${results.successfulRequests}`);
  console.log(`  Failed: ${results.failedRequests}`);
  console.log(`  Success Rate: ${((results.successfulRequests / results.totalRequests) * 100).toFixed(2)}%`);

  return results;
}

// =============================================================================
// Report Generator
// =============================================================================

function generateReport(testResults) {
  console.log('\n');
  console.log('================================================================================');
  console.log('                    SPLICE BACKEND STRESS TEST REPORT                          ');
  console.log('================================================================================');
  console.log(`Target: ${CONFIG.baseUrl}`);
  console.log(`Time: ${new Date().toISOString()}`);
  console.log('================================================================================\n');

  // Overall Summary
  const overall = metrics.getOverallStats();
  console.log('OVERALL METRICS');
  console.log('--------------------------------------------------------------------------------');
  console.log(`Total Requests:     ${overall.totalRequests}`);
  console.log(`Total Duration:     ${overall.duration}ms`);
  console.log(`Requests/Second:    ${overall.rps.toFixed(2)}`);
  console.log(`Response Time p50:  ${overall.p50}ms`);
  console.log(`Response Time p95:  ${overall.p95}ms`);
  console.log(`Response Time p99:  ${overall.p99}ms`);
  console.log(`Total Errors:       ${metrics.getErrorCount()}`);
  console.log('');

  // Test Results Summary
  console.log('TEST RESULTS SUMMARY');
  console.log('--------------------------------------------------------------------------------');

  let totalPassed = 0;
  let totalFailed = 0;

  if (testResults.endpoints) {
    totalPassed += testResults.endpoints.passed;
    totalFailed += testResults.endpoints.failed;
    console.log(`Endpoint Tests:     ${testResults.endpoints.passed} passed, ${testResults.endpoints.failed} failed`);
  }

  if (testResults.concurrency) {
    console.log(`Concurrency Test:   ${testResults.concurrency.passed}/${CONFIG.concurrentUsers} successful`);
  }

  if (testResults.rateLimit) {
    console.log(`Rate Limit Test:    ${testResults.rateLimit.rateLimited > 0 ? 'WORKING' : 'NOT TRIGGERED'}`);
  }

  if (testResults.webhook) {
    totalPassed += testResults.webhook.passed;
    totalFailed += testResults.webhook.failed;
    console.log(`Webhook Tests:      ${testResults.webhook.passed} passed, ${testResults.webhook.failed} failed`);
  }

  if (testResults.errorHandling) {
    totalPassed += testResults.errorHandling.passed;
    totalFailed += testResults.errorHandling.failed;
    console.log(`Error Handling:     ${testResults.errorHandling.passed} passed, ${testResults.errorHandling.failed} failed`);
  }

  if (testResults.auth) {
    totalPassed += testResults.auth.passed;
    totalFailed += testResults.auth.failed;
    console.log(`Authentication:     ${testResults.auth.passed} passed, ${testResults.auth.failed} failed`);
  }

  if (testResults.highLoad) {
    console.log(`High Load Test:     ${testResults.highLoad.successfulRequests}/${testResults.highLoad.totalRequests} successful`);
  }

  console.log('');
  console.log(`TOTAL:              ${totalPassed} passed, ${totalFailed} failed`);
  console.log('');

  // Response Time by Endpoint
  console.log('RESPONSE TIME PERCENTILES BY ENDPOINT');
  console.log('--------------------------------------------------------------------------------');
  console.log('Endpoint'.padEnd(40) + 'p50'.padEnd(10) + 'p95'.padEnd(10) + 'p99'.padEnd(10) + 'Count');
  console.log('-'.repeat(80));

  const endpoints = Object.keys(metrics.responseTimes).sort();
  for (const endpoint of endpoints) {
    const stats = metrics.getStats(endpoint);
    console.log(
      endpoint.substring(0, 39).padEnd(40) +
      `${stats.p50}ms`.padEnd(10) +
      `${stats.p95}ms`.padEnd(10) +
      `${stats.p99}ms`.padEnd(10) +
      stats.count
    );
  }

  // Status Code Distribution
  console.log('\nSTATUS CODE DISTRIBUTION');
  console.log('--------------------------------------------------------------------------------');
  const statusGroups = {};
  for (const [key, count] of Object.entries(metrics.statusCodes)) {
    const [endpoint, code] = key.split(':');
    if (!statusGroups[code]) statusGroups[code] = 0;
    statusGroups[code] += count;
  }
  for (const [code, count] of Object.entries(statusGroups).sort()) {
    const pct = ((count / overall.totalRequests) * 100).toFixed(1);
    console.log(`  ${code}: ${count} (${pct}%)`);
  }

  // Errors
  if (metrics.getErrorCount() > 0) {
    console.log('\nERRORS ENCOUNTERED');
    console.log('--------------------------------------------------------------------------------');
    for (const [endpoint, errors] of Object.entries(metrics.errors)) {
      console.log(`\n${endpoint}:`);
      const uniqueErrors = [...new Set(errors.map(e => e.error?.error || e.error || 'Unknown'))];
      for (const err of uniqueErrors.slice(0, 3)) {
        console.log(`  - ${err}`);
      }
    }
  }

  // Final Verdict
  console.log('\n================================================================================');
  const passRate = totalPassed / (totalPassed + totalFailed) * 100;
  const errorRate = (metrics.getErrorCount() / overall.totalRequests) * 100;

  let verdict = 'PASS';
  let verdictColor = '\x1b[32m'; // Green

  if (passRate < 80 || errorRate > 10) {
    verdict = 'FAIL';
    verdictColor = '\x1b[31m'; // Red
  } else if (passRate < 95 || errorRate > 5) {
    verdict = 'PASS WITH WARNINGS';
    verdictColor = '\x1b[33m'; // Yellow
  }

  console.log(`VERDICT: ${verdictColor}${verdict}\x1b[0m`);
  console.log(`Pass Rate: ${passRate.toFixed(1)}%`);
  console.log(`Error Rate: ${errorRate.toFixed(2)}%`);
  console.log('================================================================================\n');

  return {
    verdict,
    passRate,
    errorRate,
    totalPassed,
    totalFailed,
    overall
  };
}

// =============================================================================
// Main
// =============================================================================

async function main() {
  console.log('================================================================================');
  console.log('                    SPLICE BACKEND STRESS TEST SUITE                           ');
  console.log('================================================================================');
  console.log(`Target: ${CONFIG.baseUrl}`);
  console.log(`Concurrent Users: ${CONFIG.concurrentUsers}`);
  console.log(`Local Test: ${isLocalTest}`);
  console.log('================================================================================');

  const testResults = {};

  // Check if server is reachable
  console.log('\nChecking server connectivity...');
  const healthCheck = await makeRequest('GET', '/health');
  if (healthCheck.statusCode === 0) {
    console.log(`\n[ERROR] Cannot reach server at ${CONFIG.baseUrl}`);
    console.log(`Error: ${healthCheck.error}`);
    console.log('\nPlease ensure the server is running and accessible.');
    process.exit(1);
  }
  console.log(`Server responded with status ${healthCheck.statusCode}`);

  // Run all tests
  testResults.endpoints = await runEndpointTests();
  testResults.concurrency = await runConcurrencyTest();
  testResults.rateLimit = await runRateLimitTest();
  testResults.webhook = await runWebhookSignatureTest();
  testResults.errorHandling = await runErrorHandlingTest();
  testResults.auth = await runAuthenticationFlowTest();
  testResults.highLoad = await runHighLoadTest();

  // Generate report
  const report = generateReport(testResults);

  // Exit with appropriate code
  if (report.verdict === 'FAIL') {
    process.exit(1);
  }

  process.exit(0);
}

// Run if executed directly
main().catch(err => {
  console.error('Fatal error:', err);
  process.exit(1);
});

