async function runErrorHandlingTest() {
  console.log('\n========================================');
  console.log('ERROR HANDLING TEST');
  console.log('========================================\n');

  const results = { passed: 0, failed: 0 };

  const errorTests = [
    {
      name: 'Non-existent endpoint',
      method: 'GET',
      path: '/this/endpoint/does/not/exist',
      expectStatus: 404
    },
    {
      name: 'Wrong HTTP method',
      method: 'DELETE',
      path: '/health',
      expectStatus: [404, 405] // Express returns 404 for unhandled methods
    },
    {
      name: 'Extremely long path',
      method: 'GET',
      path: '/' + 'a'.repeat(5000),
      expectStatus: [400, 404, 414] // URI Too Long
    },
    {
      name: 'Unicode in path',
      method: 'GET',
      path: '/test/%E2%80%8B%E2%80%8B%E2%80%8B', // Zero-width spaces
      expectStatus: [400, 404]
    },
    {
      name: 'Empty POST body to analyze',
      method: 'POST',
      path: '/analyze',
      body: null,
      expectStatus: [400, 401]
    },
    {
      name: 'Null values in body',
      method: 'POST',
      path: '/license/activate',
      body: { key: null },
      expectStatus: 400
    },
    {
      name: 'Array instead of object',
      method: 'POST',
      path: '/license/activate',
      body: ['SPLICE-TEST-0000-0000'],
      expectStatus: 400
    },
    {
      name: 'Deeply nested object',
      method: 'POST',
      path: '/analyze',
      body: { a: { b: { c: { d: { e: { f: { g: { h: { wavPath: '/test.wav' } } } } } } } } },
      expectStatus: [400, 401]
    },
  ];

  for (const test of errorTests) {
    const res = await makeRequest(test.method, test.path, test.body);
    const expectedStatuses = Array.isArray(test.expectStatus)
      ? test.expectStatus
      : [test.expectStatus];

    const passed = expectedStatuses.includes(res.statusCode);

    if (passed) {
      results.passed++;
      console.log(`  [PASS] ${test.name}: ${res.statusCode}`);
    } else {
      results.failed++;
      console.log(`  [FAIL] ${test.name}: expected ${test.expectStatus}, got ${res.statusCode}`);
    }
  }

  console.log(`\nResults: ${results.passed}/${results.passed + results.failed} passed`);
  return results;
}

