async function runHighLoadTest() {
  console.log('\n========================================');
  console.log(`HIGH LOAD TEST (${CONFIG.maxConcurrentUsers} concurrent users)`);
  console.log('========================================\n');

  const results = {
    waves: [],
    totalRequests: 0,
    successfulRequests: 0,
    failedRequests: 0
  };

  const endpoints = ['/health', '/', '/music/moods', '/profanity/languages'];
  const requestsPerWave = CONFIG.maxConcurrentUsers;
  const numberOfWaves = 3;

  for (let wave = 0; wave < numberOfWaves; wave++) {
    console.log(`\nWave ${wave + 1}/${numberOfWaves}:`);

    const requests = [];
    for (let i = 0; i < requestsPerWave; i++) {
      const endpoint = endpoints[i % endpoints.length];
      requests.push(makeRequest('GET', endpoint));
    }

    const startTime = Date.now();
    const responses = await Promise.all(requests);
    const waveTime = Date.now() - startTime;

    let waveSuccess = 0;
    let waveFailed = 0;

    for (const res of responses) {
      results.totalRequests++;
      if (res.statusCode >= 200 && res.statusCode < 500) {
        results.successfulRequests++;
        waveSuccess++;
      } else {
        results.failedRequests++;
        waveFailed++;
      }
    }

    console.log(`  Requests: ${requestsPerWave}`);
    console.log(`  Success: ${waveSuccess}, Failed: ${waveFailed}`);
    console.log(`  Time: ${waveTime}ms`);
    console.log(`  Throughput: ${(requestsPerWave / (waveTime / 1000)).toFixed(2)} req/s`);

    results.waves.push({
      wave: wave + 1,
      requests: requestsPerWave,
      success: waveSuccess,
      failed: waveFailed,
      time: waveTime,
      throughput: requestsPerWave / (waveTime / 1000)
    });

    // Brief pause between waves
    await new Promise(r => setTimeout(r, 1000));
  }

  console.log(`\nOverall Results:`);
  console.log(`  Total Requests: ${results.totalRequests}`);
  console.log(`  Successful: ${results.successfulRequests}`);
  console.log(`  Failed: ${results.failedRequests}`);
  console.log(`  Success Rate: ${((results.successfulRequests / results.totalRequests) * 100).toFixed(2)}%`);

  return results;
}

