function generateReport(testResults) {
  console.log('\n');
  console.log('================================================================================');
  console.log('                    SPLICE BACKEND STRESS TEST REPORT                          ');
  console.log('================================================================================');
  console.log(`Target: ${CONFIG.baseUrl}`);
  console.log(`Time: ${new Date().toISOString()}`);
  console.log('================================================================================\n');

  // Overall Summary
  const overall = metrics.getOverallStats();
  console.log('OVERALL METRICS');
  console.log('--------------------------------------------------------------------------------');
  console.log(`Total Requests:     ${overall.totalRequests}`);
  console.log(`Total Duration:     ${overall.duration}ms`);
  console.log(`Requests/Second:    ${overall.rps.toFixed(2)}`);
  console.log(`Response Time p50:  ${overall.p50}ms`);
  console.log(`Response Time p95:  ${overall.p95}ms`);
  console.log(`Response Time p99:  ${overall.p99}ms`);
  console.log(`Total Errors:       ${metrics.getErrorCount()}`);
  console.log('');

  // Test Results Summary
  console.log('TEST RESULTS SUMMARY');
  console.log('--------------------------------------------------------------------------------');

  let totalPassed = 0;
  let totalFailed = 0;

  if (testResults.endpoints) {
    totalPassed += testResults.endpoints.passed;
    totalFailed += testResults.endpoints.failed;
    console.log(`Endpoint Tests:     ${testResults.endpoints.passed} passed, ${testResults.endpoints.failed} failed`);
  }

  if (testResults.concurrency) {
    console.log(`Concurrency Test:   ${testResults.concurrency.passed}/${CONFIG.concurrentUsers} successful`);
  }

  if (testResults.rateLimit) {
    console.log(`Rate Limit Test:    ${testResults.rateLimit.rateLimited > 0 ? 'WORKING' : 'NOT TRIGGERED'}`);
  }

  if (testResults.webhook) {
    totalPassed += testResults.webhook.passed;
    totalFailed += testResults.webhook.failed;
    console.log(`Webhook Tests:      ${testResults.webhook.passed} passed, ${testResults.webhook.failed} failed`);
  }

  if (testResults.errorHandling) {
    totalPassed += testResults.errorHandling.passed;
    totalFailed += testResults.errorHandling.failed;
    console.log(`Error Handling:     ${testResults.errorHandling.passed} passed, ${testResults.errorHandling.failed} failed`);
  }

  if (testResults.auth) {
    totalPassed += testResults.auth.passed;
    totalFailed += testResults.auth.failed;
    console.log(`Authentication:     ${testResults.auth.passed} passed, ${testResults.auth.failed} failed`);
  }

  if (testResults.highLoad) {
    console.log(`High Load Test:     ${testResults.highLoad.successfulRequests}/${testResults.highLoad.totalRequests} successful`);
  }

  console.log('');
  console.log(`TOTAL:              ${totalPassed} passed, ${totalFailed} failed`);
  console.log('');

  // Response Time by Endpoint
  console.log('RESPONSE TIME PERCENTILES BY ENDPOINT');
  console.log('--------------------------------------------------------------------------------');
  console.log('Endpoint'.padEnd(40) + 'p50'.padEnd(10) + 'p95'.padEnd(10) + 'p99'.padEnd(10) + 'Count');
  console.log('-'.repeat(80));

  const endpoints = Object.keys(metrics.responseTimes).sort();
  for (const endpoint of endpoints) {
    const stats = metrics.getStats(endpoint);
    console.log(
      endpoint.substring(0, 39).padEnd(40) +
      `${stats.p50}ms`.padEnd(10) +
      `${stats.p95}ms`.padEnd(10) +
      `${stats.p99}ms`.padEnd(10) +
      stats.count
    );
  }

  // Status Code Distribution
  console.log('\nSTATUS CODE DISTRIBUTION');
  console.log('--------------------------------------------------------------------------------');
  const statusGroups = {};
  for (const [key, count] of Object.entries(metrics.statusCodes)) {
    const [endpoint, code] = key.split(':');
    if (!statusGroups[code]) statusGroups[code] = 0;
    statusGroups[code] += count;
  }
  for (const [code, count] of Object.entries(statusGroups).sort()) {
    const pct = ((count / overall.totalRequests) * 100).toFixed(1);
    console.log(`  ${code}: ${count} (${pct}%)`);
  }

  // Errors
  if (metrics.getErrorCount() > 0) {
    console.log('\nERRORS ENCOUNTERED');
    console.log('--------------------------------------------------------------------------------');
    for (const [endpoint, errors] of Object.entries(metrics.errors)) {
      console.log(`\n${endpoint}:`);
      const uniqueErrors = [...new Set(errors.map(e => e.error?.error || e.error || 'Unknown'))];
      for (const err of uniqueErrors.slice(0, 3)) {
        console.log(`  - ${err}`);
      }
    }
  }

  // Final Verdict
  console.log('\n================================================================================');
  const passRate = totalPassed / (totalPassed + totalFailed) * 100;
  const errorRate = (metrics.getErrorCount() / overall.totalRequests) * 100;

  let verdict = 'PASS';
  let verdictColor = '\x1b[32m'; // Green

  if (passRate < 80 || errorRate > 10) {
    verdict = 'FAIL';
    verdictColor = '\x1b[31m'; // Red
  } else if (passRate < 95 || errorRate > 5) {
    verdict = 'PASS WITH WARNINGS';
    verdictColor = '\x1b[33m'; // Yellow
  }

  console.log(`VERDICT: ${verdictColor}${verdict}\x1b[0m`);
  console.log(`Pass Rate: ${passRate.toFixed(1)}%`);
  console.log(`Error Rate: ${errorRate.toFixed(2)}%`);
  console.log('================================================================================\n');

  return {
    verdict,
    passRate,
    errorRate,
    totalPassed,
    totalFailed,
    overall
  };
}

