  testResults.concurrency = await runConcurrencyTest();
  testResults.rateLimit = await runRateLimitTest();
  testResults.webhook = await runWebhookSignatureTest();
  testResults.errorHandling = await runErrorHandlingTest();
  testResults.auth = await runAuthenticationFlowTest();
  testResults.highLoad = await runHighLoadTest();

  // Generate report
  const report = generateReport(testResults);

  // Exit with appropriate code
  if (report.verdict === 'FAIL') {
    process.exit(1);
  }

  process.exit(0);
}

// Run if executed directly
main().catch(err => {
  console.error('Fatal error:', err);
  process.exit(1);
});

