     1→/**
     2→ * Music Generation Service
     3→ * Generates AI music using Mureka API
     4→ *
     5→ * Circuit Breaker Protection:
     6→ * - All Mureka API calls wrapped with circuit breaker
     7→ * - Audio downloads protected separately
     8→ * - Failures save request to dead letter queue (future enhancement)
     9→ * - Returns clear error messages when circuit is OPEN
    10→ */
    11→
    12→const https = require('https');
    13→const { execFile } = require('child_process');
    14→const { promisify } = require('util');
    15→const fs = require('fs').promises;
    16→const path = require('path');
    17→const sceneAnalysis = require('./sceneAnalysis');
    18→const { circuitBreakers } = require('../config/circuitBreakers');
    19→
    20→// SECURITY: Using execFile with array arguments prevents command injection
    21→const execFileAsync = promisify(execFile);
    22→
    23→// Mureka API configuration
    24→const MUREKA_API_KEY = process.env.MUREKA_API_KEY;
    25→const MUREKA_BASE_URL = 'api.mureka.ai';
    26→const MUREKA_API_VERSION = 'v1';
    27→
    28→// Generation settings
    29→const DEFAULT_DURATION = 60;
    30→const MIN_DURATION = 30;
    31→const MAX_DURATION = 180;
    32→const POLL_INTERVAL = 5000; // 5 seconds
    33→const MAX_POLL_ATTEMPTS = 120; // 10 minutes max by attempt count
    34→const MAX_TOTAL_TIME_MS = 10 * 60 * 1000; // 10 minutes hard limit by wall clock
    35→
    36→// Mood presets with Mureka-compatible descriptions
    37→const MOOD_PRESETS = {
    38→  energetic: 'upbeat, high energy, driving rhythm, motivational',
    39→  relaxed: 'calm, peaceful, ambient, soothing',
    40→  melancholic: 'sad, emotional, introspective, minor key',
    41→  intense: 'powerful, dramatic, cinematic, building tension',
    42→  happy: 'joyful, cheerful, bright, uplifting',
    43→  mysterious: 'dark, suspenseful, atmospheric, enigmatic',
    44→  romantic: 'warm, intimate, gentle, loving',
    45→  epic: 'grand, orchestral, sweeping, heroic',
    46→  chill: 'lo-fi, laid-back, smooth, easy-going',
    47→  neutral: 'balanced, versatile, general purpose'
    48→};
    49→
    50→// Instrument categories
    51→const INSTRUMENT_PRESETS = {
    52→  acoustic: ['acoustic guitar', 'piano', 'strings', 'woodwinds'],
    53→  electronic: ['synthesizer', 'drum machine', 'bass', 'pads'],
    54→  rock: ['electric guitar', 'drums', 'bass guitar', 'distortion'],
    55→  orchestral: ['strings', 'brass', 'woodwinds', 'percussion', 'harp'],
    56→  minimal: ['piano', 'ambient pads'],
    57→  hiphop: ['808 bass', 'hi-hats', 'trap drums', 'synth']
    58→};
    59→
    60→/**
    61→ * Check if Mureka API key is configured
    62→ * @returns {boolean}
    63→ */
    64→function hasMurekaCredentials() {
    65→  return !!MUREKA_API_KEY;
    66→}
    67→
    68→/**
    69→ * Build prompt for Mureka API from generation options
    70→ * @param {Object} options - Generation options
    71→ * @returns {string} Formatted prompt
    72→ */
    73→function buildMurekaPrompt(options) {
    74→  const parts = [];
    75→
    76→  // Add custom prompt if provided
    77→  if (options.prompt) {
    78→    parts.push(options.prompt);
    79→  }
    80→
    81→  // Add reference song style if available
    82→  if (options.referenceSong && options.referenceSong.identified) {
    83→    parts.push(`Style inspired by "${options.referenceSong.title}" by ${options.referenceSong.artist}`);
    84→
    85→    if (options.referenceSong.bpm) {
    86→      parts.push(`Tempo around ${options.referenceSong.bpm} BPM`);
    87→    }
    88→
    89→    if (options.referenceSong.key) {
    90→      parts.push(`Key: ${options.referenceSong.key}`);
    91→    }
    92→
    93→    if (options.referenceSong.genres?.length > 0) {
    94→      parts.push(`Genre: ${options.referenceSong.genres.join(', ')}`);
    95→    }
    96→  }
    97→
    98→  // Add mood description
    99→  if (options.mood) {
   100→    const moodDescription = MOOD_PRESETS[options.mood] || options.mood;
   101→    parts.push(`Mood: ${moodDescription}`);
   102→  }
   103→
   104→  // Add instruments
   105→  if (options.instruments?.length > 0) {
   106→    const instruments = options.instruments.map(i => {
   107→      // Check if it's a preset or individual instrument
   108→      if (INSTRUMENT_PRESETS[i]) {
   109→        return INSTRUMENT_PRESETS[i].join(', ');
   110→      }
   111→      return i;
   112→    }).join(', ');
   113→    parts.push(`Instruments: ${instruments}`);
   114→  }
   115→
   116→  // Add duration
   117→  const duration = clampDuration(options.duration || DEFAULT_DURATION);
   118→  parts.push(`Duration: approximately ${duration} seconds`);
   119→
   120→  // Add quality markers
   121→  parts.push('Instrumental only, no vocals');
   122→  parts.push('Suitable as background music for video');
   123→  parts.push('Professional quality, well-mixed');
   124→
   125→  return parts.join('. ');
   126→}
   127→
   128→/**
   129→ * Clamp duration to valid range
   130→ * @param {number} duration - Duration in seconds
   131→ * @returns {number} Clamped duration
   132→ */
   133→function clampDuration(duration) {
   134→  return Math.min(Math.max(duration, MIN_DURATION), MAX_DURATION);
   135→}
   136→
   137→/**
   138→ * Make HTTP request to Mureka API
   139→ * @param {string} method - HTTP method
   140→ * @param {string} endpoint - API endpoint
   141→ * @param {Object} data - Request body (for POST)
   142→ * @returns {Promise<Object>} API response
   143→ */
   144→async function murekaRequest(method, endpoint, data = null) {
   145→  if (!MUREKA_API_KEY) {
   146→    throw new Error('Mureka API key not configured. Set MUREKA_API_KEY environment variable.');
   147→  }
   148→
   149→  return new Promise((resolve, reject) => {
   150→    const options = {
   151→      hostname: MUREKA_BASE_URL,
   152→      path: `/${MUREKA_API_VERSION}${endpoint}`,
   153→      method,
   154→      headers: {
   155→        'Authorization': `Bearer ${MUREKA_API_KEY}`,
   156→        'Content-Type': 'application/json',
   157→        'Accept': 'application/json'
   158→      }
   159→    };
   160→
   161→    const req = https.request(options, (res) => {
   162→      let responseData = '';
   163→
   164→      res.on('data', (chunk) => {
   165→        responseData += chunk;
   166→      });
   167→
   168→      res.on('end', () => {
   169→        try {
   170→          const parsed = JSON.parse(responseData);
   171→
   172→          if (res.statusCode >= 400) {
   173→            const error = new Error(parsed.message || parsed.error || 'Mureka API error');
   174→            error.statusCode = res.statusCode;
   175→            error.response = parsed;
   176→            reject(error);
   177→            return;
   178→          }
   179→
   180→          resolve(parsed);
   181→        } catch (parseError) {
   182→          reject(new Error(`Failed to parse Mureka response: ${parseError.message}`));
   183→        }
   184→      });
   185→    });
   186→
   187→    req.on('error', (error) => {
   188→      reject(new Error(`Mureka API request failed: ${error.message}`));
   189→    });
   190→
   191→    req.on('timeout', () => {
   192→      req.destroy();
   193→      reject(new Error('Mureka API request timeout'));
   194→    });
   195→
   196→    req.setTimeout(60000); // 60 second timeout
   197→
   198→    if (data) {
   199→      req.write(JSON.stringify(data));
   200→    }
   201→
   202→    req.end();
   203→  });
   204→}
   205→
   206→/**
   207→ * Submit music generation request to Mureka
   208→ * @param {Object} options - Generation options
   209→ * @returns {Promise<{taskId: string, status: string}>}
   210→ */
   211→async function submitGeneration(options) {
   212→  const prompt = buildMurekaPrompt(options);
   213→  const duration = clampDuration(options.duration || DEFAULT_DURATION);
   214→
   215→  const requestBody = {
   216→    prompt,
   217→    duration,
   218→    type: 'instrumental', // Background music mode
   219→    format: 'wav',
   220→    quality: 'high'
   221→  };
   222→
   223→  // Add reference track if available
   224→  if (options.referenceTrackUrl) {
   225→    requestBody.reference_url = options.referenceTrackUrl;
   226→  }
   227→
   228→  const response = await murekaRequest('POST', '/generate', requestBody);
   229→
   230→  if (!response.task_id) {
   231→    throw new Error('Mureka did not return a task ID');
   232→  }
   233→
   234→  return {
   235→    taskId: response.task_id,
   236→    status: response.status || 'pending',
   237→    estimatedTime: response.estimated_time || '3-5 minutes'
   238→  };
   239→}
   240→
   241→/**
   242→ * Check status of a generation task
   243→ * @param {string} taskId - Mureka task ID
   244→ * @returns {Promise<Object>} Task status
   245→ */
   246→async function checkTaskStatus(taskId) {
   247→  const response = await murekaRequest('GET', `/tasks/${taskId}`);
   248→
   249→  return {
   250→    taskId,
   251→    status: response.status,
   252→    progress: response.progress || 0,
   253→    audioUrl: response.audio_url || null,
   254→    previewUrl: response.preview_url || null,
   255→    duration: response.duration || null,
   256→    error: response.error || null
   257→  };
   258→}
   259→
   260→/**
   261→ * Poll for task completion
   262→ * @param {string} taskId - Mureka task ID
   263→ * @param {Function} onProgress - Progress callback
   264→ * @returns {Promise<Object>} Completed task result
   265→ */
   266→async function pollForCompletion(taskId, onProgress = null) {
   267→  let attempts = 0;
   268→  const startTime = Date.now();
   269→  let consecutiveFailures = 0;
   270→
   271→  while (attempts < MAX_POLL_ATTEMPTS) {
   272→    // Check elapsed time first (wall clock limit)
   273→    const elapsed = Date.now() - startTime;
   274→    if (elapsed > MAX_TOTAL_TIME_MS) {
   275→      throw new Error(`Mureka generation timed out after ${Math.round(elapsed / 1000)} seconds`);
   276→    }
   277→
   278→    attempts++;
   279→
   280→    try {
   281→      const status = await checkTaskStatus(taskId);
   282→      consecutiveFailures = 0; // Reset on success
   283→
   284→      if (onProgress) {
   285→        onProgress(status.progress, status.status);
   286→      }
   287→
   288→      if (status.status === 'completed') {
   289→        if (!status.audioUrl) {
   290→          throw new Error('Mureka completed but no audio URL provided');
   291→        }
   292→        return status;
   293→      }
   294→
   295→      if (status.status === 'failed') {
   296→        throw new Error(status.error || 'Mureka generation failed');
   297→      }
   298→
   299→      if (status.status === 'cancelled') {
   300→        throw new Error('Mureka generation was cancelled');
   301→      }
   302→    } catch (err) {
   303→      // Track consecutive failures for early abort
   304→      if (err.message.includes('timeout') || err.message.includes('ECONNREFUSED') || err.message.includes('ETIMEDOUT')) {
   305→        consecutiveFailures++;
   306→        console.warn(`[Mureka] Poll attempt ${attempts} failed: ${err.message} (${consecutiveFailures} consecutive failures)`);
   307→        if (consecutiveFailures >= 3) {
   308→          throw new Error('Mureka API unavailable - aborting after 3 consecutive failures');
   309→        }
   310→      } else {
   311→        throw err; // Re-throw non-transient errors
   312→      }
   313→    }
   314→
   315→    // Wait before polling again
   316→    await new Promise(resolve => setTimeout(resolve, POLL_INTERVAL));
   317→  }
   318→
   319→  throw new Error('Mureka generation timed out after maximum poll attempts');
   320→}
   321→
   322→/**
   323→ * Download audio file from URL
   324→ * @param {string} url - Audio URL
   325→ * @returns {Promise<Buffer>} Audio buffer
   326→ */
   327→async function downloadAudio(url) {
   328→  return new Promise((resolve, reject) => {
   329→    const protocol = url.startsWith('https') ? https : require('http');
   330→
   331→    protocol.get(url, (res) => {
   332→      if (res.statusCode === 302 || res.statusCode === 301) {
   333→        // Handle redirect
   334→        return downloadAudio(res.headers.location).then(resolve).catch(reject);
   335→      }
   336→
   337→      if (res.statusCode !== 200) {
   338→        reject(new Error(`Failed to download audio: HTTP ${res.statusCode}`));
   339→        return;
   340→      }
   341→
   342→      const chunks = [];
   343→      res.on('data', (chunk) => chunks.push(chunk));
   344→      res.on('end', () => resolve(Buffer.concat(chunks)));
   345→      res.on('error', reject);
   346→    }).on('error', reject);
   347→  });
   348→}
   349→
   350→/**
   351→ * Convert audio buffer to preview MP3
   352→ * @param {Buffer} wavBuffer - WAV audio buffer
   353→ * @returns {Promise<Buffer>} MP3 buffer
   354→ */
   355→async function createPreviewMp3(wavBuffer) {
   356→  const tempDir = process.env.TEMP_DIR || '/tmp/splice-music';
   357→  const tempWav = path.join(tempDir, `preview_${Date.now()}.wav`);
   358→  const tempMp3 = path.join(tempDir, `preview_${Date.now()}.mp3`);
   359→
   360→  try {
   361→    // Ensure temp dir exists
   362→    await fs.mkdir(tempDir, { recursive: true });
   363→
   364→    // Write WAV to temp file
   365→    await fs.writeFile(tempWav, wavBuffer);
   366→
   367→    // Convert to MP3 using FFmpeg
   368→    // SECURITY: Using execFileAsync with array arguments prevents command injection
   369→    const ffmpegArgs = ['-y', '-i', tempWav, '-codec:a', 'libmp3lame', '-qscale:a', '4', tempMp3];
   370→    await execFileAsync('ffmpeg', ffmpegArgs, { timeout: 30000 });
   371→
   372→    // Read MP3 buffer
   373→    const mp3Buffer = await fs.readFile(tempMp3);
   374→
   375→    return mp3Buffer;
   376→  } finally {
   377→    // Cleanup temp files
   378→    try {
   379→      await fs.unlink(tempWav);
   380→      await fs.unlink(tempMp3);
   381→    } catch (_e) {
   382→      // Ignore cleanup errors
   383→    }
   384→  }
   385→}
   386→
   387→/**
   388→ * Generate music title from options
   389→ * @param {Object} options - Generation options
   390→ * @returns {string} Generated title
   391→ */
   392→function generateTitle(options) {
   393→  const parts = [];
   394→
   395→  // Add mood
   396→  if (options.mood) {
   397→    parts.push(options.mood.charAt(0).toUpperCase() + options.mood.slice(1));
   398→  }
   399→
   400→  // Add reference song if available
   401→  if (options.referenceSong?.title) {
   402→    parts.push(`(${options.referenceSong.title} style)`);
   403→  }
   404→
   405→  // Add genre if available
   406→  if (options.referenceSong?.genres?.[0]) {
   407→    parts.push(options.referenceSong.genres[0]);
   408→  }
   409→
   410→  // Fallback
   411→  if (parts.length === 0) {
   412→    parts.push('AI Generated');
   413→  }
   414→
   415→  parts.push('Music');
   416→
   417→  return parts.join(' ');
   418→}
   419→
   420→/**
   421→ * Full music generation flow
   422→ * @param {Object} options - Generation options
   423→ * @param {Function} onProgress - Progress callback
   424→ * @returns {Promise<Object>} Generated music result
   425→ */
   426→async function generateMusic(options, onProgress = null) {
   427→  // Validate credentials
   428→  if (!hasMurekaCredentials()) {
   429→    throw new Error('Mureka API key not configured. Set MUREKA_API_KEY environment variable.');
   430→  }
   431→
   432→  // Submit generation request
   433→  const { taskId } = await submitGeneration(options);
   434→
   435→  if (onProgress) {
   436→    onProgress(10, 'submitted');
   437→  }
   438→
   439→  // Poll for completion
   440→  const result = await pollForCompletion(taskId, (progress, status) => {
   441→    if (onProgress) {
   442→      // Scale progress to 10-90 range (10 for submitted, 90 for downloading)
   443→      const scaledProgress = 10 + Math.floor(progress * 0.8);
   444→      onProgress(scaledProgress, status);
   445→    }
   446→  });
   447→
   448→  if (onProgress) {
   449→    onProgress(90, 'downloading');
   450→  }
   451→
   452→  // Download audio
   453→  const audioBuffer = await downloadAudio(result.audioUrl);
   454→
   455→  if (onProgress) {
   456→    onProgress(95, 'processing');
   457→  }
   458→
   459→  // Create preview MP3
   460→  let previewBuffer;
   461→  try {
   462→    previewBuffer = await createPreviewMp3(audioBuffer);
   463→  } catch (error) {
   464→    console.warn('Failed to create preview MP3:', error.message);
   465→    previewBuffer = null;
   466→  }
   467→
   468→  // Generate title
   469→  const title = generateTitle(options);
   470→
   471→  if (onProgress) {
   472→    onProgress(100, 'completed');
   473→  }
   474→
   475→  return {
   476→    title,
   477→    taskId: result.taskId,
   478→    duration: result.duration || options.duration || DEFAULT_DURATION,
   479→    audioBuffer,
   480→    previewBuffer,
   481→    audioUrl: result.audioUrl,
   482→    previewUrl: result.previewUrl
   483→  };
   484→}
   485→
   486→/**
   487→ * Get available moods
   488→ * @returns {Object} Mood presets
   489→ */
   490→function getAvailableMoods() {
   491→  return Object.keys(MOOD_PRESETS).map(key => ({
   492→    id: key,
   493→    name: key.charAt(0).toUpperCase() + key.slice(1),
   494→    description: MOOD_PRESETS[key]
   495→  }));
   496→}
   497→
   498→/**
   499→ * Get available instrument presets
   500→ * @returns {Object} Instrument presets
   501→ */
   502→function getAvailableInstruments() {
   503→  return Object.keys(INSTRUMENT_PRESETS).map(key => ({
   504→    id: key,
   505→    name: key.charAt(0).toUpperCase() + key.slice(1),
   506→    instruments: INSTRUMENT_PRESETS[key]
   507→  }));
   508→}
   509→
   510→/**
   511→ * Validate generation options
   512→ * @param {Object} options - Generation options
   513→ * @returns {{valid: boolean, errors: string[]}}
   514→ */
   515→function validateOptions(options) {
   516→  const errors = [];
   517→
   518→  // Check duration
   519→  if (options.duration !== undefined) {
   520→    if (typeof options.duration !== 'number') {
   521→      errors.push('Duration must be a number');
   522→    } else if (options.duration < MIN_DURATION) {
   523→      errors.push(`Duration must be at least ${MIN_DURATION} seconds`);
   524→    } else if (options.duration > MAX_DURATION) {
   525→      errors.push(`Duration must be at most ${MAX_DURATION} seconds`);
   526→    }
   527→  }
   528→
   529→  // Check mood
   530→  if (options.mood && !MOOD_PRESETS[options.mood]) {
   531→    // Allow custom moods, just log a warning
   532→    console.warn(`Unknown mood preset: ${options.mood}. Using as custom description.`);
   533→  }
   534→
   535→  // Check instruments
   536→  if (options.instruments && !Array.isArray(options.instruments)) {
   537→    errors.push('Instruments must be an array');
   538→  }
   539→
   540→  return {
   541→    valid: errors.length === 0,
   542→    errors
   543→  };
   544→}
   545→
   546→/**
   547→ * Generate multiple music variations in parallel
   548→ * Creates 3 variations with subtle prompt differences for user selection
   549→ * @param {Object} options - Generation options
   550→ * @param {Function} onProgress - Progress callback (variationIndex, progress, status)
   551→ * @returns {Promise<Object>} Object with variations array
   552→ */
   553→async function generateVariations(options, onProgress = null) {
   554→  // Validate credentials
   555→  if (!hasMurekaCredentials()) {
   556→    throw new Error('Mureka API key not configured. Set MUREKA_API_KEY environment variable.');
   557→  }
   558→
   559→  // Create 3 prompt variations with subtle differences
   560→  const variationPrompts = createVariationPrompts(options);
   561→  const variationCount = 3;
   562→
   563→  // Track progress for each variation
   564→  const variationProgress = new Array(variationCount).fill(0);
   565→  const variationStatus = new Array(variationCount).fill('pending');
   566→
   567→  // Submit all variations in parallel
   568→  const submittedTasks = await Promise.all(
   569→    variationPrompts.map(async (varOptions, index) => {
   570→      try {
   571→        const { taskId } = await submitGeneration(varOptions);
   572→        variationStatus[index] = 'submitted';
   573→        if (onProgress) {
   574→          onProgress(index, 10, 'submitted');
   575→        }
   576→        return { taskId, options: varOptions, index };
   577→      } catch (error) {
   578→        console.error(`Variation ${index + 1} submission failed:`, error.message);
   579→        throw new Error(`Variation ${index + 1} failed to submit: ${error.message}`);
   580→      }
   581→    })
   582→  );
   583→
   584→  // Poll all tasks in parallel
   585→  const results = await Promise.all(
   586→    submittedTasks.map(async ({ taskId, options: varOptions, index }) => {
   587→      try {
   588→        const result = await pollForCompletion(taskId, (progress, status) => {
   589→          variationProgress[index] = progress;
   590→          variationStatus[index] = status;
   591→          if (onProgress) {
   592→            // Scale progress to 10-90 range
   593→            const scaledProgress = 10 + Math.floor(progress * 0.8);
   594→            onProgress(index, scaledProgress, status);
   595→          }
   596→        });
   597→
   598→        if (onProgress) {
   599→          onProgress(index, 90, 'downloading');
   600→        }
   601→
   602→        // Download audio
   603→        const audioBuffer = await downloadAudio(result.audioUrl);
   604→
   605→        if (onProgress) {
   606→          onProgress(index, 95, 'processing');
   607→        }
   608→
   609→        // Create preview MP3
   610→        let previewBuffer;
   611→        try {
   612→          previewBuffer = await createPreviewMp3(audioBuffer);
   613→        } catch (error) {
   614→          console.warn(`Failed to create preview MP3 for variation ${index + 1}:`, error.message);
   615→          previewBuffer = null;
   616→        }
   617→
   618→        if (onProgress) {
   619→          onProgress(index, 100, 'completed');
   620→        }
   621→
   622→        return {
   623→          variationIndex: index,
   624→          variationName: getVariationName(index),
   625→          title: generateTitle(varOptions),
   626→          taskId: result.taskId,
   627→          duration: result.duration || varOptions.duration || DEFAULT_DURATION,
   628→          audioBuffer,
   629→          previewBuffer,
   630→          audioUrl: result.audioUrl,
   631→          previewUrl: result.previewUrl,
   632→          promptDescription: varOptions.variationDescription || ''
   633→        };
   634→      } catch (error) {
   635→        console.error(`Variation ${index + 1} failed:`, error.message);
   636→        return {
   637→          variationIndex: index,
   638→          variationName: getVariationName(index),
   639→          error: error.message,
   640→          failed: true
   641→        };
   642→      }
   643→    })
   644→  );
   645→
   646→  // Check if at least one variation succeeded
   647→  const successfulVariations = results.filter(r => !r.failed);
   648→  if (successfulVariations.length === 0) {
   649→    throw new Error('All variations failed to generate');
   650→  }
   651→
   652→  return {
   653→    variations: results,
   654→    successCount: successfulVariations.length,
   655→    failedCount: results.length - successfulVariations.length,
   656→    totalCount: results.length
   657→  };
   658→}
   659→
   660→/**
   661→ * Create 3 variation prompts with subtle differences
   662→ * @param {Object} baseOptions - Base generation options
   663→ * @returns {Object[]} Array of 3 variation options
   664→ */
   665→function createVariationPrompts(baseOptions) {
   666→  // Variation modifiers to create subtle differences
   667→  const variations = [
   668→    {
   669→      suffix: 'Version A - Original interpretation',
   670→      variationDescription: 'Original interpretation with balanced arrangement',
   671→      moodModifier: ''
   672→    },
   673→    {
   674→      suffix: 'Version B - More dynamic',
   675→      variationDescription: 'More dynamic with increased energy and variation',
   676→      moodModifier: 'with added dynamic range and rhythmic variation'
   677→    },
   678→    {
   679→      suffix: 'Version C - More subtle',
   680→      variationDescription: 'More subtle and minimalist approach',
   681→      moodModifier: 'with a more subtle, understated approach'
   682→    }
   683→  ];
   684→
   685→  return variations.map((variation, index) => {
   686→    // Create modified options for each variation
   687→    const modifiedOptions = { ...baseOptions };
   688→
   689→    // Build variation-specific prompt
   690→    const basePrompt = baseOptions.prompt || '';
   691→    const variationPrompt = basePrompt
   692→      ? `${basePrompt}. ${variation.suffix}`
   693→      : variation.suffix;
   694→
   695→    modifiedOptions.prompt = variationPrompt;
   696→    modifiedOptions.variationIndex = index;
   697→    modifiedOptions.variationDescription = variation.variationDescription;
   698→
   699→    // Slightly modify mood description for variety
   700→    if (variation.moodModifier) {
   701→      modifiedOptions.moodModifier = variation.moodModifier;
   702→    }
   703→
   704→    return modifiedOptions;
   705→  });
   706→}
   707→
   708→/**
   709→ * Get variation display name
   710→ * @param {number} index - Variation index (0-2)
   711→ * @returns {string} Display name
   712→ */
   713→function getVariationName(index) {
   714→  const names = ['Version A', 'Version B', 'Version C'];
   715→  return names[index] || `Version ${index + 1}`;
   716→}
   717→
   718→/**
   719→ * Build Mureka prompt with variation modifier
   720→ * Extended version of buildMurekaPrompt that handles variations
   721→ * @param {Object} options - Generation options with potential variation modifiers
   722→ * @returns {string} Formatted prompt
   723→ */
   724→function buildMurekaPromptWithVariation(options) {
   725→  const parts = [];
   726→
   727→  // Add custom prompt if provided (includes variation suffix)
   728→  if (options.prompt) {
   729→    parts.push(options.prompt);
   730→  }
   731→
   732→  // Add reference song style if available
   733→  if (options.referenceSong && options.referenceSong.identified) {
   734→    parts.push(`Style inspired by "${options.referenceSong.title}" by ${options.referenceSong.artist}`);
   735→
   736→    if (options.referenceSong.bpm) {
   737→      parts.push(`Tempo around ${options.referenceSong.bpm} BPM`);
   738→    }
   739→
   740→    if (options.referenceSong.key) {
   741→      parts.push(`Key: ${options.referenceSong.key}`);
   742→    }
   743→
   744→    if (options.referenceSong.genres?.length > 0) {
   745→      parts.push(`Genre: ${options.referenceSong.genres.join(', ')}`);
   746→    }
   747→  }
   748→
   749→  // Add mood description with variation modifier
   750→  if (options.mood) {
   751→    let moodDescription = MOOD_PRESETS[options.mood] || options.mood;
   752→    if (options.moodModifier) {
   753→      moodDescription = `${moodDescription} ${options.moodModifier}`;
   754→    }
   755→    parts.push(`Mood: ${moodDescription}`);
   756→  }
   757→
   758→  // Add instruments
   759→  if (options.instruments?.length > 0) {
   760→    const instruments = options.instruments.map(i => {
   761→      if (INSTRUMENT_PRESETS[i]) {
   762→        return INSTRUMENT_PRESETS[i].join(', ');
   763→      }
   764→      return i;
   765→    }).join(', ');
   766→    parts.push(`Instruments: ${instruments}`);
   767→  }
   768→
   769→  // Add duration
   770→  const duration = clampDuration(options.duration || DEFAULT_DURATION);
   771→  parts.push(`Duration: approximately ${duration} seconds`);
   772→
   773→  // Add quality markers
   774→  parts.push('Instrumental only, no vocals');
   775→  parts.push('Suitable as background music for video');
   776→  parts.push('Professional quality, well-mixed');
   777→
   778→  return parts.join('. ');
   779→}
   780→
   781→/**
   782→ * Generate scene-aware music using transcript analysis
   783→ * Analyzes transcript for mood/energy and builds enhanced prompt
   784→ * @param {Object[]} segments - Transcript segments with {id, start, end, text}
   785→ * @param {Object} options - Generation options
   786→ * @param {Function} onProgress - Progress callback
   787→ * @returns {Promise<Object>} Generated music result with scene context
   788→ */
   789→async function generateSceneAwareMusic(segments, options, onProgress = null) {
   790→  // Validate credentials
   791→  if (!hasMurekaCredentials()) {
   792→    throw new Error('Mureka API key not configured. Set MUREKA_API_KEY environment variable.');
   793→  }
   794→
   795→  if (onProgress) {
   796→    onProgress(5, 'analyzing');
   797→  }
   798→
   799→  // Analyze scenes from transcript
   800→  const sceneContext = await sceneAnalysis.analyzeScenes(segments);
   801→
   802→  if (onProgress) {
   803→    onProgress(15, 'building_prompt');
   804→  }
   805→
   806→  // Build scene-aware prompt
   807→  const enhancedPrompt = sceneAnalysis.buildSceneAwarePrompt(sceneContext, options);
   808→
   809→  // Create modified options with scene-aware prompt
   810→  const sceneAwareOptions = {
   811→    ...options,
   812→    prompt: enhancedPrompt,
   813→    // Store scene context for metadata
   814→    _sceneContext: sceneContext
   815→  };
   816→
   817→  if (onProgress) {
   818→    onProgress(20, 'generating');
   819→  }
   820→
   821→  // Submit generation with scene-aware prompt
   822→  const { taskId } = await submitGeneration(sceneAwareOptions);
   823→
   824→  // Poll for completion
   825→  const result = await pollForCompletion(taskId, (progress, pollStatus) => {
   826→    if (onProgress) {
   827→      // Scale progress to 20-90 range
   828→      const scaledProgress = 20 + Math.floor(progress * 0.7);
   829→      onProgress(scaledProgress, pollStatus);
   830→    }
   831→  });
   832→
   833→  if (onProgress) {
   834→    onProgress(90, 'downloading');
   835→  }
   836→
   837→  // Download audio
   838→  const audioBuffer = await downloadAudio(result.audioUrl);
   839→
   840→  if (onProgress) {
   841→    onProgress(95, 'processing');
   842→  }
   843→
   844→  // Create preview MP3
   845→  let previewBuffer;
   846→  try {
   847→    previewBuffer = await createPreviewMp3(audioBuffer);
   848→  } catch (error) {
   849→    console.warn('Failed to create preview MP3:', error.message);
   850→    previewBuffer = null;
   851→  }
   852→
   853→  // Generate title with scene context
   854→  const title = generateSceneAwareTitle(options, sceneContext);
   855→
   856→  if (onProgress) {
   857→    onProgress(100, 'completed');
   858→  }
   859→
   860→  return {
   861→    title,
   862→    taskId: result.taskId,
   863→    duration: result.duration || options.duration || DEFAULT_DURATION,
   864→    audioBuffer,
   865→    previewBuffer,
   866→    audioUrl: result.audioUrl,
   867→    previewUrl: result.previewUrl,
   868→    // Include scene analysis metadata
   869→    sceneContext: {
   870→      dominantMood: sceneContext.dominantMood,
   871→      averageEnergy: sceneContext.averageEnergy,
   872→      sceneCount: sceneContext.scenes.length,
   873→      summary: sceneAnalysis.getSceneContextSummary(sceneContext)
   874→    }
   875→  };
   876→}
   877→
   878→/**
   879→ * Generate title for scene-aware music
   880→ * @param {Object} options - Generation options
   881→ * @param {Object} sceneContext - Scene analysis result
   882→ * @returns {string} Generated title
   883→ */
   884→function generateSceneAwareTitle(options, sceneContext) {
   885→  const parts = [];
   886→
   887→  // Add detected mood
   888→  if (sceneContext.dominantMood && sceneContext.dominantMood !== 'neutral') {
   889→    const moodName = sceneContext.dominantMood.charAt(0).toUpperCase() +
   890→      sceneContext.dominantMood.slice(1);
   891→    parts.push(moodName);
   892→  }
   893→
   894→  // Add energy indicator
   895→  const energy = sceneContext.averageEnergy;
   896→  if (energy >= 75) {
   897→    parts.push('High-Energy');
   898→  } else if (energy <= 30) {
   899→    parts.push('Ambient');
   900→  }
   901→
   902→  // Add user mood if different
   903→  if (options.mood && options.mood !== sceneContext.dominantMood) {
   904→    const userMood = options.mood.charAt(0).toUpperCase() + options.mood.slice(1);
   905→    parts.push(userMood);
   906→  }
   907→
   908→  // Add reference song if available
   909→  if (options.referenceSong?.title) {
   910→    parts.push(`(${options.referenceSong.title} style)`);
   911→  }
   912→
   913→  // Fallback
   914→  if (parts.length === 0) {
   915→    parts.push('Scene-Aware');
   916→  }
   917→
   918→  parts.push('Music');
   919→
   920→  return parts.join(' ');
   921→}
   922→
   923→/**
   924→ * Validate scene-aware generation options
   925→ * @param {Object[]} segments - Transcript segments
   926→ * @param {Object} options - Generation options
   927→ * @returns {{valid: boolean, errors: string[]}}
   928→ */
   929→function validateSceneAwareOptions(segments, options) {
   930→  const errors = [];
   931→
   932→  // Check segments
   933→  if (!segments || !Array.isArray(segments)) {
   934→    errors.push('Transcript segments are required');
   935→  } else if (segments.length === 0) {
   936→    errors.push('At least one transcript segment is required');
   937→  } else {
   938→    // Validate segment format
   939→    const hasValidFormat = segments.every(s =>
   940→      typeof s.start === 'number' &&
   941→      typeof s.end === 'number' &&
   942→      typeof s.text === 'string'
   943→    );
   944→    if (!hasValidFormat) {
   945→      errors.push('Segments must have start, end (numbers), and text (string)');
   946→    }
   947→  }
   948→
   949→  // Also validate base options
   950→  const baseValidation = validateOptions(options);
   951→  if (!baseValidation.valid) {
   952→    errors.push(...baseValidation.errors);
   953→  }
   954→
   955→  return {
   956→    valid: errors.length === 0,
   957→    errors
   958→  };
   959→}
   960→
   961→module.exports = {
   962→  hasMurekaCredentials,
   963→  buildMurekaPrompt,
   964→  buildMurekaPromptWithVariation,
   965→  clampDuration,
   966→  submitGeneration,
   967→  checkTaskStatus,
   968→  pollForCompletion,
   969→  downloadAudio,
   970→  createPreviewMp3,
   971→  generateTitle,
   972→  generateMusic,
   973→  generateVariations,
   974→  createVariationPrompts,
   975→  getVariationName,
   976→  getAvailableMoods,
   977→  getAvailableInstruments,
   978→  validateOptions,
   979→  // Scene-aware functions
   980→  generateSceneAwareMusic,
   981→  generateSceneAwareTitle,
   982→  validateSceneAwareOptions,
   983→  MOOD_PRESETS,
   984→  INSTRUMENT_PRESETS,
