   698→function handleYoutubeUrlChange() {
   699→  const urlInput = musicElements.youtubeUrl || document.getElementById('music-youtube-url');
   700→  const identifyBtn = musicElements.identifyBtn || document.getElementById('music-identify-btn');
   701→
   702→  if (!urlInput || !identifyBtn) return;
   703→
   704→  const url = urlInput.value.trim();
   705→  const isValidUrl = isValidYouTubeUrl(url);
   706→  identifyBtn.disabled = !isValidUrl;
   707→
   708→  if (musicState.identifiedSong) {
   709→    handleClearReference();
   710→  }
   711→}
   712→
   713→function isValidYouTubeUrl(url) {
   714→  if (!url) return false;
   715→  const patterns = [
   716→    /youtube\.com\/watch\?v=[\w-]{11}/,
   717→    /youtu\.be\/[\w-]{11}/,
   718→    /youtube\.com\/embed\/[\w-]{11}/
   719→  ];
   720→  return patterns.some(p => p.test(url));
   721→}
   722→
   723→async function handleIdentifySong() {
   724→  const urlInput = musicElements.youtubeUrl || document.getElementById('music-youtube-url');
   725→  const identifyBtn = musicElements.identifyBtn || document.getElementById('music-identify-btn');
   726→
   727→  if (!urlInput || !identifyBtn) return;
   728→
   729→  const url = urlInput.value.trim();
   730→  if (!isValidYouTubeUrl(url)) {
   731→    setMusicStatus('Invalid YouTube URL', 'error');
   732→    return;
   733→  }
   734→
   735→  try {
   736→    musicState.isIdentifying = true;
   737→    identifyBtn.disabled = true;
   738→    identifyBtn.textContent = 'Identifying...';
   739→    setMusicStatus('Identifying song...', 'info');
   740→
   741→    const result = await identifySong(url);
   742→
   743→    if (result.identified) {
   744→      musicState.identifiedSong = result;
   745→      displayIdentifiedSong(result);
   746→      setMusicStatus(`Identified: ${result.title} by ${result.artist}`, 'success');
   747→    } else {
   748→      setMusicStatus('Could not identify song. You can still generate music.', 'warning');
   749→    }
   750→
   751→  } catch (error) {
   752→    console.error('Identification error:', error);
   753→    setMusicStatus(`Error: ${error.message}`, 'error');
   754→  } finally {
   755→    musicState.isIdentifying = false;
   756→    identifyBtn.disabled = false;
   757→    identifyBtn.textContent = 'Identify';
   758→  }
   759→}
   760→
   761→function displayIdentifiedSong(song) {
   762→  const display = musicElements.referenceDisplay || document.getElementById('music-identify-result');
   763→  if (!display) return;
   764→
   765→  const details = [];
   766→  if (song.bpm) details.push(`${song.bpm} BPM`);
   767→  if (song.key) details.push(song.key);
   768→  if (song.mood) details.push(song.mood);
   769→
   770→  display.innerHTML = `
   771→    <div class="music-reference-card">
   772→      <div class="music-ref-title">${escapeHtml(song.title)}</div>
   773→      <div class="music-ref-artist">${escapeHtml(song.artist)}</div>
   774→      <div class="music-ref-details">${details.join(' - ')}</div>
   775→      <button id="musicClearRefBtn" class="music-clear-ref-btn" title="Clear reference">x</button>
   776→    </div>
   777→  `;
   778→  display.style.display = 'block';
   779→}
   780→
   781→function handleClearReference() {
   782→  musicState.identifiedSong = null;
   783→
   784→  const display = musicElements.referenceDisplay || document.getElementById('music-identify-result');
