   905→function startPollingVariations(jobId) {
   906→  // Clear any existing polling
   907→  if (musicState.variationsPollingInterval) {
   908→    clearInterval(musicState.variationsPollingInterval);
   909→  }
   910→
   911→  // Track which variations job we're polling for (race condition fix)
   912→  musicState.currentVariationsJobId = jobId;
   913→
   914→  musicState.variationsPollingInterval = setInterval(async () => {
   915→    // Race condition check: verify we're still polling for this job
   916→    if (musicState.currentVariationsJobId !== jobId) {
   917→      return; // A new variations job started polling, ignore this callback
   918→    }
   919→
   920→    try {
   921→      const status = await getVariationsStatus(jobId);
   922→
   923→      // Double-check after async operation
   924→      if (musicState.currentVariationsJobId !== jobId) {
   925→        return;
   926→      }
   927→
   928→      musicState.variationsJob = { ...musicState.variationsJob, ...status };
   929→      renderVariationsProgress(status);
   930→
   931→      if (status.status === 'selecting' || status.status === 'completed') {
   932→        clearInterval(musicState.variationsPollingInterval);
   933→        musicState.variationsPollingInterval = null;
   934→        musicState.currentVariationsJobId = null;
   935→
   936→        if (status.variations && status.variations.length > 0) {
   937→          setMusicStatus('Variations ready! Select your favorite.', 'success');
   938→          renderVariationsSelection(status.variations);
   939→        }
   940→      } else if (status.status === 'failed') {
   941→        clearInterval(musicState.variationsPollingInterval);
   942→        musicState.variationsPollingInterval = null;
   943→        musicState.currentVariationsJobId = null;
   944→        setMusicStatus(`Variations failed: ${status.failedReason || 'Unknown error'}`, 'error');
   945→      }
   946→    } catch (error) {
   947→      console.error('Variations polling error:', error);
   948→    }
   949→  }, MUSIC_POLL_INTERVAL);
   950→}
