  1082→
  1083→  const player = musicState.variationPlayers[index];
  1084→
  1085→  if (player.paused || player.src !== url) {
  1086→    player.src = url;
  1087→    try {
  1088→      await player.play();
  1089→      btn.textContent = 'Pause';
  1090→    } catch (error) {
  1091→      console.error('Play error:', error);
  1092→      setMusicStatus('Could not play preview', 'error');
  1093→    }
  1094→  } else {
