  1332→  const modal = document.getElementById('music-align-modal');
  1333→  if (!modal) {
  1334→    console.error('Alignment modal not found in DOM');
  1335→    return;
  1336→  }
  1337→
  1338→  // Populate Info
  1339→  const infoEl = document.getElementById('music-align-info');
  1340→  if (infoEl) {
  1341→    const defaultDuration = sequenceDuration ? Math.round(sequenceDuration) : Math.round(beatAnalysis.duration * 0.8);
  1342→
  1343→    // SECURITY: Escape dynamic content to prevent XSS
  1344→    let html = `
  1345→          <p><strong>Original Duration:</strong> ${escapeHtml(formatDuration(beatAnalysis.duration))}</p>
  1346→          <p><strong>Detected BPM:</strong> ${escapeHtml(beatAnalysis.bpm || 'Unknown')}</p>
  1347→          <p><strong>Beat Count:</strong> ${escapeHtml(beatAnalysis.beatCount)}</p>
  1348→      `;
  1349→    if (sequenceDuration) {
  1350→      html += `<p class="highlight"><strong>Sequence Duration:</strong> ${escapeHtml(formatDuration(sequenceDuration))}</p>`;
  1351→    }
  1352→    infoEl.innerHTML = html;
  1353→
  1354→    // Set default target duration
  1355→    const targetInput = document.getElementById('music-align-target');
  1356→    if (targetInput) {
  1357→      targetInput.value = defaultDuration;
  1358→      targetInput.max = Math.ceil(beatAnalysis.duration);
  1359→    }
  1360→  }
  1361→
  1362→  // Setup Fade Slider
  1363→  const fadeSlider = document.getElementById('music-fade-slider');
  1364→  const fadeValue = document.getElementById('music-fade-value');
  1365→  if (fadeSlider && fadeValue) {
  1366→    fadeSlider.min = options.fadeDuration.min;
  1367→    fadeSlider.max = options.fadeDuration.max;
  1368→    fadeSlider.value = options.fadeDuration.default;
  1369→    fadeValue.textContent = options.fadeDuration.default;
  1370→
  1371→    // Remove old listener to avoid duplicates if any
  1372→    const newSlider = fadeSlider.cloneNode(true);
  1373→    fadeSlider.parentNode.replaceChild(newSlider, fadeSlider);
  1374→
  1375→    newSlider.addEventListener('input', (e) => {
  1376→      if (fadeValue) fadeValue.textContent = e.target.value;
  1377→    });
  1378→  }
  1379→
  1380→  // Setup Match Sequence Button
  1381→  const matchBtn = document.getElementById('music-align-match-seq');
  1382→  if (matchBtn) {
