  1384→      matchBtn.style.display = 'inline-block';
  1385→      matchBtn.onclick = () => {
  1386→        const targetInput = document.getElementById('music-align-target');
  1387→        if (targetInput) targetInput.value = Math.round(sequenceDuration);
  1388→      };
  1389→    } else {
  1390→      matchBtn.style.display = 'none';
  1391→    }
  1392→  }
  1393→
  1394→  // Setup Confirm Button
  1395→  const confirmBtn = document.getElementById('music-align-btn');
  1396→  if (confirmBtn) {
  1397→    confirmBtn.onclick = (e) => handleAlignmentConfirm(e, jobId);
  1398→    confirmBtn.disabled = false;
  1399→    confirmBtn.textContent = 'Align & Trim';
  1400→  }
