  1418→async function handleAlignmentConfirm(event, jobId) {
  1419→  const btn = event.target;
  1420→  // jobId is passed directly now, or fallback to state? 
  1421→  // The original code used dataset.jobId. We passed it in the onclick handler closure.
  1422→
  1423→  const targetInput = document.getElementById('music-align-target');
  1424→  const fadeInput = document.getElementById('music-fade-slider'); // It's a slider now in HTML
  1425→  const beatAlignInput = document.getElementById('music-beat-align');
  1426→
  1427→  const targetDuration = parseFloat(targetInput?.value);
