     1→/**
     2→ * SPLICE CEP Panel Configuration
     3→ * Backend URLs, paths, and utility functions
     4→ */
     5→
     6→// ============================================================================
     7→// BACKEND CONFIGURATION
     8→// ============================================================================
     9→const SPLICE_CONFIG = {
    10→    VERSION: '6.0.3',
    11→    BACKEND_PROD: 'https://splice-api-production.up.railway.app',
    12→    BACKEND_DEV: 'https://127.0.0.1:3847',
    13→    FETCH_TIMEOUT: 120000, // 2 minutes
    14→    RETRY_ATTEMPTS: 3,
    15→    RETRY_DELAY: 1000
    16→};
    17→
    18→/**
    19→ * Get the backend URL based on environment
    20→ */
    21→function getBackendUrl() {
    22→    // Check for development mode
    23→    const isDev = window.location.protocol === 'file:' ||
    24→                  window.location.hostname === 'localhost' ||
    25→                  window.location.hostname === '127.0.0.1';
    26→
    27→    // Allow override via localStorage
    28→    const customUrl = localStorage.getItem('spliceBackendUrl');
    29→    if (customUrl) return customUrl;
    30→
    31→    return isDev ? SPLICE_CONFIG.BACKEND_DEV : SPLICE_CONFIG.BACKEND_PROD;
    32→}
    33→
    34→// ============================================================================
    35→// JSX BRIDGE - Communicate with Premiere Pro
    36→// ============================================================================
    37→const jsx = {
    38→    cs: null,
    39→    _initPromise: null,
    40→    _initialized: false,
    41→
    42→    /**
    43→     * Initialize the CSInterface
    44→     * Returns a promise that resolves when ready (true/false)
    45→     * Does NOT reject - gracefully handles missing CEP environment
    46→     */
    47→    init: function() {
    48→        if (this._initPromise) return this._initPromise;
    49→
    50→        this._initPromise = new Promise((resolve) => {
    51→            try {
    52→                if (typeof CSInterface !== 'undefined') {
    53→                    this.cs = new CSInterface();
    54→                    // Verify we're in a real CEP environment by checking for __adobe_cep__
    55→                    if (window.__adobe_cep__) {
    56→                        this._initialized = true;
    57→                        console.log('[JSX] CSInterface initialized successfully (CEP environment detected)');
    58→                    } else {
    59→                        // CSInterface exists but no CEP runtime - browser/dev mode
    60→                        this._initialized = true;
    61→                        console.log('[JSX] CSInterface initialized (development/browser mode)');
    62→                    }
    63→                } else {
    64→                    console.warn('[JSX] CSInterface not available - panel may not function correctly');
    65→                    this._initialized = false;
    66→                }
    67→            } catch (err) {
    68→                console.error('[JSX] Failed to initialize CSInterface:', err);
    69→                this._initialized = false;
    70→            }
    71→            resolve(this._initialized);
    72→        });
    73→
    74→        return this._initPromise;
    75→    },
    76→
    77→    /**
    78→     * Ensure JSX is initialized before making calls
    79→     */
    80→    ensureInitialized: async function() {
    81→        if (!this._initialized) {
    82→            await this.init();
    83→        }
    84→        if (!this.cs) {
    85→            throw new Error('CSInterface not initialized. Make sure you are running in CEP environment.');
    86→        }
    87→    },
    88→
    89→    /**
    90→     * Execute ExtendScript in Premiere Pro
    91→     * @param {string} script - ExtendScript to execute
    92→     * @returns {Promise<any>} - Parsed result
    93→     */
    94→    evalScript: async function(script) {
    95→        await this.ensureInitialized();
    96→
    97→        return new Promise((resolve, reject) => {
    98→            this.cs.evalScript(script, (result) => {
    99→                if (result === 'undefined' || result === undefined) {
   100→                    resolve(null);
   101→                    return;
   102→                }
   103→
   104→                // Check for EvalScript error or ExtendScript runtime errors
   105→                if (typeof result === 'string' && (
   106→                    result.indexOf('EvalScript error') !== -1 ||
   107→                    result.indexOf('Error:') !== -1 ||
   108→                    result.indexOf('TypeError:') !== -1 ||
   109→                    result.indexOf('ReferenceError:') !== -1
   110→                )) {
   111→                    reject(new Error(result));
   112→                    return;
   113→                }
   114→
   115→                // Try to parse JSON
   116→                try {
   117→                    const parsed = JSON.parse(result);
   118→                    if (parsed.error) {
   119→                        reject(new Error(parsed.error));
   120→                    } else {
   121→                        resolve(parsed);
   122→                    }
   123→                } catch (e) {
   124→                    // Return as-is if not JSON
   125→                    resolve(result);
   126→                }
   127→            });
   128→        });
   129→    },
   130→
   131→    /**
   132→     * Call a JSX function with arguments
   133→     * @param {string} funcName - Function name
   134→     * @param {...any} args - Arguments to pass
   135→     * @returns {Promise<any>}
   136→     */
   137→    call: async function(funcName, ...args) {
   138→        await this.ensureInitialized();
   139→
   140→        const escapedArgs = args.map(arg => {
   141→            if (arg === null || arg === undefined) return 'null';
   142→            if (typeof arg === 'string') {
   143→                // Escape backslashes first, then other special chars
   144→                return `'${arg
   145→                    .replace(/\\/g, '\\\\')
   146→                    .replace(/'/g, "\\'")
   147→                    .replace(/\r/g, '\\r')
   148→                    .replace(/\n/g, '\\n')
   149→                    .replace(/\t/g, '\\t')}'`;
   150→            }
   151→            if (typeof arg === 'object') {
   152→                return `'${JSON.stringify(arg)
   153→                    .replace(/\\/g, '\\\\')
   154→                    .replace(/'/g, "\\'")}'`;
   155→            }
   156→            return String(arg);
   157→        });
   158→
   159→        const script = `${funcName}(${escapedArgs.join(', ')})`;
   160→        return this.evalScript(script);
   161→    }
   162→};
