   291→// ============================================================================
   292→// OFFLINE DETECTION
   293→// ============================================================================
   294→
   295→let isOnlineState = true;
   296→let offlineCheckInterval = null;
   297→
   298→/**
   299→ * Check if online
   300→ */
   301→function isOnline() {
   302→    return isOnlineState && navigator.onLine;
   303→}
   304→
   305→/**
   306→ * Initialize offline detection
   307→ */
   308→function initOfflineDetection() {
   309→    // Browser events
   310→    window.addEventListener('online', () => {
   311→        isOnlineState = true;
   312→        updateOfflineUI(true);
   313→    });
   314→
   315→    window.addEventListener('offline', () => {
   316→        isOnlineState = false;
   317→        updateOfflineUI(false);
   318→    });
   319→
   320→    // Periodic check
   321→    offlineCheckInterval = setInterval(async () => {
   322→        try {
   323→            const response = await fetchWithTimeout(
   324→                `${getBackendUrl()}/health`,
   325→                { method: 'GET' },
   326→                5000
   327→            );
   328→            isOnlineState = response.ok;
   329→        } catch {
   330→            isOnlineState = false;
   331→        }
   332→        updateOfflineUI(isOnlineState);
   333→    }, 30000);
   334→}
   335→
   336→/**
   337→ * Update UI based on online state
   338→ */
   339→function updateOfflineUI(online) {
   340→    const goBtn = document.getElementById('goBtn');
   341→    const status = document.getElementById('status');
   342→
   343→    if (goBtn) {
   344→        goBtn.disabled = !online;
   345→    }
   346→
   347→    if (status) {
   348→        if (!online) {
   349→            status.textContent = 'Offline - Check your connection';
   350→            status.style.color = '#dc3545';
   351→        } else {
   352→            // Reset status when back online
   353→            status.textContent = 'Ready';
   354→            status.style.color = '#888';
   355→        }
   356→    }
   357→}
