   581→/**
   582→ * Apply a preset to current settings
   583→ * @param {string} presetName - Name of the preset to apply
   584→ * @returns {Object} The applied settings
   585→ */
   586→function applyPreset(presetName) {
   587→    const preset = PRESETS[presetName];
   588→
   589→    if (!preset) {
   590→        console.warn(`[SPLICE] Unknown preset: ${presetName}`);
   591→        return getSettings();
   592→    }
   593→
   594→    // Custom preset uses current settings
   595→    if (presetName === 'custom' || !preset.settings) {
   596→        saveSettings({ activePreset: 'custom' });
   597→        return getSettings();
   598→    }
   599→
   600→    // Apply preset settings
   601→    const newSettings = {
   602→        ...preset.settings,
   603→        activePreset: presetName
   604→    };
   605→
   606→    saveSettings(newSettings);
   607→    console.log(`[SPLICE] Applied preset: ${preset.name}`);
   608→
   609→    return getSettings();
   610→}
