   619→}
   620→
   621→/**
   622→ * Initialize preset selector
   623→ */
   624→function initPresetSelector() {
   625→    const presetSelector = document.getElementById('presetSelector');
   626→    const sensitivitySlider = document.getElementById('sensitivitySlider');
   627→
   628→    if (!presetSelector) return;
   629→
   630→    // Set initial value from settings
   631→    const settings = getSettings();
   632→    presetSelector.value = settings.activePreset || 'podcast';
   633→
   634→    // Handle preset change
   635→    presetSelector.addEventListener('change', () => {
   636→        const presetId = presetSelector.value;
   637→        const appliedSettings = applyPreset(presetId);
   638→
   639→        // Update sensitivity slider to match preset
   640→        if (sensitivitySlider && appliedSettings.sensitivity !== undefined) {
   641→            sensitivitySlider.value = appliedSettings.sensitivity;
   642→            // Update display value
   643→            const sensitivityValue = document.getElementById('sensitivityValue');
   644→            if (sensitivityValue) {
   645→                sensitivityValue.textContent = appliedSettings.sensitivity;
   646→            }
   647→        }
   648→
   649→        // Update takes detection checkbox
   650→        const enableTakesDetection = document.getElementById('enableTakesDetection');
   651→        if (enableTakesDetection && appliedSettings.enableTakesDetection !== undefined) {
   652→            enableTakesDetection.checked = appliedSettings.enableTakesDetection;
   653→        }
   654→
   655→        // Update J-Cut checkbox
   656→        const enableJCut = document.getElementById('enableJCut');
   657→        if (enableJCut && appliedSettings.enableJCut !== undefined) {
   658→            enableJCut.checked = appliedSettings.enableJCut;
   659→            // Toggle J-Cut settings visibility
   660→            const jcutSettings = document.getElementById('jcutSettings');
   661→            if (jcutSettings) {
   662→                jcutSettings.classList.toggle('collapsed', !appliedSettings.enableJCut);
   663→            }
   664→        }
   665→
   666→        const preset = getPreset(presetId);
   667→        if (preset) {
   668→            setStatus(`Preset: ${preset.name} - ${preset.description}`);
   669→        }
   670→    });
   671→
   672→    // Switch to custom when user manually changes sensitivity
   673→    if (sensitivitySlider) {
   674→        sensitivitySlider.addEventListener('change', () => {
   675→            if (presetSelector.value !== 'custom') {
   676→                presetSelector.value = 'custom';
   677→                saveSettings({ activePreset: 'custom' });
   678→            }
   679→        });
   680→    }
   681→}
