   532→
   533→async function runDetection() {
   534→    if (isOperationInProgress) return;
   535→
   536→    // Check sequence
   537→    const hasSequence = await checkSequenceOpen();
   538→    if (!hasSequence) {
   539→        setStatus('Please open a sequence first', true);
   540→        return;
   541→    }
   542→
   543→    // Check online
   544→    if (!isOnline()) {
   545→        setStatus('Offline - Check your connection', true);
   546→        return;
   547→    }
   548→
   549→    isOperationInProgress = true;
   550→    ui.goBtn.disabled = true;
   551→    showProgress('Detecting silences...');
   552→
   553→    try {
   554→        const settings = getSettings();
   555→
   556→        // Get sequence info
   557→        const seqInfo = await jsx.call('getActiveSequence');
   558→        console.log('[SPLICE] Sequence info:', seqInfo);
   559→
   560→        // Store sequence info for later use
   561→        currentSequenceInfo = seqInfo;
   562→        window.spliceState.activeSequence = seqInfo;
   563→        if (seqInfo && seqInfo.videoPath) {
   564→            window.spliceState.currentVideoPath = seqInfo.videoPath;
   565→        }
   566→
   567→        // Export audio via JSX for silence detection
   568→        updateProgress(10, 'Exporting audio...');
   569→
   570→        let audioFilePath = null;
   571→        try {
   572→            // Get sequence info for audio export
   573→            const exportResult = await jsx.call('exportSequenceAudioForAnalysis');
   574→            if (exportResult && exportResult.success && exportResult.outputPath) {
   575→                audioFilePath = exportResult.outputPath;
   576→                console.log('[SPLICE] Audio exported to:', audioFilePath);
   577→            } else if (exportResult && exportResult.error) {
   578→                throw new Error(exportResult.error);
   579→            }
   580→        } catch (exportError) {
   581→            console.warn('[SPLICE] Audio export failed, trying fallback:', exportError.message);
   582→            // Try to get audio from first clip in timeline
   583→            try {
   584→                const clipInfo = await jsx.call('getFirstClipAudioPath');
   585→                if (clipInfo && clipInfo.path) {
   586→                    audioFilePath = clipInfo.path;
   587→                    console.log('[SPLICE] Using clip audio path:', audioFilePath);
   588→                }
   589→            } catch (clipError) {
   590→                console.warn('[SPLICE] Fallback audio path failed:', clipError.message);
   591→            }
   592→        }
   593→
   594→        if (!audioFilePath) {
   595→            throw new Error('Could not export or locate audio file. Please ensure sequence has audio tracks.');
   596→        }
   597→
   598→        // Store audio path globally for other modules
   599→        currentAudioPath = audioFilePath;
   600→        window.spliceState.audioPath = audioFilePath;
   601→
   602→        // Call backend for silence detection with audio file path
   603→        updateProgress(30, 'Detecting silences...');
   604→        const silenceResponse = await fetchWithTimeout(
   605→            `${getBackendUrl()}/silences-rms`,
   606→            {
   607→                method: 'POST',
   608→                headers: getAuthHeaders(),
   609→                body: JSON.stringify({
   610→                    wavPath: audioFilePath,
   611→                    sensitivity: settings.sensitivity,
   612→                    minSilenceLength: settings.minSilenceLength || 0.5
   613→                })
   614→            }
   615→        );
   616→
   617→        if (!silenceResponse.ok) {
   618→            throw new Error(await parseErrorResponse(silenceResponse));
   619→        }
   620→
   621→        const silenceData = await silenceResponse.json();
   622→        previewSilences = silenceData.silences || [];
   623→
   624→        // Detect takes if enabled
   625→        if (settings.enableTakesDetection) {
   626→            updateProgress(60, 'Detecting takes...');
   627→            try {
   628→                const analyzeResponse = await fetchWithTimeout(
