  1031→async function buildSequence() {
  1032→    if (isOperationInProgress) return;
  1033→
  1034→    isOperationInProgress = true;
  1035→    setStatus('Building sequence...');
  1036→
  1037→    try {
  1038→        const settings = getSettings();
  1039→
  1040→        // Ensure we have sequence info
  1041→        if (!currentSequenceInfo) {
  1042→            currentSequenceInfo = await jsx.call('getActiveSequence');
  1043→        }
  1044→
  1045→        // Create cut list from selected silences
  1046→        const selectedSilences = previewSilences.filter((_, i) => selectedSilenceIndices.has(i));
  1047→
  1048→        // Calculate total duration from sequence info or last silence
  1049→        let duration = 0;
  1050→        if (currentSequenceInfo && currentSequenceInfo.duration) {
  1051→            duration = currentSequenceInfo.duration;
  1052→        } else if (previewSilences.length > 0) {
  1053→            const lastSilence = previewSilences[previewSilences.length - 1];
  1054→            duration = lastSilence.end + 10; // Add buffer
  1055→        }
  1056→
  1057→        // Call backend to generate cut list
  1058→        const cutListResponse = await fetchWithTimeout(
  1059→            `${getBackendUrl()}/cut-list`,
  1060→            {
  1061→                method: 'POST',
  1062→                headers: getAuthHeaders(),
  1063→                body: JSON.stringify({
  1064→                    sourceName: currentSequenceInfo?.name || 'Untitled Sequence',
  1065→                    sourcePath: currentSequenceInfo?.treePath || currentSequenceInfo?.name || 'Untitled',
  1066→                    duration: duration,
  1067→                    silences: selectedSilences,
  1068→                    takes: previewTakes,
  1069→                    enableTakes: settings.enableTakesDetection,
  1070→                    jCutOffset: settings.enableJCut ? settings.jcutLeadIn : 0,
  1071→                    lCutOffset: settings.enableJCut ? settings.jcutLeadOut : 0
  1072→                })
  1073→            }
  1074→        );
  1075→
  1076→        if (!cutListResponse.ok) {
  1077→            throw new Error(await parseErrorResponse(cutListResponse));
  1078→        }
  1079→
  1080→        const cutList = await cutListResponse.json();
  1081→
  1082→        // Build sequence via JSX
  1083→        const result = await jsx.call('buildSequenceFromCutList', JSON.stringify(cutList));
  1084→
  1085→        if (result && result.success) {
  1086→            setStatus(`Built sequence: ${result.sequenceName}`);
  1087→            hidePreview();
  1088→        } else {
  1089→            throw new Error(result?.error || 'Build failed');
  1090→        }
  1091→    } catch (error) {
  1092→        setStatus('Build failed: ' + error.message, true);
  1093→    } finally {
  1094→        isOperationInProgress = false;
  1095→    }
  1096→}
