  1237→ * @param {string} requiredTier - Required tier (Pro, Team)
  1238→ */
  1239→function showUpgradeModal(featureName, requiredTier = 'Pro') {
  1240→    const modal = document.getElementById('upgradeModal');
  1241→    const featureText = document.getElementById('upgradeFeatureName');
  1242→    const tierText = document.getElementById('upgradeRequiredTier');
  1243→
  1244→    if (featureText) {
  1245→        featureText.textContent = featureName;
  1246→    }
  1247→    if (tierText) {
  1248→        tierText.textContent = requiredTier;
  1249→    }
  1250→    if (modal) {
  1251→        modal.classList.remove('hidden');
  1252→    }
  1253→}
  1254→
  1255→/**
  1256→ * Close the upgrade modal
  1257→ */
  1258→function closeUpgradeModal() {
  1259→    const modal = document.getElementById('upgradeModal');
  1260→    if (modal) {
  1261→        modal.classList.add('hidden');
  1262→    }
  1263→}
  1264→
  1265→// Expose showUpgradeModal and closeUpgradeModal globally for feature modules
  1266→window.showUpgradeModal = showUpgradeModal;
  1267→window.closeUpgradeModal = closeUpgradeModal;
  1268→
  1269→async function activateLicense() {
  1270→    if (!ui.licenseKeyInput) return;
  1271→
  1272→    const licenseKey = ui.licenseKeyInput.value.trim();
  1273→    if (!licenseKey) {
  1274→        setStatus('Please enter a license key', true);
  1275→        return;
  1276→    }
  1277→
  1278→    try {
  1279→        const response = await fetchWithTimeout(
  1280→            `${getBackendUrl()}/license/activate`,
  1281→            {
  1282→                method: 'POST',
  1283→                headers: { 'Content-Type': 'application/json' },
  1284→                body: JSON.stringify({ key: licenseKey })
  1285→            }
  1286→        );
  1287→
  1288→        if (!response.ok) {
  1289→            throw new Error(await parseErrorResponse(response));
  1290→        }
  1291→
  1292→        const data = await response.json();
  1293→        saveSettings({
  1294→            customerId: data.customerId,
  1295→            licenseKey: licenseKey
  1296→        });
  1297→
  1298→        if (ui.loginModal) ui.loginModal.classList.add('hidden');
  1299→        setStatus('License activated');
  1300→        updateCredits();
  1301→    } catch (error) {
  1302→        setStatus('Activation failed: ' + error.message, true);
  1303→    }
  1304→}
  1305→
  1306→/**
  1307→ * Look up license key by email address
  1308→ */
  1309→async function lookupLicense() {
  1310→    const lookupEmailInput = document.getElementById('lookupEmailInput');
  1311→    const licenseKeyInput = document.getElementById('licenseKeyInput');
  1312→
  1313→    if (!lookupEmailInput) return;
  1314→
  1315→    const email = lookupEmailInput.value.trim().toLowerCase();
  1316→    if (!email) {
  1317→        setStatus('Please enter your email address', true);
  1318→        return;
  1319→    }
  1320→
  1321→    // Basic email validation
  1322→    const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  1323→    if (!emailRegex.test(email)) {
  1324→        setStatus('Please enter a valid email address', true);
  1325→        return;
  1326→    }
  1327→
  1328→    const lookupBtn = document.getElementById('lookupLicenseBtn');
  1329→    if (lookupBtn) {
  1330→        lookupBtn.disabled = true;
  1331→        lookupBtn.textContent = 'Looking up...';
  1332→    }
  1333→
  1334→    try {
  1335→        const response = await fetchWithTimeout(
  1336→            `${getBackendUrl()}/license/lookup`,
  1337→            {
  1338→                method: 'POST',
  1339→                headers: { 'Content-Type': 'application/json' },
  1340→                body: JSON.stringify({ email })
  1341→            },
  1342→            30000
  1343→        );
  1344→
  1345→        const result = await response.json();
  1346→
  1347→        // Note: For security, the backend does NOT return the license key directly.
  1348→        // It sends the key via email to prevent enumeration attacks.
  1349→        if (result.success) {
  1350→            setStatus(result.message || 'If a license exists, it has been sent to your email.');
  1351→        } else {
  1352→            setStatus(result.error || 'Lookup failed. Please try again.', true);
  1353→        }
  1354→    } catch (error) {
  1355→        setStatus('Lookup failed: ' + error.message, true);
  1356→    } finally {
  1357→        if (lookupBtn) {
  1358→            lookupBtn.disabled = false;
  1359→            lookupBtn.textContent = 'Lookup';
  1360→        }
  1361→    }
  1362→}
  1363→
  1364→// ============================================================================
  1365→// CREDITS
  1366→// ============================================================================
  1367→function initCredits() {
  1368→    updateCredits();
  1369→}
  1370→
  1371→async function updateCredits() {
  1372→    if (!ui.creditBadge) return;
  1373→
  1374→    const settings = getSettings();
  1375→    if (!settings.customerId) {
  1376→        ui.creditBadge.className = 'credit-badge login';
  1377→        ui.creditBadge.textContent = 'Login';
  1378→        ui.creditBadge.style.display = 'flex';
  1379→        return;
  1380→    }
  1381→
  1382→    try {
  1383→        const response = await fetchWithTimeout(
  1384→            `${getBackendUrl()}/credits`,
  1385→            {
  1386→                method: 'GET',
  1387→                headers: getAuthHeaders()
  1388→            }
  1389→        );
  1390→
  1391→        if (response.ok) {
  1392→            const data = await response.json();
  1393→            // Backend returns hoursRemaining (not remainingMinutes)
  1394→            const hours = (data.hoursRemaining || 0).toFixed(1);
  1395→            ui.creditBadge.textContent = `${hours}h`;
  1396→            // Low warning when less than 1 hour remaining
  1397→            ui.creditBadge.className = data.hoursRemaining < 1 ? 'credit-badge low' : 'credit-badge ok';
  1398→            ui.creditBadge.style.display = 'flex';
  1399→        } else {
  1400→            ui.creditBadge.className = 'credit-badge error';
  1401→            ui.creditBadge.textContent = 'Error';
  1402→            ui.creditBadge.style.display = 'flex';
  1403→        }
  1404→    } catch {
  1405→        ui.creditBadge.className = 'credit-badge error';
  1406→        ui.creditBadge.textContent = 'Offline';
  1407→        ui.creditBadge.style.display = 'flex';
  1408→    }
  1409→}
  1410→
  1411→// ============================================================================
  1412→// HELP BUTTON
  1413→// ============================================================================
  1414→function initHelpButton() {
  1415→    const helpBtn = document.getElementById('helpBtn');
  1416→    if (helpBtn) {
  1417→        helpBtn.addEventListener('click', () => {
  1418→            setStatus('Silence: removes quiet gaps | Takes: detects repeated content | Chapters: AI topic segmentation');
  1419→        });
  1420→    }
  1421→}
  1422→
  1423→// Export for debugging
  1424→window.spliceDebug = {
  1425→    getState: () => ({
  1426→        silences: previewSilences,
  1427→        takes: previewTakes,
  1428→        chapters: currentChapters,
  1429→        zooms: currentZooms,
  1430→        selected: Array.from(selectedSilenceIndices)
  1431→    }),
