// PLATFORM & ASPECT RATIO SELECTION
// ============================================================================

/**
 * SECURITY: Escape HTML to prevent XSS
 */
function escapeHtmlReframe(str) {
  if (!str) return '';
  const div = document.createElement('div');
  div.textContent = str;
  return div.innerHTML;
}

/**
 * Render platform selector buttons
 */
function renderPlatformSelector() {
  const container = reframeElements.platformSelector || document.getElementById('reframe-platform-selector');
  if (!container) return;

  // SECURITY: Escape platform data to prevent XSS
  const html = socialReframeState.platforms.map(platform => {
    const safeId = escapeHtmlReframe(platform.id);
    const safeName = escapeHtmlReframe(platform.name);
    return `
    <button class="reframe-platform-btn ${socialReframeState.selectedPlatform === platform.id ? 'selected' : ''}"
            data-platform-id="${safeId}"
            aria-label="Select ${safeName}">
      <span class="platform-icon platform-icon-${safeId}"></span>
      <span class="platform-name">${safeName}</span>
    </button>
  `;
  }).join('');

  container.innerHTML = html;

  // Also render aspect ratio buttons
  renderAspectRatioSelector();
}

