  // Music credits functions
  getMusicCredits,
  hasMusicCredits,
  deductMusicCredit,
  resetMusicCredits,
  addMusicCredits,
  getMusicUsageHistory,
  initMusicCreditsColumns,
  // Variations credits functions
  checkVariationsCredits,
  deductVariationsCredit,
  // Scene-aware credits functions
  checkSceneAwareCredits,
  deductSceneAwareCredit,
  // Pool management
  closePool,
  checkDatabase,
  // Trial management
  updateTrialEnd,
  // Constants
  TIER_HOURS,
  TIER_ISOLATION_MINUTES,
  TIER_ISOLATION_HOURS,
  ISOLATION_OVERAGE_RATE,
  HOURS_OVERAGE_RATE,
  TIER_MUSIC_CREDITS,
  MUSIC_OVERAGE_RATE,
  MUSIC_OVERAGE_RATES,
  VARIATIONS_CREDIT_COST,
  VARIATIONS_OVERAGE_RATES,
  SCENE_AWARE_CREDIT_COST,
  SCENE_AWARE_OVERAGE_RATES,
  // Feature access
  TIER_FEATURES,
  hasFeatureAccess
};

