            14-day free trial on all plans
          </Badge>
          <h2 className="text-3xl md:text-4xl font-bold mb-4 text-white">
            Simple, Transparent{" "}
            <span className="gradient-text">Pricing</span>
          </h2>
          <p className="text-lg text-gray-400 max-w-2xl mx-auto mb-6">
            No hidden fees. No credit card required for trial. Cancel anytime.
          </p>

          {/* Countdown timer */}
          <div className="mb-8">
            <p className="text-sm text-gray-500 mb-2">Beta pricing expires in:</p>
            <UrgencyCountdown />
          </div>

          {/* Social proof */}
          <div className="flex flex-wrap items-center justify-center gap-6 text-sm text-gray-400">
            <div className="flex items-center gap-2">
              <Users className="w-4 h-4 text-[#00D4FF]" />
              <span><span className="text-white font-semibold">2,847</span> editors joined this week</span>
            </div>
            <div className="flex items-center gap-2">
              <TrendingUp className="w-4 h-4 text-green-400" />
              <span><span className="text-white font-semibold">94%</span> of users upgrade after trial</span>
            </div>
          </div>
        </motion.div>

        {/* Pricing cards */}
        <div className="grid md:grid-cols-3 gap-8 max-w-6xl mx-auto">
          {plans.map((plan, index) => (
            <motion.div
              key={plan.name}
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.5, delay: index * 0.1 }}
              className={plan.popular ? "md:-mt-4 md:mb-4" : ""}
            >
              <Card
                className={`h-full relative bg-[#0d1420] ${
                  plan.popular
                    ? "border-[#00D4FF] shadow-lg shadow-[#00D4FF]/20"
