"use client";

import { motion } from "framer-motion";

const stats = [
  { value: "10,000+", label: "Editors" },
  { value: "500K+", label: "Hours Saved" },
  { value: "4.9/5", label: "Rating" },
];

const logos = [
  { name: "YouTube", className: "text-red-500" },
  { name: "TikTok", className: "text-white" },
  { name: "Netflix", className: "text-red-600" },
  { name: "Spotify", className: "text-green-500" },
  { name: "Adobe", className: "text-red-500" },
];

export function SocialProof() {
  return (
    <section className="py-12 border-y border-gray-800 bg-[#0a0f1a] dark">
      <div className="container mx-auto px-4">
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.5 }}
          className="text-center mb-8"
        >
          <p className="text-sm text-gray-400">
            Trusted by video editors at
          </p>
        </motion.div>

        {/* Logo strip */}
        <motion.div
          initial={{ opacity: 0 }}
          whileInView={{ opacity: 1 }}
          viewport={{ once: true }}
          transition={{ duration: 0.5, delay: 0.2 }}
          className="flex flex-wrap items-center justify-center gap-8 md:gap-12 mb-10"
        >
          {logos.map((logo) => (
            <div
              key={logo.name}
              className={`text-xl font-bold opacity-50 hover:opacity-100 transition-opacity ${logo.className}`}
            >
              {logo.name}
            </div>
          ))}
        </motion.div>

        {/* Stats */}
        <motion.div
          initial={{ opacity: 0, y: 20 }}
          whileInView={{ opacity: 1, y: 0 }}
          viewport={{ once: true }}
          transition={{ duration: 0.5, delay: 0.3 }}
          className="grid grid-cols-3 gap-8 max-w-2xl mx-auto"
        >
          {stats.map((stat) => (
            <div key={stat.label} className="text-center">
              <div className="text-2xl md:text-3xl font-bold text-[#00D4FF]">
                {stat.value}
              </div>
              <div className="text-sm text-gray-400 mt-1">
                {stat.label}
              </div>
            </div>
          ))}
        </motion.div>
      </div>
    </section>
  );
}

