   492→
   493→async function runDetection() {
   494→    if (isOperationInProgress) return;
   495→
   496→    // Check sequence
   497→    const hasSequence = await checkSequenceOpen();
   498→    if (!hasSequence) {
   499→        setStatus('Please open a sequence first', true);
   500→        return;
   501→    }
   502→
   503→    // Check online
   504→    if (!isOnline()) {
   505→        setStatus('Offline - Check your connection', true);
   506→        return;
   507→    }
   508→
   509→    isOperationInProgress = true;
   510→    ui.goBtn.disabled = true;
   511→    showProgress('Detecting silences...');
   512→
   513→    try {
   514→        const settings = getSettings();
   515→
   516→        // Get sequence info
   517→        const seqInfo = await jsx.call('getActiveSequence');
   518→        console.log('[SPLICE] Sequence info:', seqInfo);
   519→
   520→        // Store sequence info for later use
   521→        currentSequenceInfo = seqInfo;
   522→        window.spliceState.activeSequence = seqInfo;
   523→        if (seqInfo && seqInfo.videoPath) {
   524→            window.spliceState.currentVideoPath = seqInfo.videoPath;
   525→        }
   526→
   527→        // Export audio via JSX for silence detection
   528→        updateProgress(10, 'Exporting audio...');
   529→
   530→        let audioFilePath = null;
   531→        try {
   532→            // Get sequence info for audio export
   533→            const exportResult = await jsx.call('exportSequenceAudioForAnalysis');
   534→            if (exportResult && exportResult.success && exportResult.outputPath) {
   535→                audioFilePath = exportResult.outputPath;
   536→                console.log('[SPLICE] Audio exported to:', audioFilePath);
   537→            } else if (exportResult && exportResult.error) {
   538→                throw new Error(exportResult.error);
   539→            }
   540→        } catch (exportError) {
   541→            console.warn('[SPLICE] Audio export failed, trying fallback:', exportError.message);
   542→            // Try to get audio from first clip in timeline
   543→            try {
   544→                const clipInfo = await jsx.call('getFirstClipAudioPath');
   545→                if (clipInfo && clipInfo.path) {
   546→                    audioFilePath = clipInfo.path;
   547→                    console.log('[SPLICE] Using clip audio path:', audioFilePath);
   548→                }
   549→            } catch (clipError) {
   550→                console.warn('[SPLICE] Fallback audio path failed:', clipError.message);
   551→            }
   552→        }
   553→
   554→        if (!audioFilePath) {
   555→            throw new Error('Could not export or locate audio file. Please ensure sequence has audio tracks.');
   556→        }
   557→
   558→        // Store audio path globally for other modules
   559→        currentAudioPath = audioFilePath;
   560→        window.spliceState.audioPath = audioFilePath;
   561→
   562→        // Call backend for silence detection with audio file path
   563→        updateProgress(30, 'Detecting silences...');
   564→        const silenceResponse = await fetchWithTimeout(
   565→            `${getBackendUrl()}/silences-rms`,
   566→            {
   567→                method: 'POST',
   568→                headers: getAuthHeaders(),
   569→                body: JSON.stringify({
   570→                    wavPath: audioFilePath,
   571→                    sensitivity: settings.sensitivity,
   572→                    minSilenceLength: settings.minSilenceLength || 0.5
   573→                })
   574→            }
   575→        );
   576→
   577→        if (!silenceResponse.ok) {
   578→            throw new Error(await parseErrorResponse(silenceResponse));
   579→        }
   580→
   581→        const silenceData = await silenceResponse.json();
   582→        previewSilences = silenceData.silences || [];
   583→
   584→        // Detect takes if enabled
   585→        if (settings.enableTakesDetection) {
   586→            updateProgress(60, 'Detecting takes...');
   587→            const analyzeResponse = await fetchWithTimeout(
   588→                `${getBackendUrl()}/analyze`,
   589→                {
   590→                    method: 'POST',
   591→                    headers: getAuthHeaders(),
   592→                    body: JSON.stringify({
   593→                        wavPath: audioFilePath,
   594→                        detectTakes: true
   595→                    })
   596→                }
   597→            );
   598→
   599→            if (analyzeResponse.ok) {
   600→                const analyzeData = await analyzeResponse.json();
   601→                previewTakes = analyzeData.takes || [];
   602→
   603→                // Store transcript for captions/music modules
   604→                if (analyzeData.transcript) {
   605→                    currentTranscript = analyzeData.transcript;
   606→                    window.currentTranscript = analyzeData.transcript;
   607→                    window.spliceState.lastTranscript = analyzeData.transcript;
   608→                }
   609→            }
   610→        }
   611→
   612→        // Detect chapters if enabled
   613→        if (settings.enableChapters) {
   614→            updateProgress(80, 'Detecting chapters...');
   615→            try {
   616→                const chapterResponse = await fetchWithTimeout(
   617→                    `${getBackendUrl()}/chapters`,
   618→                    {
   619→                        method: 'POST',
   620→                        headers: getAuthHeaders(),
   621→                        body: JSON.stringify({
   622→                            wavPath: audioFilePath,
   623→                            transcript: currentTranscript,
   624→                            maxChapters: settings.maxChapters || 10,
   625→                            minChapterLength: settings.minChapterLength || 60
   626→                        })
   627→                    }
   628→                );
   629→
   630→                if (chapterResponse.ok) {
   631→                    const chapterData = await chapterResponse.json();
   632→                    currentChapters = chapterData.chapters || [];
   633→                }
   634→            } catch (e) {
   635→                console.warn('[SPLICE] Chapter detection failed:', e);
   636→            }
   637→        }
   638→
   639→        // Detect zoom points if enabled
   640→        if (settings.enableZoom) {
   641→            updateProgress(90, 'Detecting zoom points...');
   642→            try {
   643→                const zoomResponse = await fetchWithTimeout(
   644→                    `${getBackendUrl()}/zoom`,
   645→                    {
   646→                        method: 'POST',
   647→                        headers: getAuthHeaders(),
   648→                        body: JSON.stringify({
   649→                            wavPath: audioFilePath,
   650→                            transcript: currentTranscript,
   651→                            frequency: settings.zoomFrequency || 'medium',
   652→                            preset: settings.zoomPreset || 'medium',
   653→                            placement: settings.zoomPlacement || 'sentence_start'
   654→                        })
   655→                    }
   656→                );
   657→
   658→                if (zoomResponse.ok) {
   659→                    const zoomData = await zoomResponse.json();
   660→                    currentZooms = zoomData.zoomPoints || [];
   661→                }
   662→            } catch (e) {
   663→                console.warn('[SPLICE] Zoom detection failed:', e);
   664→            }
   665→        }
   666→
   667→        // Store results
   668→        lastDetectionResult = {
   669→            silences: previewSilences,
   670→            takes: previewTakes,
   671→            chapters: currentChapters,
   672→            zooms: currentZooms
   673→        };
   674→
   675→        // Show preview
   676→        hideProgress();
   677→        showCombinedPreview();
   678→        setStatus(`Found ${previewSilences.length} silences, ${previewTakes.length} takes`);
   679→
   680→    } catch (error) {
   681→        console.error('[SPLICE] Detection error:', error);
   682→        setStatus('Detection failed: ' + error.message, true);
   683→        hideProgress();
   684→    } finally {
   685→        isOperationInProgress = false;
   686→        ui.goBtn.disabled = false;
   687→    }
   688→}
