   750→        const duration = silence.end - silence.start;
   751→        html.push(`
   752→            <div class="preview-item ${isSelected ? '' : 'excluded'}" data-index="${i}" data-type="silence">
   753→                <input type="checkbox" class="preview-item-check" ${isSelected ? 'checked' : ''}>
   754→                <div class="preview-item-info">
   755→                    <div class="preview-item-time">${formatTime(silence.start)} - ${formatTime(silence.end)}</div>
   756→                    <div class="preview-item-duration">${duration.toFixed(2)}s silence</div>
   757→                </div>
   758→            </div>
   759→        `);
   760→    });
   761→
   762→    // Render takes
   763→    previewTakes.forEach((take, i) => {
   764→        // SECURITY: Escape take label to prevent XSS
   765→        const safeLabel = escapeHtml(take.label || 'Take ' + (take.takeNumber || i + 1));
   766→        html.push(`
   767→            <div class="preview-item take-item" data-index="${i}" data-type="take">
