   918→        for (const take of previewTakes) {
   919→            await jsx.call('createMarker', take.start, take.label || `Take ${take.takeNumber}`, take.end - take.start, null, 5);
   920→        }
   921→
   922→        setStatus(`Added ${selectedSilences.length + previewTakes.length} markers`);
   923→    } catch (error) {
   924→        setStatus('Failed to add markers: ' + error.message, true);
   925→    } finally {
