"use client";

import { Card, CardContent, CardHeader, CardTitle } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Separator } from "@/components/ui/separator";
import { motion } from "framer-motion";
import {
  Download,
  Monitor,
  CheckCircle,
  AlertCircle,
  ArrowRight,
  Apple,
  MonitorCog,
} from "lucide-react";
import Link from "next/link";
import { Header } from "@/components/landing/Header";
import { Footer } from "@/components/landing/Footer";
import { useState, useEffect } from "react";

interface InstallStep {
  step: number;
  title: string;
  description: string;
}

interface DownloadOption {
  name: string;
  version: string;
  type: string;
  description: string;
  compatibility: string;
  badge: string;
  badgeColor: string;
  features: string[];
  downloadUrlMac: string;
  downloadUrlWindows: string;
  highlight: boolean;
}

const downloads: DownloadOption[] = [
  {
    name: "SPLICE for UXP",
    version: "v6.0.3",
    type: "Modern",
    description: "For Premiere Pro 25.6 and newer. Uses Adobe's modern UXP architecture.",
    compatibility: "Premiere Pro 25.6+",
    badge: "Recommended",
    badgeColor: "bg-[#00D4FF] text-black",
    features: [
      "Faster performance with native APIs",
      "Better stability and memory management",
      "Future-proof architecture",
      "Full feature parity with CEP",
    ],
    downloadUrlMac: "/downloads/SPLICE-v6.0.3.ccx",
    downloadUrlWindows: "/downloads/SPLICE-v6.0.3.ccx",
    highlight: true,
  },
  {
    name: "SPLICE for CEP",
    version: "v6.0.3",
    type: "Legacy",
    description: "For Premiere Pro 14.0 to 25.9. Uses Adobe's legacy CEP architecture.",
    compatibility: "Premiere Pro 14.0 - 25.9",
    badge: "Wide Compatibility",
    badgeColor: "bg-gray-700 text-white",
    features: [
      "Works with older Premiere Pro versions",
      "Proven stability (millions of installs)",
      "Full feature set available",
      "Best for mixed version teams",
    ],
    downloadUrlMac: "/downloads/splice-v6.0.3.zxp",
    downloadUrlWindows: "/downloads/SPLICE-Installer-6.0.3.exe",
    highlight: false,
  },
];

const installStepsUXP: Record<string, InstallStep[]> = {
  mac: [
    {
      step: 1,
      title: "Download the CCX file",
      description: "Click the download button above to get the Creative Cloud plugin (.ccx).",
    },
    {
      step: 2,
      title: "Double-click to install",
      description: "Double-click the .ccx file. Creative Cloud Desktop will open automatically and prompt you to install.",
    },
    {
      step: 3,
      title: "Click Install in Creative Cloud",
      description: "Click 'Install' when prompted. The plugin will be automatically installed for Premiere Pro.",
    },
    {
      step: 4,
      title: "Open SPLICE in Premiere Pro",
      description: "Launch Premiere Pro and go to Window → Extensions → SPLICE. Enter your license key to activate.",
    },
  ],
  windows: [
    {
      step: 1,
      title: "Download the CCX file",
      description: "Click the download button above to get the Creative Cloud plugin (.ccx).",
    },
    {
      step: 2,
      title: "Double-click to install",
      description: "Double-click the .ccx file. Creative Cloud Desktop will open automatically and prompt you to install.",
    },
    {
      step: 3,
      title: "Click Install in Creative Cloud",
      description: "Click 'Install' when prompted. The plugin will be automatically installed for Premiere Pro.",
    },
    {
      step: 4,
      title: "Open SPLICE in Premiere Pro",
      description: "Launch Premiere Pro and go to Window → Extensions → SPLICE. Enter your license key to activate.",
    },
  ],
};

const installStepsCEP: Record<string, InstallStep[]> = {
  mac: [
    {
      step: 1,
      title: "Get a FREE ZXP Installer (one-time)",
      description: "Download Anastasiy's Extension Manager from install.anastasiy.com — it's free and takes 30 seconds to install.",
    },
    {
      step: 2,
      title: "Download SPLICE",
      description: "Click the download button above to get splice-v6.0.3.zxp (or the ZIP if your email blocked the ZXP).",
    },
    {
      step: 3,
      title: "Drag & Drop to Install",
      description: "Open Anastasiy's Extension Manager and drag the .zxp file onto it. Done! SPLICE is now installed.",
    },
    {
      step: 4,
      title: "Open in Premiere Pro",
      description: "Launch Premiere Pro → Window → Extensions → SPLICE. Enter your license key to activate.",
    },
  ],
  windows: [
    {
      step: 1,
      title: "Download the installer",
      description: "Click the download button above to get the Windows installer (.exe).",
    },
    {
      step: 2,
      title: "Run the installer",
      description: "Double-click the downloaded .exe file. If SmartScreen appears, click 'More info' then 'Run anyway'.",
    },
    {
      step: 3,
      title: "Follow the prompts",
      description: "Click through the installer. Accept the license agreement and choose your installation location.",
    },
    {
      step: 4,
      title: "Open SPLICE in Premiere Pro",
      description: "Launch Premiere Pro and go to Window → Extensions → SPLICE. Enter your license key to activate.",
    },
  ],
};

// Keep backward compatibility - default to CEP steps
const installSteps = installStepsCEP;

export default function DownloadPage() {
  const [selectedOS, setSelectedOS] = useState<"mac" | "windows">("mac");
  const [selectedPlugin, setSelectedPlugin] = useState<"uxp" | "cep">("uxp");

  // Auto-detect OS on mount
  useEffect(() => {
    if (typeof window !== "undefined") {
      const userAgent = window.navigator.userAgent.toLowerCase();
      if (userAgent.includes("win")) {
        setSelectedOS("windows");
      } else {
        setSelectedOS("mac");
      }
    }
  }, []);

  const getCurrentSteps = () => {
    if (selectedPlugin === "uxp") {
      return installStepsUXP[selectedOS];
    }
    return installStepsCEP[selectedOS];
  };

  const currentSteps = getCurrentSteps();

  const getDownloadUrl = (download: DownloadOption) => {
    return selectedOS === "mac" ? download.downloadUrlMac : download.downloadUrlWindows;
  };

  const getFileExtension = (download: DownloadOption) => {
    if (download.name.includes("UXP")) {
      return ".ccx";
    }
    return selectedOS === "mac" ? ".zxp" : ".exe";
  };

  return (
    <div className="min-h-screen bg-[#060b14] dark">
      <Header />
      <main className="pt-24">
        {/* Hero section */}
        <section className="py-16 bg-[#0a0f1a]">
          <div className="container mx-auto px-4">
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.5 }}
              className="text-center"
            >
              <div className="w-16 h-16 rounded-xl bg-[#00D4FF]/10 flex items-center justify-center mx-auto mb-4">
                <Download className="w-8 h-8 text-[#00D4FF]" />
              </div>
              <h1 className="text-4xl md:text-5xl font-bold mb-4 text-white">
                Download <span className="gradient-text">SPLICE</span>
              </h1>
              <p className="text-lg text-gray-400 max-w-2xl mx-auto mb-6">
                Choose the version that matches your Premiere Pro installation.
                Both versions have the same features.
              </p>

              {/* OS Selector */}
              <div className="flex items-center gap-2 justify-center">
                <span className="text-sm text-gray-400">Downloading for:</span>
                <div className="inline-flex rounded-lg border border-gray-700 p-1 bg-[#0d1420]">
                  <button
                    className={`px-4 py-2 rounded-md text-sm font-medium transition-colors flex items-center gap-2 ${selectedOS === "mac"
                      ? "bg-[#00D4FF] text-black"
                      : "text-gray-300 hover:bg-white/10"
                      }`}
                    onClick={() => setSelectedOS("mac")}
                  >
                    <Apple className="w-4 h-4" />
                    macOS
                  </button>
                  <button
                    className={`px-4 py-2 rounded-md text-sm font-medium transition-colors flex items-center gap-2 ${selectedOS === "windows"
                      ? "bg-[#00D4FF] text-black"
                      : "text-gray-300 hover:bg-white/10"
                      }`}
                    onClick={() => setSelectedOS("windows")}
                  >
                    <MonitorCog className="w-4 h-4" />
                    Windows
                  </button>
                </div>
              </div>
            </motion.div>
          </div>
        </section>

        {/* Download options */}
        <section className="py-16">
          <div className="container mx-auto px-4">
            <div className="grid md:grid-cols-2 gap-8 max-w-4xl mx-auto">
              {downloads.map((download, index) => (
                <motion.div
                  key={download.name}
                  initial={{ opacity: 0, y: 20 }}
                  animate={{ opacity: 1, y: 0 }}
                  transition={{ duration: 0.5, delay: index * 0.1 }}
                >
                  <Card
                    className={`h-full bg-[#0d1420] ${download.highlight
                      ? "border-[#00D4FF] shadow-lg shadow-[#00D4FF]/10"
                      : "border-gray-800"
                      }`}
                  >
                    <CardHeader>
                      <div className="flex items-start justify-between">
                        <div>
                          <Badge className={download.badgeColor}>
                            {download.badge}
                          </Badge>
                          <CardTitle className="mt-2 text-white">{download.name}</CardTitle>
                        </div>
                        <div className="text-right">
                          <div className="font-mono text-sm text-white">{download.version}</div>
                          <div className="text-xs text-gray-400">
                            {download.type}
                          </div>
                        </div>
                      </div>
                      <p className="text-sm text-gray-400 mt-2">
                        {download.description}
                      </p>
                    </CardHeader>
                    <CardContent>
                      <div className="mb-4">
                        <div className="flex items-center gap-2 text-sm font-medium mb-2 text-white">
                          <Monitor className="w-4 h-4 text-[#00D4FF]" />
                          {download.compatibility}
                        </div>
                      </div>

                      <ul className="space-y-2 mb-6">
                        {download.features.map((feature) => (
                          <li key={feature} className="flex items-start gap-2 text-sm text-gray-300">
                            <CheckCircle className="w-4 h-4 text-[#00D4FF] shrink-0 mt-0.5" />
                            {feature}
                          </li>
                        ))}
                      </ul>

                      <div className="flex flex-col gap-3">
                        <Button
                          className={`w-full ${download.highlight
                            ? "bg-[#00D4FF] hover:bg-[#00B8E6] text-black font-semibold"
                            : "border-gray-700 text-white hover:bg-white/10"
                            }`}
                          variant={download.highlight ? "default" : "outline"}
                          asChild
                        >
                          <Link href="/checkout?plan=pro">
                            Start Free Trial
                          </Link>
                        </Button>
                        <div className="text-center space-y-1">
                          <a
                            href={getDownloadUrl(download)}
                            download
                            className="inline-flex items-center gap-2 text-sm text-gray-400 hover:text-[#00D4FF] transition-colors"
                            onClick={() => setSelectedPlugin(download.name.includes("UXP") ? "uxp" : "cep")}
                          >
                            <Download className="w-4 h-4" />
                            Download {getFileExtension(download)} for {selectedOS === "mac" ? "macOS" : "Windows"}
                          </a>
                          {download.name.includes("CEP") && selectedOS === "mac" && (
                            <div>
                              <a
                                href="/downloads/SPLICE-v6.0.3-macOS.zip"
                                download
                                className="inline-flex items-center gap-2 text-xs text-gray-500 hover:text-gray-300 transition-colors"
                              >
                                <Download className="w-3 h-3" />
                                ZIP version (for email sharing)
                              </a>
                            </div>
                          )}
                        </div>
                      </div>
                    </CardContent>
                  </Card>
                </motion.div>
              ))}
            </div>
          </div>
        </section>

        {/* Installation guide */}
        <section className="py-16 bg-[#0a0f1a]">
          <div className="container mx-auto px-4">
            <motion.div
              initial={{ opacity: 0, y: 20 }}
              whileInView={{ opacity: 1, y: 0 }}
              viewport={{ once: true }}
              transition={{ duration: 0.5 }}
              className="max-w-3xl mx-auto"
            >
              <div className="text-center mb-8">
                <h2 className="text-2xl md:text-3xl font-bold mb-2 text-white">
                  Installation Guide
                </h2>
                <p className="text-gray-400">
                  Simple 4-step installation — no Terminal or Registry edits required
                </p>
              </div>

              {/* Steps */}
              <Card className="bg-[#0d1420] border-gray-800">
                <CardContent className="p-6">
                  <div className="space-y-6">
                    {currentSteps.map((step, index) => (
                      <div key={step.step}>
                        <div className="flex items-start gap-4">
                          <div className="w-8 h-8 rounded-full bg-[#00D4FF] flex items-center justify-center text-black font-bold shrink-0">
                            {step.step}
                          </div>
                          <div className="flex-1">
                            <h3 className="font-semibold mb-1 text-white">{step.title}</h3>
                            <p className="text-sm text-gray-400">
                              {step.description}
                            </p>
                          </div>
                        </div>
                        {index < currentSteps.length - 1 && (
                          <Separator className="my-4 ml-12 bg-gray-800" />
                        )}
                      </div>
                    ))}
                  </div>
                </CardContent>
              </Card>

              {/* Security note */}
              <div className="mt-8 bg-amber-500/10 border border-amber-500/20 rounded-lg p-4 flex items-start gap-3">
                <AlertCircle className="w-5 h-5 text-amber-500 shrink-0 mt-0.5" />
                <div>
                  <h4 className="font-medium mb-1 text-white">
                    {selectedPlugin === "uxp" ? "CCX Installation" : selectedOS === "mac" ? "Need a ZXP Installer? (Free)" : "Windows SmartScreen"}
                  </h4>
                  <p className="text-sm text-gray-400">
                    {selectedPlugin === "uxp" ? (
                      <>
                        When you double-click the .ccx file, Creative Cloud will show an &quot;Install Plugin&quot; dialog.
                        Click &quot;Install&quot; to proceed. No additional tools needed!
                      </>
                    ) : selectedOS === "mac" ? (
                      <>
                        <strong className="text-white">Step 1:</strong> Download{" "}
                        <a href="https://install.anastasiy.com" target="_blank" rel="noopener noreferrer" className="text-[#00D4FF] hover:underline font-medium">
                          Anastasiy&apos;s Extension Manager
                        </a>{" "}
                        (free, 30 seconds to install)
                        <br />
                        <strong className="text-white">Step 2:</strong> Drag the .zxp file onto it — done!
                        <br />
                        <span className="text-gray-500 text-xs mt-1 block">
                          Alternative:{" "}
                          <a href="https://aescripts.com/learn/zxp-installer/" target="_blank" rel="noopener noreferrer" className="text-gray-400 hover:text-[#00D4FF]">
                            aescripts ZXP Installer
                          </a>
                        </span>
                      </>
                    ) : (
                      <>
                        If Windows SmartScreen appears, click &quot;More info&quot; then &quot;Run anyway&quot;.
                        This happens because we&apos;re a new publisher.
                      </>
                    )}
                  </p>
                </div>
              </div>

              {/* Help callout */}
              <div className="mt-4 bg-[#00D4FF]/10 border border-[#00D4FF]/20 rounded-lg p-4 flex items-start gap-3">
                <AlertCircle className="w-5 h-5 text-[#00D4FF] shrink-0 mt-0.5" />
                <div>
                  <h4 className="font-medium mb-1 text-white">Need help installing?</h4>
                  <p className="text-sm text-gray-400">
                    Check out our{" "}
                    <Link href="/docs/installation" className="text-[#00D4FF] hover:underline">
                      detailed installation guide
                    </Link>{" "}
                    or{" "}
                    <a
                      href="mailto:support@splice.app"
                      className="text-[#00D4FF] hover:underline"
                    >
                      contact support
                    </a>
                    .
                  </p>
                </div>
              </div>
            </motion.div>
          </div>
        </section>

        {/* CTA */}
        <section className="py-16 bg-[#060b14]">
          <div className="container mx-auto px-4 text-center">
            <h2 className="text-2xl font-bold mb-4 text-white">
              Don&apos;t have a license yet?
            </h2>
            <p className="text-gray-400 mb-6">
              Start your 14-day free trial with full access to all features.
            </p>
            <Button
              size="lg"
              className="bg-[#00D4FF] hover:bg-[#00B8E6] text-black font-semibold btn-glow"
              asChild
            >
              <Link href="/pricing">
                View Pricing
                <ArrowRight className="w-4 h-4 ml-2" />
              </Link>
            </Button>
          </div>
        </section>
      </main>
      <Footer />
    </div>
  );
}

"use client";

import { motion } from "framer-motion";
import { Card, CardContent } from "@/components/ui/card";
import { Button } from "@/components/ui/button";
import { Badge } from "@/components/ui/badge";
import { Tabs, TabsContent, TabsList, TabsTrigger } from "@/components/ui/tabs";
import {
  ArrowLeft,
  ArrowRight,
  Check,
  Copy,
  CheckCircle,
  AlertTriangle,
  Monitor,
  Apple,
  Download,
  Settings,
  Zap,
} from "lucide-react";
import Link from "next/link";
import { Header } from "@/components/landing/Header";
import { Footer } from "@/components/landing/Footer";
import { useState } from "react";

const requirements = {
  uxp: {
    title: "UXP Version (Preview)",
    items: [
      "Adobe Premiere Pro 25.6 or newer",
      "macOS 12+ or Windows 10/11",
      "8GB RAM minimum (16GB recommended)",
      "Active internet connection for license activation",
    ],
  },
  cep: {
    title: "CEP Version (Recommended)",
    items: [
      "Adobe Premiere Pro 14.0 - 25.9",
      "macOS 10.15+ or Windows 10/11",
      "8GB RAM minimum (16GB recommended)",
      "Active internet connection for license activation",
    ],
  },
};

export default function InstallationPage() {
  const [copiedCommand, setCopiedCommand] = useState<string | null>(null);

  const copyToClipboard = (text: string, id: string) => {
    navigator.clipboard.writeText(text);
    setCopiedCommand(id);
    setTimeout(() => setCopiedCommand(null), 2000);
  };

  return (
    <div className="min-h-screen bg-[#060b14] dark">
      <Header />
      <main className="pt-24 pb-16">
        <div className="container mx-auto px-4 max-w-4xl">
          {/* Back link */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.3 }}
          >
            <Link href="/docs" className="inline-flex items-center text-gray-400 hover:text-[#00D4FF] mb-8">
              <ArrowLeft className="w-4 h-4 mr-2" />
              Back to Documentation
            </Link>
          </motion.div>

          {/* Hero */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5 }}
            className="mb-12"
          >
            <div className="flex items-center gap-3 mb-4">
              <div className="w-12 h-12 rounded-xl bg-[#00D4FF]/10 flex items-center justify-center">
                <Settings className="w-6 h-6 text-[#00D4FF]" />
              </div>
              <Badge className="bg-[#00D4FF]/10 text-[#00D4FF] border-[#00D4FF]/30">
                Setup Guide
              </Badge>
            </div>
            <h1 className="text-4xl md:text-5xl font-bold mb-4 text-white">
              Installation Guide
            </h1>
            <p className="text-lg text-gray-400">
              Complete instructions for installing SPLICE on your system. We recommend the CEP version for maximum compatibility and feature set.
            </p>
          </motion.div>

          {/* Version Selection */}
          <motion.div
            initial={{ opacity: 0, y: 20 }}
            animate={{ opacity: 1, y: 0 }}
            transition={{ duration: 0.5, delay: 0.1 }}
          >
            <Tabs defaultValue="cep" className="mb-12">
              <TabsList className="grid w-full grid-cols-2 bg-[#0d1420] border border-gray-800">
                <TabsTrigger value="cep" className="data-[state=active]:bg-[#00D4FF] data-[state=active]:text-black">
                  <Monitor className="w-4 h-4 mr-2" />
                  CEP (Recommended)
                </TabsTrigger>
                <TabsTrigger value="uxp" className="data-[state=active]:bg-[#00D4FF] data-[state=active]:text-black">
                  <Zap className="w-4 h-4 mr-2" />
                  UXP (Preview)
                </TabsTrigger>
              </TabsList>

              {/* UXP Installation */}
              <TabsContent value="uxp" className="mt-8">
                {/* Requirements */}
                <Card className="bg-[#0d1420] border-gray-800 mb-8">
                  <CardContent className="p-6">
                    <h3 className="text-lg font-semibold text-white mb-4 flex items-center gap-2">
                      <Monitor className="w-5 h-5 text-[#00D4FF]" />
                      {requirements.uxp.title}
                    </h3>
                    <ul className="space-y-2">
                      {requirements.uxp.items.map((item, i) => (
                        <li key={i} className="flex items-center gap-2 text-gray-300">
                          <Check className="w-4 h-4 text-[#00D4FF]" />
                          {item}
                        </li>
                      ))}
                    </ul>
                  </CardContent>
                </Card>

                {/* macOS Steps */}
                <h3 className="text-xl font-semibold text-white mb-4 flex items-center gap-2">
                  <Apple className="w-5 h-5" />
                  macOS Installation
                </h3>
                <div className="space-y-4 mb-8">
                  {[
                    {
                      step: 1,
                      title: "Download SPLICE UXP",
                      description: "Download the latest UXP version from the downloads page.",
                      action: { label: "Download", href: "/download" },
                    },
                    {
                      step: 2,
                      title: "Extract the ZIP file",
                      description: "Extract the downloaded ZIP file to a permanent location, such as your Applications folder or Documents.",
                    },
                    {
                      step: 3,
                      title: "Open UXP Developer Tool",
                      description: "In Premiere Pro, go to Window > Extensions > UXP Developer Tool. If you don't see it, download it from Adobe Exchange.",
                    },
                    {
                      step: 4,
                      title: "Load the Plugin",
                      description: 'In UXP Developer Tool, click the "Add" button and select the extracted SPLICE folder. The manifest.json file should be in the root.',
                    },
                    {
                      step: 5,
                      title: "Launch SPLICE",
                      description: "Go to Window > Extensions > SPLICE to open the panel. Enter your license key to activate.",
                    },
                  ].map((item) => (
                    <Card key={item.step} className="bg-[#0d1420] border-gray-800">
                      <CardContent className="p-4">
                        <div className="flex gap-4">
                          <div className="w-8 h-8 rounded-full bg-[#00D4FF] flex items-center justify-center text-black font-bold shrink-0">
                            {item.step}
                          </div>
                          <div className="flex-1">
                            <h4 className="font-semibold text-white mb-1">{item.title}</h4>
                            <p className="text-sm text-gray-400">{item.description}</p>
                            {item.action && (
                              <Button size="sm" className="mt-3 bg-[#00D4FF]/10 text-[#00D4FF] hover:bg-[#00D4FF]/20" asChild>
                                <Link href={item.action.href}>
                                  <Download className="w-4 h-4 mr-2" />
                                  {item.action.label}
                                </Link>
                              </Button>
                            )}
                          </div>
                        </div>
                      </CardContent>
                    </Card>
                  ))}
                </div>

                {/* Windows Steps */}
                <h3 className="text-xl font-semibold text-white mb-4 flex items-center gap-2">
                  <Monitor className="w-5 h-5" />
                  Windows Installation
                </h3>
                <div className="space-y-4">
                  {[
                    {
                      step: 1,
                      title: "Download SPLICE UXP",
                      description: "Download the latest UXP version from the downloads page.",
                    },
                    {
                      step: 2,
                      title: "Extract to a folder",
                      description: "Extract the ZIP file to a folder like C:\\Program Files\\SPLICE or your Documents folder.",
                    },
                    {
                      step: 3,
                      title: "Open UXP Developer Tool",
