import { Metadata } from "next";
import { Header } from "@/components/landing/Header";
import { Footer } from "@/components/landing/Footer";
import { Badge } from "@/components/ui/badge";

export const metadata: Metadata = {
  title: "Changelog - SPLICE",
  description: "See what's new in SPLICE - release notes and updates.",
};

type ChangeType = "new" | "improved" | "fixed" | "deprecated";

interface Change {
  type: ChangeType;
  text: string;
}

interface Release {
  version: string;
  date: string;
  type: "major" | "minor";
  changes: Change[];
}

const releases: Release[] = [
  {
    version: "5.6.0",
    date: "December 27, 2024",
    type: "major" as const,
    changes: [
      { type: "new", text: "AI-powered music generation with scene-aware mode" },
      { type: "new", text: "Batch processing for multiple sequences" },
      { type: "improved", text: "50% faster silence detection algorithm" },
      { type: "improved", text: "Better handling of background noise" },
      { type: "fixed", text: "Memory leak when processing long sequences" },
      { type: "fixed", text: "Crash on macOS Sonoma with certain audio formats" },
    ],
  },
  {
    version: "5.5.0",
    date: "December 1, 2024",
    type: "minor" as const,
    changes: [
      { type: "new", text: "Profanity detection for automatic bleeping" },
      { type: "new", text: "Export presets for common workflows" },
      { type: "improved", text: "UXP plugin stability improvements" },
      { type: "fixed", text: "Timeline markers not syncing correctly" },
    ],
  },
  {
    version: "5.4.0",
    date: "November 15, 2024",
    type: "minor" as const,
    changes: [
      { type: "new", text: "Song identification using audio fingerprinting" },
      { type: "improved", text: "Reduced API latency by 30%" },
      { type: "improved", text: "Better transcription accuracy for accented speech" },
      { type: "fixed", text: "Undo history not preserved after batch operations" },
    ],
  },
  {
    version: "5.3.0",
    date: "October 20, 2024",
    type: "minor" as const,
    changes: [
      { type: "new", text: "Team tier with shared credits and admin dashboard" },
      { type: "new", text: "Usage analytics in plugin panel" },
      { type: "improved", text: "Faster project analysis on startup" },
      { type: "fixed", text: "License activation issues on Windows" },
    ],
  },
];

const changeTypeStyles = {
  new: "bg-green-500/10 text-green-400 border-green-500/20",
  improved: "bg-blue-500/10 text-blue-400 border-blue-500/20",
  fixed: "bg-yellow-500/10 text-yellow-400 border-yellow-500/20",
  deprecated: "bg-red-500/10 text-red-400 border-red-500/20",
};

const changeTypeLabels = {
  new: "New",
  improved: "Improved",
  fixed: "Fixed",
  deprecated: "Deprecated",
};

export default function ChangelogPage() {
  return (
    <div className="min-h-screen dark bg-[#060b14] text-white">
      <Header />
      <main className="pt-24 pb-16">
        <div className="container mx-auto px-4 max-w-4xl">
          <h1 className="text-4xl md:text-5xl font-bold mb-6">Changelog</h1>
          <p className="text-xl text-muted-foreground mb-12">
            See what&apos;s new in SPLICE. We ship updates regularly.
          </p>

          <div className="space-y-12">
            {releases.map((release) => (
              <article key={release.version} className="relative">
                <div className="flex items-center gap-4 mb-4">
                  <Badge
                    className={
                      release.type === "major"
                        ? "bg-[#00D4FF] text-black"
                        : "bg-gray-700 text-white"
                    }
                  >
                    v{release.version}
                  </Badge>
                  <span className="text-muted-foreground">{release.date}</span>
                </div>

                <div className="bg-[#0d1420] border border-gray-800 rounded-lg p-6">
                  <ul className="space-y-3">
                    {release.changes.map((change, index) => (
                      <li key={index} className="flex items-start gap-3">
                        <Badge
                          variant="outline"
                          className={`text-xs shrink-0 ${changeTypeStyles[change.type]}`}
                        >
                          {changeTypeLabels[change.type]}
                        </Badge>
                        <span className="text-muted-foreground">{change.text}</span>
                      </li>
                    ))}
                  </ul>
                </div>
              </article>
            ))}
          </div>
        </div>
      </main>
      <Footer />
    </div>
  );
}
