   739→async function runDetection() {
   740→    if (isOperationInProgress) return;
   741→
   742→    // Check sequence with enhanced error messaging
   743→    const hasSequence = await checkSequenceOpen();
   744→    if (!hasSequence) {
   745→        // Check if there was a communication error vs just no sequence
   746→        const lastError = window.spliceState.lastSequenceCheckError;
   747→        if (lastError && (lastError.includes('CEP') || lastError.includes('communication') || lastError.includes('evalScript'))) {
   748→            setStatus('Premiere Pro connection issue - try reopening panel', true);
   749→            console.error('[SPLICE] runDetection: JSX communication failure:', lastError);
   750→        } else {
   751→            setStatus('Please open a sequence in the Timeline first', true);
   752→            debugLog('runDetection: No active sequence in Timeline');
   753→        }
   754→        // Clear the error for next check
   755→        window.spliceState.lastSequenceCheckError = null;
   756→        return;
   757→    }
   758→
   759→    // Check online
   760→    if (!isOnline()) {
   761→        setStatus('Offline - Check your connection', true);
   762→        return;
   763→    }
   764→
   765→    isOperationInProgress = true;
   766→    ui.goBtn.disabled = true;
   767→    showProgress('Detecting silences...');
   768→
   769→    try {
   770→        const settings = getSettings();
   771→
   772→        // Get sequence info
   773→        const seqInfo = await jsx.call('getActiveSequence');
   774→        debugLog('Sequence info:', seqInfo);
   775→
   776→        // Store sequence info for later use
   777→        currentSequenceInfo = seqInfo;
   778→        window.spliceState.activeSequence = seqInfo;
   779→        if (seqInfo && seqInfo.videoPath) {
   780→            window.spliceState.currentVideoPath = seqInfo.videoPath;
   781→        }
   782→
   783→        // Export audio via JSX for silence detection
   784→        updateProgress(10, 'Exporting audio...');
   785→
   786→        let audioFilePath = null;
   787→        try {
   788→            // Get sequence info for audio export
   789→            const exportResult = await jsx.call('exportSequenceAudioForAnalysis');
   790→            if (exportResult && exportResult.success && exportResult.outputPath) {
   791→                audioFilePath = exportResult.outputPath;
   792→                debugLog('Audio exported to:', audioFilePath);
   793→            } else if (exportResult && exportResult.error) {
   794→                throw new Error(exportResult.error);
   795→            }
   796→        } catch (exportError) {
   797→            console.warn('[SPLICE] Audio export failed, trying fallback:', exportError.message);
   798→            // Try to get audio from first clip in timeline
   799→            try {
   800→                const clipInfo = await jsx.call('getFirstClipAudioPath');
   801→                if (clipInfo && clipInfo.path) {
   802→                    audioFilePath = clipInfo.path;
   803→                    debugLog('Using clip audio path:', audioFilePath);
   804→                }
   805→            } catch (clipError) {
   806→                console.warn('[SPLICE] Fallback audio path failed:', clipError.message);
   807→            }
   808→        }
   809→
   810→        if (!audioFilePath) {
   811→            throw new Error('Could not export or locate audio file. Please ensure sequence has audio tracks.');
   812→        }
   813→
   814→        // Store audio path globally for other modules
   815→        currentAudioPath = audioFilePath;
   816→        window.spliceState.audioPath = audioFilePath;
   817→
   818→        // Call backend for silence detection with audio file path
   819→        updateProgress(30, 'Detecting silences...');
   820→        const silenceResponse = await fetchWithTimeout(
   821→            `${getBackendUrl()}/silences-rms`,
   822→            {
   823→                method: 'POST',
   824→                headers: getAuthHeaders(),
   825→                body: JSON.stringify({
   826→                    wavPath: audioFilePath,
   827→                    sensitivity: settings.sensitivity,
   828→                    minSilenceLength: settings.minSilenceLength || 0.5
   829→                })
   830→            }
   831→        );
   832→
   833→        if (!silenceResponse.ok) {
   834→            throw new Error(await parseErrorResponse(silenceResponse));
   835→        }
   836→
   837→        const silenceData = await silenceResponse.json();
   838→        previewSilences = silenceData.silences || [];
   839→
   840→        // Detect takes if enabled
   841→        if (settings.enableTakesDetection) {
   842→            updateProgress(60, 'Detecting takes...');
   843→            try {
   844→                const analyzeResponse = await fetchWithTimeout(
   845→                    `${getBackendUrl()}/analyze`,
   846→                    {
   847→                        method: 'POST',
   848→                        headers: getAuthHeaders(),
   849→                        body: JSON.stringify({
   850→                            wavPath: audioFilePath,
   851→                            detectTakes: true
   852→                        })
   853→                    }
   854→                );
   855→
   856→                if (analyzeResponse.ok) {
   857→                    const analyzeData = await analyzeResponse.json();
   858→                    previewTakes = analyzeData.takes || [];
   859→
   860→                    // Store transcript for captions/music modules
   861→                    if (analyzeData.transcript) {
   862→                        currentTranscript = analyzeData.transcript;
   863→                        window.currentTranscript = analyzeData.transcript;
   864→                        window.spliceState.lastTranscript = analyzeData.transcript;
   865→                    }
   866→                } else {
   867→                    // Handle specific error codes
   868→                    const errorMsg = await parseErrorResponse(analyzeResponse);
   869→                    if (analyzeResponse.status === 402) {
   870→                        console.warn('[SPLICE] Takes detection skipped: insufficient credits');
   871→                        setStatus('Takes detection skipped: insufficient credits', true);
   872→                    } else if (analyzeResponse.status === 401) {
   873→                        console.warn('[SPLICE] Takes detection skipped: authentication required');
   874→                    } else {
   875→                        console.warn('[SPLICE] Takes detection failed:', errorMsg);
   876→                    }
   877→                }
   878→            } catch (e) {
   879→                console.warn('[SPLICE] Takes detection failed:', e.message);
   880→            }
   881→        }
   882→
   883→        // Detect profanity if enabled
   884→        if (settings.enableProfanity) {
   885→            updateProgress(65, 'Detecting profanity...');
   886→            try {
   887→                const profanityResponse = await fetchWithTimeout(
   888→                    `${getBackendUrl()}/profanity`,
   889→                    {
   890→                        method: 'POST',
   891→                        headers: getAuthHeaders(),
   892→                        body: JSON.stringify({
   893→                            wavPath: audioFilePath,
   894→                            language: settings.profanityLanguage || 'en',
   895→                            bleepType: settings.bleepType || 'standard'
   896→                        })
   897→                    }
   898→                );
   899→
   900→                if (profanityResponse.ok) {
   901→                    const profanityData = await profanityResponse.json();
   902→                    // Backend returns 'words' array, not 'instances'
   903→                    currentProfanity = profanityData.words || profanityData.instances || [];
   904→                    if (currentProfanity.length > 0) {
   905→                        debugLog(`Found ${currentProfanity.length} profanity instances`);
   906→                    }
   907→                } else {
   908→                    console.warn('[SPLICE] Profanity detection failed:', await parseErrorResponse(profanityResponse));
   909→                }
   910→            } catch (e) {
   911→                console.warn('[SPLICE] Profanity detection failed:', e.message);
   912→            }
   913→        }
   914→
   915→        // Detect chapters if enabled
   916→        if (settings.enableChapters) {
   917→            updateProgress(80, 'Detecting chapters...');
   918→            try {
   919→                const chapterResponse = await fetchWithTimeout(
   920→                    `${getBackendUrl()}/chapters`,
   921→                    {
   922→                        method: 'POST',
   923→                        headers: getAuthHeaders(),
   924→                        body: JSON.stringify({
   925→                            wavPath: audioFilePath,
   926→                            transcript: currentTranscript,
   927→                            maxChapters: settings.maxChapters || 10,
   928→                            minChapterLength: settings.minChapterLength || 60
   929→                        })
   930→                    }
   931→                );
   932→
   933→                if (chapterResponse.ok) {
   934→                    const chapterData = await chapterResponse.json();
   935→                    currentChapters = chapterData.chapters || [];
   936→                } else {
   937→                    const errorMsg = await parseErrorResponse(chapterResponse);
   938→                    console.warn('[SPLICE] Chapter detection failed:', errorMsg);
   939→                    // Don't block workflow, just notify user
   940→                    setStatus('Chapter detection skipped: ' + errorMsg, true);
   941→                }
   942→            } catch (e) {
   943→                console.warn('[SPLICE] Chapter detection failed:', e);
   944→                setStatus('Chapter detection failed - continuing without chapters', true);
   945→            }
   946→        }
   947→
   948→        // Detect zoom points if enabled
   949→        if (settings.enableZoom) {
   950→            updateProgress(90, 'Detecting zoom points...');
   951→            try {
   952→                const zoomResponse = await fetchWithTimeout(
   953→                    `${getBackendUrl()}/zoom`,
   954→                    {
   955→                        method: 'POST',
   956→                        headers: getAuthHeaders(),
   957→                        body: JSON.stringify({
   958→                            wavPath: audioFilePath,
   959→                            transcript: currentTranscript,
   960→                            frequency: settings.zoomFrequency || 'medium',
   961→                            preset: settings.zoomPreset || 'medium',
   962→                            placement: settings.zoomPlacement || 'sentence_start'
   963→                        })
   964→                    }
   965→                );
   966→
   967→                if (zoomResponse.ok) {
   968→                    const zoomData = await zoomResponse.json();
   969→                    currentZooms = zoomData.zoomPoints || [];
   970→                } else {
   971→                    const errorMsg = await parseErrorResponse(zoomResponse);
   972→                    console.warn('[SPLICE] Zoom detection failed:', errorMsg);
   973→                    setStatus('Zoom detection skipped: ' + errorMsg, true);
   974→                }
   975→            } catch (e) {
   976→                console.warn('[SPLICE] Zoom detection failed:', e);
   977→                setStatus('Zoom detection failed - continuing without zoom points', true);
   978→            }
   979→        }
   980→
   981→        // Store results
   982→        lastDetectionResult = {
   983→            silences: previewSilences,
   984→            takes: previewTakes,
   985→            chapters: currentChapters,
   986→            zooms: currentZooms
   987→        };
   988→
   989→        // Show preview
   990→        hideProgress();
   991→        showCombinedPreview();
   992→        setStatus(`Found ${previewSilences.length} silences, ${previewTakes.length} takes`);
   993→
   994→    } catch (error) {
   995→        console.error('[SPLICE] Detection error:', error);
   996→        setStatus('Detection failed: ' + error.message, true);
   997→        hideProgress();
   998→    } finally {
   999→        isOperationInProgress = false;
  1000→        ui.goBtn.disabled = false;
  1001→    }
  1002→}
