  1130→async function seekToTime(seconds) {
  1131→    try {
  1132→        // FIX: Removed invalid 'app' check - 'app' is ExtendScript DOM, not available in panel JS
  1133→        // The JSX evalScript will handle the error case if no sequence is open
  1134→
  1135→        // Use JSX to set playhead position
  1136→        // Note: setPlayerPosition expects ticks as a string without quotes around the number value
  1137→        const ticks = Math.round(seconds * 254016000000);
  1138→        const result = await jsx.evalScript(`
  1139→            (function() {
  1140→                try {
  1141→                    if (!app.project.activeSequence) {
  1142→                        return JSON.stringify({ error: 'No active sequence' });
  1143→                    }
  1144→                    app.project.activeSequence.setPlayerPosition('${ticks}');
  1145→                    return JSON.stringify({ success: true });
  1146→                } catch (e) {
  1147→                    return JSON.stringify({ error: e.message });
  1148→                }
  1149→            })()
  1150→        `);
  1151→
  1152→        // Check for errors
